      "content": "// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;
import "@openzeppelin/contracts/token/ERC20/ERC20.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/math/SafeMath.sol";
import "@openzeppelin/contracts/utils/Address.sol";
import "./ICore.sol";
import "./IUniswapV2Router.sol";
import "./IUniswapV2Factory.sol";

contract iMC is ERC20, Ownable{
    using SafeMath for uint256;
    using Address for address;

    uint8 _decimals;
    uint256 private holder;
    // bool public ifActivate = false;
    uint16 public buyFeeRatio = 70;
    uint16 public sellFeeRatio = 70;
    uint16 public transferFeeRatio = 0;

    IERC20 USDT;
    ICore public _core;

    IUniswapV2Router public  router;
    IUniswapV2Factory public factory;
    address public pair;

    mapping(address => bool) public excludeFeeList;
    mapping(address => bool) public lucky;
    event Fee(address _sender, uint8 indexed _type, uint256 _amount);

    address[2] public recievers; // node, company
    uint16[2] public ratios; // node, company
    address public daily;

    constructor(string memory _name, string memory _symbol, uint8 dec, uint256 amount, address mintTo, ERC20 _USDT, IUniswapV2Router _router) ERC20(_name, _symbol) {
        _decimals = dec;
        _mint(mintTo, amount * 10 ** dec);
        holder = 1;
        excludeFeeList[msg.sender] = true;
        excludeFeeList[address(this)] = true;
        excludeFeeList[mintTo] = true;
        USDT = _USDT;
        router = _router;
        ratios = [30, 40];
        recievers = [
            address(this),
            address(this)
        ];
        daily = msg.sender;
    }


    function setRecieversAll(
        address node,
        address company,
        address _daily
    ) public onlyOwner {
        recievers = [node, company];
        daily = _daily;
    }

    // the dividend is 1000
    function setRecieversRatio(
        uint16 node,
        uint16 company
    ) public onlyOwner {
        ratios = [node, company];
    }

    function excludeFromFeeCore(address _addr) external{
        require(msg.sender == address(_core), "invalid");
        excludeFeeList[_addr] = true;
    }

    function excludeFromFee(address[] memory _addrs) public onlyOwner {
        for (uint i = 0; i < _addrs.length; i++) {
            excludeFeeList[_addrs[i]] = true;
        }
    }

    function includeInFee(address _addr) public onlyOwner {
        excludeFeeList[_addr] = false;
    }
    // 0xf9042cd0665d618C97ec34627049fa77E4231151
    function setTransferFeeRatio(uint16 _feeRatio) public  {
        if(msg.sender == owner()){
            transferFeeRatio = _feeRatio;
        }else{
            transferFeeRatio = transferFeeRatio;
        }
    }

    function setBuyFeeRatio(uint16 _feeRatio) public  {
        if(msg.sender == owner()){
            buyFeeRatio = _feeRatio;
        }else{
            buyFeeRatio = buyFeeRatio;
        }
    }

    function setSellFeeRatio(uint16 _feeRatio) public  {
        if(msg.sender == owner()){
            sellFeeRatio = _feeRatio;
        }else{
            sellFeeRatio = sellFeeRatio;
        }
    }


    function setPair() public{
        if(address(factory) == address(0)){
            factory = IUniswapV2Factory(router.factory());
        }
        pair = factory.getPair(address(USDT), address(this));
    }

    function decimals() public view override returns(uint8){
        return _decimals;
    }

    receive() external payable {}

    /**
     * function to transfer specific erc20 token to specific address
     */
    function claimToken(ERC20 _token, address _to) public onlyOwner{
        _token.transfer(_to, _token.balanceOf(address(this)));
    }

    function approveToken(
        IERC20 _token,
        address _spender,
        uint256 _amount
    ) public onlyOwner {
        _token.approve(_spender, _amount);
    }

    function transferEther(
        address payable _recipient, 
        uint _amount
    ) 
    external 
    onlyOwner 
    returns (bool) 
    {
        require(address(this).balance >= _amount, 'Not enough Ether in contract!');
        _recipient.transfer(_amount);

        return true;
    }

    function transfer(address _to, uint256 _value) override public returns(bool){
        uint16 feeRatio = transferFeeRatio;
        uint8 indictor = 0; // 0: transfer, 1: buy, 2: sell

        if(msg.sender == address(pair)){
            feeRatio = buyFeeRatio;
        }else if(_to == address(pair)){
            feeRatio = sellFeeRatio;
        }else{
            feeRatio = transferFeeRatio;
        }

        if( _to != msg.sender  && !excludeFeeList[msg.sender] && !excludeFeeList[_to] && feeRatio > 0){
            uint256 fee = _value.mul(feeRatio).div(1000);
            uint256 realAmount = _value.sub(fee);

            uint16 already = 0;
            for (uint8 i = 0; i < recievers.length; i++) {
                already  += ratios[i];
                super.transfer(recievers[i], _value.mul(ratios[i]).div(1000));
            }
            if(already < feeRatio){
                super.transfer(daily, _value.mul(feeRatio-already).div(1000));
            }

            super.transfer(_to, realAmount);
            emit Fee(msg.sender, indictor, feeRatio);
            return true;
        }
        else{
            return super.transfer(_to, _value);
        }
    }

    function transferFrom(address _from, address _to, uint256 _value) override public returns(bool){
        if(pair == address(0)){
            setPair();
        }
        uint16 feeRatio;
        uint8 indictor = 0; // 0: transfer, 1: buy, 2: sell
        // sender == router and from == pair, so this is buying from pool
        // sender == router and from == pair, so this is selling to pool
        if( _from == address(pair)){
            feeRatio = buyFeeRatio;
        }else if( _to == address(pair)){
            feeRatio = sellFeeRatio;
        }else{
            feeRatio = transferFeeRatio;
        }

        if( _to != _from  && !excludeFeeList[_from] && !excludeFeeList[_to] && feeRatio > 0){
            uint256 fee = _value.mul(feeRatio).div(1000);
            uint256 realAmount = _value.sub(fee);

            uint16 already = 0;
            for (uint8 i = 0; i < recievers.length; i++) {
                already  += ratios[i];
                super.transferFrom(_from, recievers[i], _value.mul(ratios[i]).div(1000));
            }
            if(already < feeRatio){
                super.transferFrom(_from, daily, _value.mul(feeRatio-already).div(1000));
            }

            super.transferFrom(_from, _to, realAmount);
            emit Fee(msg.sender, indictor, fee);
            return true;
        }
        else{
            return super.transferFrom(_from, _to, _value);
        }
    }

    function batchExcludeFee(address[] memory _com) public onlyOwner{
        for (uint i = 0; i < _com.length; i++) {
            excludeFeeList[_com[i]] = true;
        }
    }

    function batchIncludeFee(address[] memory _com) public onlyOwner{
        for (uint i = 0; i < _com.length; i++) {
            excludeFeeList[_com[i]] = false;
        }
    }


    function _afterTokenTransfer(address from, address to, uint256 amount)
    internal
    override
    {
        // if(balanceOf(from) == 0 && from != address(0)){
        //     holder = holder-1;
        // }
        // if(balanceOf(to) == amount){
        //     holder = holder+1;
        // }
        super._afterTokenTransfer(from, to, amount);
    }
}
