interface ICore {
    struct Stake {
        address user;
        uint256 amount;
        uint256 power;
        uint256 since;
        uint256 already;
        uint256 totalValue;
        uint256 alreadyValue;
        bool ifWithdrawn;
        uint256 rewardDebt;
        uint256 pending;
    }

    struct Stakeholder {
        address user;
        Stake[] address_stakes;
        uint256 dynamic;
    }

    struct StakingSummary {
        Stake[] stakes;
        uint256 count;
    }

    function hasStake(address _staker) external view returns (StakingSummary memory);
}
