contract Anote is ERC20, Ownable, AccessControl, ERC20Burnable {
    mapping(address => uint256) public _withdraws;
    mapping(address => uint8) public _withdrawCount;
    uint256 _withdrawFee;
    bytes32 public constant MINTER_ROLE = keccak256("MINTER_ROLE");

    constructor() ERC20("Anote", "ANOTE") Ownable(msg.sender) {
        _withdrawFee = 5000000 gwei;
        _grantRole(MINTER_ROLE, msg.sender);
        _grantRole(DEFAULT_ADMIN_ROLE, msg.sender);
    }

    function decimals() public view virtual override returns (uint8) {
        return 8;
    }

    function deposit(string calldata to, uint256 amount) public returns(string calldata) {
        burn(amount);
        return to;
    }

    function withdraw() public payable {
        require(msg.value == _withdrawFee * _withdrawCount[msg.sender], "Fee is required.");
        
        address payable owner = payable(owner());
        owner.transfer(msg.value);

        if (_withdraws[msg.sender] > 0) {
            _mint(msg.sender, _withdraws[msg.sender]);
            _withdraws[msg.sender] = 0;
            _withdrawCount[msg.sender] = 0;
        }
    }

    function withdrawExists(address addr) public view returns(bool)  {
        return _withdraws[addr] > 0;
    }

    function addWithdraw(address addr, uint256 amount) public {
        require(hasRole(MINTER_ROLE, msg.sender), "Caller is not an owner");
        _withdraws[addr] += amount;
        _withdrawCount[addr]++;
    }

    function cancelWithdraw(address addr) public {
        if (addr == address(0)) {
            addr = msg.sender;
        } else {
            require(hasRole(MINTER_ROLE, msg.sender), "Caller is not an owner");
        }
        _withdraws[addr] = 0;
        _withdrawCount[addr] = 0;
    }

    function setWithdrawFee(uint256 amount) public {
        require(hasRole(MINTER_ROLE, msg.sender), "Caller is not an owner");
        _withdrawFee = amount;
    }

    function getWithdrawFee() public view returns (uint256) {
        require(hasRole(MINTER_ROLE, msg.sender), "Caller is not an owner");
        return _withdrawFee;
    }
}
