// ░█████╗░██╗░░██╗░█████╗░██╗███╗░░██╗░██████╗
// ██╔══██╗██║░░██║██╔══██╗██║████╗░██║██╔════╝
// ██║░░╚═╝███████║███████║██║██╔██╗██║╚█████╗░
// ██║░░██╗██╔══██║██╔══██║██║██║╚████║░╚═══██╗
// ╚█████╔╝██║░░██║██║░░██║██║██║░╚███║██████╔╝
// ░╚════╝░╚═╝░░╚═╝╚═╝░░╚═╝╚═╝╚═╝░░╚══╝╚═════╝░

contract V2UniversalChainsONFT721 is ONFT721, ERC721Enumerable {
    /**********/
    /* ERRORS */
    /**********/

    error UniversalChainsONFT721_MaxLimitReached();
    error UniversalChainsONFT721_ReferrerCannotBeSender();
    error UniversalChainsONFT721_IncorrectMintingFee();
    error UniversalChainsONFT721_NoEarningsToClaim();
    error UniversalChainsONFT721_OnlyProtocolAddressCanClaim();
    error UniversalChainsONFT721_TokenURIIsLocked();
    error UniversalChainsONFT721_ReferralEarningsShareBipsIsTooHigh();

    /**********/
    /* EVENTS */
    /**********/

    event OldToNewConversion(
        address indexed sender,
        uint256 indexed oldTokenId,
        uint256 indexed newTokenId
    );
    event MintingFeeUpdated(
        uint256 indexed oldMintingFee,
        uint256 indexed newMintingFee
    );
    event BridgeFeeUpdated(
        uint256 indexed oldBridgeFee,
        uint256 indexed newBridgeFee
    );
    event BridgeFeeEarned(uint256 indexed amount, uint16 indexed dstChainId);
    event ReferralEarningsShareBipsUpdated(
        uint256 indexed oldReferralEarningsShareBips,
        uint256 indexed newReferralEarningsShareBips
    );
    event ReferrerToShareBipsUpdated(
        address indexed referrer,
        uint256 indexed newShareBips
    );
    event ProtocolAddressUpdated(
        address indexed oldProtocolAddress,
        address indexed newProtocolAddress
    );
    event ONFTMinted(
        address indexed minter,
        address indexed referrer,
        uint256 indexed mintId,
        uint256 referralEarnings,
        uint256 protocolEarnings
    );
    event EarningsClaimed(address indexed claimer, uint256 amount);
    event ProtocolEarningsClaimed(
        address indexed protocolAddress,
        uint256 amount
    );
    event TokenURIUpdated(
        string indexed oldTokenURI,
        string indexed newTokenURI
    );
    event TokenURILocked();

    /*************/
    /* CONSTANTS */
    /*************/

    uint public DENOMINATOR = 10000;

    /**********/
    /* STATES */
    /**********/

    address public onftOld;

    uint public nextMintId;
    uint public maxMintId;

    uint256 public amountMinted;

    uint256 public mintingFee;
    uint256 public bridgeFee;
    address public protocolAddress;
    uint256 public referralEarningsShareBips = 2000; // 20% of the referral earnings

    mapping(address => uint256) public referralEarningsOpen;
    mapping(address => uint256) public referralEarningsClaimed;
    mapping(address => uint256) public amountOfMintsWithReferrer;
    mapping(address => uint256) public referrerToShareBips; // e.g. 0x1234... => 5000 (50%)

    uint256 public protocolEarningsOpen;
    uint256 public protocolEarningsClaimed;

    string private currentTokenURI;
    bool public isTokenURILocked;

    /*****************/
    /*  CONSTRUCTOR  */
    /*****************/

    /// @notice Constructor for the UniversalONFT
    /// @param _layerZeroEndpoint handles message transmission across chains
    /// @param _startMintId the starting mint number on this chain
    /// @param _endMintId the max number of mints on this chain
    /// @param _mintingFee fee for minting ONFTs
    /// @param _bridgeFee fee for bridging ONFTs
    /// @param _protocolAddress, used for claiming protocol earnings
    /// @param _onftOld, can convert old ONFTs to new ONFTs
    constructor(
        uint256 _minGasToTransfer,
        address _layerZeroEndpoint,
        uint _startMintId,
        uint _endMintId,
        uint256 _mintingFee,
        uint256 _bridgeFee,
        address _protocolAddress,
        address _onftOld
    )
        ONFT721(
            "OmniRock Edicts V2",
            "ORE",
            _minGasToTransfer,
            _layerZeroEndpoint
        )
    {
        nextMintId = _startMintId;
        maxMintId = _endMintId;
        mintingFee = _mintingFee;
        bridgeFee = _bridgeFee;
        protocolAddress = _protocolAddress;
        onftOld = _onftOld;
    }

    /***********/
    /*  ADMIN  */
    /***********/

    /// @notice Update the minting fee
    /// @param _mintingFee the new minting fee
    function setMintingFee(uint256 _mintingFee) external onlyOwner {
        uint256 oldMintingFee = mintingFee;
        mintingFee = _mintingFee;
        emit MintingFeeUpdated(oldMintingFee, _mintingFee);
    }

    /// @notice Update the bridge fee
    /// @param _bridgeFee the new bridge fee
    function setBridgeFee(uint256 _bridgeFee) external onlyOwner {
        uint256 oldBridgeFee = bridgeFee;
        bridgeFee = _bridgeFee;
        emit BridgeFeeUpdated(oldBridgeFee, _bridgeFee);
    }

    /// @notice Update the referral earnings share bips
    /// @notice The referral earnings share bips must be less than 50%
    /// @param _referralEarningsShareBips the new referral earnings share in bips
    function setReferralEarningsShareBips(
        uint256 _referralEarningsShareBips
    ) external onlyOwner {
        if (_referralEarningsShareBips > 5000) {
            revert UniversalChainsONFT721_ReferralEarningsShareBipsIsTooHigh();
        }
        uint256 oldReferralEarningsShareBips = referralEarningsShareBips;
        referralEarningsShareBips = _referralEarningsShareBips;
        emit ReferralEarningsShareBipsUpdated(
            oldReferralEarningsShareBips,
            _referralEarningsShareBips
        );
    }

    /// @notice Update the share bips for a referrer
    /// @notice The share bips must be less than 100%
    /// @param referrer the referrer address
    /// @param shareBips the new share in bips
    function setReferrerToShareBips(
        address referrer,
        uint256 shareBips
    ) external onlyOwner {
        if (shareBips > DENOMINATOR) {
            revert UniversalChainsONFT721_ReferralEarningsShareBipsIsTooHigh();
        }
        referrerToShareBips[referrer] = shareBips;
        emit ReferrerToShareBipsUpdated(referrer, shareBips);
    }

    /// @notice Set the protocol address
    /// @param _protocolAddress the new protocol address
    function setProtocolAddress(address _protocolAddress) external onlyOwner {
        address oldProtocolAddress = protocolAddress;
        protocolAddress = _protocolAddress;
        emit ProtocolAddressUpdated(oldProtocolAddress, _protocolAddress);
    }

    /// @notice Sets the URI for the token
    /// @dev If the tokenURI is locked, this function reverts
    /// @param newtokenURI The URI to be set
    function setTokenURI(string memory newtokenURI) external onlyOwner {
        string memory oldTokenURI = currentTokenURI;

        if (isTokenURILocked) {
            revert UniversalChainsONFT721_TokenURIIsLocked();
        }
        currentTokenURI = newtokenURI;

        emit TokenURIUpdated(oldTokenURI, newtokenURI);
    }

    /// @notice Locks the token URI, preventing future changes
    /// @dev Once locked, the tokenURI cannot be changed again
    function lockTokenURI() external onlyOwner {
        if (isTokenURILocked) {
            revert UniversalChainsONFT721_TokenURIIsLocked();
        }
        isTokenURILocked = true;

        emit TokenURILocked();
    }

    /// @notice Claim referral earnings as the protocol
    /// @notice Intended to be called after 6 months of the referral earnings being unclaimed
    /// @notice This is to avoid lost earnings for the protocol
    /// @param _referrer the referrer address
    function claimEarningsAsProtocol(address _referrer) external {
        if (_msgSender() != protocolAddress) {
            revert UniversalChainsONFT721_OnlyProtocolAddressCanClaim();
        }
        uint256 earnings = referralEarningsOpen[_referrer];
        if (earnings == 0) {
            revert UniversalChainsONFT721_NoEarningsToClaim();
        }

        referralEarningsOpen[_referrer] = 0;
        protocolEarningsClaimed += earnings;

        (bool sent, ) = payable(protocolAddress).call{value: earnings}("");
        require(sent, "Failed to send Ether");

        emit EarningsClaimed(protocolAddress, earnings);
    }

    /*****************************/
    /*  Convert Old ONFT to new  */
    /*****************************/

    /// @notice Convert an old ONFT to a new ONFT
    /// @param tokenId the old ONFT tokenId
    function oldOnftToNew(uint256 tokenId) external {
        amountMinted++;
        uint newId = nextMintId;
        nextMintId++;

        IERC721(onftOld).transferFrom(_msgSender(), address(this), tokenId);
        _safeMint(_msgSender(), newId);

        emit OldToNewConversion(_msgSender(), tokenId, newId);
    }

    /**********/
    /*  MINT  */
    /**********/

    function mint() external payable {
        mint(address(0));
    }

    /// @notice Mint your ONFT with a referral. If the referrer address is the zero address,
    ///         the minting fee will go entirely to the protocol, and no referral earnings will be calculated or stored.
    /// @param referrer The address of the referrer, or the zero address to skip the referral program
    function mint(address referrer) public payable {
        if (nextMintId > maxMintId) {
            revert UniversalChainsONFT721_MaxLimitReached();
        }
        if (referrer == _msgSender()) {
            revert UniversalChainsONFT721_ReferrerCannotBeSender();
        }
        if (msg.value != mintingFee) {
            revert UniversalChainsONFT721_IncorrectMintingFee();
        }

        amountMinted++;
        uint newId = nextMintId;
        nextMintId++;

        uint256 referrerEarnings = 0;
        uint256 ownerEarnings = mintingFee;
        if (referrer != address(0)) {
            uint256 share = referrerToShareBips[referrer] == 0
                ? referralEarningsShareBips
                : referrerToShareBips[referrer];

            amountOfMintsWithReferrer[referrer]++;
            referrerEarnings = (mintingFee * share) / DENOMINATOR;

            ownerEarnings = mintingFee - referrerEarnings; // 90% of the minting fee
            referralEarningsOpen[referrer] += referrerEarnings;
        }

        protocolEarningsOpen += ownerEarnings;

        _safeMint(_msgSender(), newId);

        emit ONFTMinted(
            _msgSender(),
            referrer,
            newId,
            referrerEarnings,
            ownerEarnings
        );
    }

    /************/
    /*  BRIDGE  */
    /************/

    /**
     * @dev See {ONFT721Core-estimateSendFee}.
     * @dev This function is overridden to add the bridge fee to the native fee
     */
    function estimateSendFee(
        uint16 _dstChainId,
        bytes memory _toAddress,
        uint _tokenId,
        bool _useZro,
        bytes memory _adapterParams
    )
        public
        view
        virtual
        override(ONFT721Core, IONFT721Core)
        returns (uint nativeFee, uint zroFee)
    {
        return
            this.estimateSendBatchFee(
                _dstChainId,
                _toAddress,
                _toSingletonArray(_tokenId),
                _useZro,
                _adapterParams
            );
    }

    /**
     * @dev See {ONFT721Core-estimateSendBatchFee}.
     * @dev This function is overridden to add the bridge fee to the native fee
     */
    function estimateSendBatchFee(
        uint16 _dstChainId,
        bytes memory _toAddress,
        uint256[] memory _tokenIds,
        bool _useZro,
        bytes memory _adapterParams
    )
        public
        view
        virtual
        override(ONFT721Core, IONFT721Core)
        returns (uint256 nativeFee, uint256 zroFee)
    {
        (nativeFee, zroFee) = super.estimateSendBatchFee(
            _dstChainId,
            _toAddress,
            _tokenIds,
            _useZro,
            _adapterParams
        );
        nativeFee += bridgeFee;
        return (nativeFee, zroFee);
    }

    /**
     * @dev See {ONFT721Core-sendFrom}.
     * @dev This function is overridden to add the bridge fee to the native fee
     */
    function sendFrom(
        address _from,
        uint16 _dstChainId,
        bytes memory _toAddress,
        uint256 _tokenId,
        address payable _refundAddress,
        address _zroPaymentAddress,
        bytes memory _adapterParams
    ) public payable virtual override(ONFT721Core, IONFT721Core) {
        _handleSend(
            _from,
            _dstChainId,
            _toAddress,
            _toSingletonArray(_tokenId),
            _refundAddress,
            _zroPaymentAddress,
            _adapterParams
        );
    }

    /**
     * @dev See {ONFT721Core-sendBatchFrom}.
     * @dev This function is overridden to add the bridge fee to the native fee
     */
    function sendBatchFrom(
        address _from,
        uint16 _dstChainId,
        bytes memory _toAddress,
        uint[] memory _tokenIds,
        address payable _refundAddress,
        address _zroPaymentAddress,
        bytes memory _adapterParams
    ) public payable virtual override(ONFT721Core, IONFT721Core) {
        _handleSend(
            _from,
            _dstChainId,
            _toAddress,
            _tokenIds,
            _refundAddress,
            _zroPaymentAddress,
            _adapterParams
        );
    }

    /**
     * @dev A helper function to handle common logic for `sendFrom` and `sendBatchFrom`
     */
    function _handleSend(
        address _from,
        uint16 _dstChainId,
        bytes memory _toAddress,
        uint[] memory _tokenIds,
        address payable _refundAddress,
        address _zroPaymentAddress,
        bytes memory _adapterParams
    ) private {
        uint256 _nativeFee = msg.value - bridgeFee;

        protocolEarningsOpen = protocolEarningsOpen + bridgeFee;

        _send(
            _from,
            _dstChainId,
            _toAddress,
            _tokenIds,
            _refundAddress,
            _zroPaymentAddress,
            _adapterParams,
            _nativeFee
        );

        emit BridgeFeeEarned(bridgeFee, _dstChainId);
    }

    /**
     * @dev See {ONFT721Core-_send}.
     * @dev This function is overridden to allow setting the native fee
     */
    function _send(
        address _from,
        uint16 _dstChainId,
        bytes memory _toAddress,
        uint[] memory _tokenIds,
        address payable _refundAddress,
        address _zroPaymentAddress,
        bytes memory _adapterParams,
        uint256 _nativeFee
    ) internal virtual {
        // allow 1 by default
        require(_tokenIds.length > 0, "tokenIds[] is empty");
        require(
            _tokenIds.length == 1 ||
                _tokenIds.length <= dstChainIdToBatchLimit[_dstChainId],
            "batch size exceeds dst batch limit"
        );

        for (uint i = 0; i < _tokenIds.length; i++) {
            _debitFrom(_from, _dstChainId, _toAddress, _tokenIds[i]);
        }

        bytes memory payload = abi.encode(_toAddress, _tokenIds);

        _checkGasLimit(
            _dstChainId,
            FUNCTION_TYPE_SEND,
            _adapterParams,
            dstChainIdToTransferGas[_dstChainId] * _tokenIds.length
        );
        _lzSend(
            _dstChainId,
            payload,
            _refundAddress,
            _zroPaymentAddress,
            _adapterParams,
            _nativeFee
        );
        emit SendToChain(_dstChainId, _from, _toAddress, _tokenIds);
    }

    /***********/
    /*  CLAIM  */
    /***********/

    /// @notice Claim referral earnings
    function claimEarnings() external {
        uint256 earnings = referralEarningsOpen[_msgSender()];
        if (earnings == 0) {
            revert UniversalChainsONFT721_NoEarningsToClaim();
        }

        referralEarningsOpen[_msgSender()] = 0;
        referralEarningsClaimed[_msgSender()] += earnings;

        (bool sent, ) = payable(_msgSender()).call{value: earnings}("");
        require(sent, "Failed to send Ether");

        emit EarningsClaimed(_msgSender(), earnings);
    }

    /// @notice Claim protocol earnings
    function claimProtocolEarnings() external {
        if (_msgSender() != protocolAddress) {
            revert UniversalChainsONFT721_OnlyProtocolAddressCanClaim();
        }
        uint256 earnings = protocolEarningsOpen;
        if (earnings == 0) {
            revert UniversalChainsONFT721_NoEarningsToClaim();
        }

        protocolEarningsOpen = 0;
        protocolEarningsClaimed += earnings;
        (bool sent, ) = payable(_msgSender()).call{value: earnings}("");
        require(sent, "Failed to send Ether");

        emit ProtocolEarningsClaimed(protocolAddress, earnings);
    }

    /**********************/
    /*  ERC721Enumerable  */
    /**********************/

    /**
     * @dev See {ERC721-_beforeTokenTransfer}.
     */
    function _beforeTokenTransfer(
        address from,
        address to,
        uint256 firstTokenId,
        uint256 batchSize
    ) internal virtual override(ERC721, ERC721Enumerable) {
        super._beforeTokenTransfer(from, to, firstTokenId, batchSize);
    }

    /**
     * @dev See {IERC165-supportsInterface}.
     */
    function supportsInterface(
        bytes4 interfaceId
    ) public view virtual override(ERC721Enumerable, ONFT721) returns (bool) {
        return
            interfaceId == type(IONFT721).interfaceId ||
            super.supportsInterface(interfaceId);
    }

    /**********/
    /*  VIEW  */
    /**********/

    /// @notice Get the URI
    /// @dev This function is overridden to return the currentTokenURI variable
    /// @dev The URI is always the same for all tokens
    /// @return the URI
    function tokenURI(
        uint256 tokenId
    ) public view virtual override returns (string memory) {
        tokenId;
        return currentTokenURI;
    }
}
"
    }
  },
  "settings": {
    "optimizer": {
      "enabled": true,
      "runs": 200
    },
    "outputSelection": {
      "*": {
        "*": [
          "evm.bytecode",
          "evm.deployedBytecode",
          "abi"
        ]
      }
    }
  }
}}
