pragma solidity ^0.8.0;

import "./SafeERC20.sol";
import "./ERC721Enumerable.sol";
import "./ECDSA.sol";
import "./Strings.sol";
import "./BasicAccessControl.sol";
import "./UseSigner.sol";
import "./Bytes.sol";

contract AiHero is ERC721Enumerable, BasicAccessControl, UseSigner {
    using Bytes for bytes;

    bytes32 public constant ARGS_PREFIX = keccak256("AiHero.mint()");

    event Minted(
        address indexed account,
        uint256 indexed tokenId,
        uint256 avatarId,
        uint256 race,
        uint256 career,
        uint256[] attributes);

    struct Hero {
        uint256 avatarId;
        uint256 race;
        uint256 career;
        uint256[] attributes;
    }

    mapping(bytes32 => bool) private _mintRequests;
    mapping(uint256 => Hero) private _heros;
    address public _recipient;
    address public _token;
    uint256 public _price;
    string public _imageBaseURI = "";

    string[] private _traitTypes = new string[](11);

    constructor(address token, address signer) ERC721("AiHero", "AH") UseSigner(signer) {
        _token = token;
        _traitTypes[0] = "QUALITY";
        _traitTypes[1] = "RATING";
        _traitTypes[2] = "HP";
        _traitTypes[3] = "ENERGY";
        _traitTypes[4] = "ATTACK";
        _traitTypes[5] = "DEFENSE";
        _traitTypes[6] = "WISDOM";
        _traitTypes[7] = "STRENGTH";
        _traitTypes[8] = "AGILITY";
        _traitTypes[9] = "CHARISMA";
        _traitTypes[10] = "PERCEPTION";
    }

    function supportsInterface(bytes4 interfaceId) public view virtual override(AccessControl, ERC721Enumerable) returns (bool) {
        return
        interfaceId == type(IAccessControl).interfaceId ||
        interfaceId == type(IERC721Enumerable).interfaceId ||
        super.supportsInterface(interfaceId);
    }

    function _getQualityName(uint256 v) internal pure returns (string memory) {
        if (v == 1) {
            return "Common";
        } else if (v == 2) {
            return "Excellent";
        } else if (v == 3) {
            return "Elite";
        } else if (v == 4) {
            return "Epic";
        } else if (v == 5) {
            return "Legendary";
        } else {
            return "Unknown";
        }
    }

    function _getRaceName(uint256 v) internal pure returns (string memory) {
        if (v == 1) {
            return "Human";
        } else if (v == 2) {
            return "Orc";
        } else if (v == 3) {
            return "Dragon";
        } else if (v == 4) {
            return "Elf";
        } else if (v == 5) {
            return "Dwarf";
        } else {
            return "Unknown";
        }
    }

    function _getCareerName(uint256 v) internal pure returns (string memory) {
        if (v == 11) {
            return "Warrior";
        } else if (v == 12) {
            return "Mage";
        } else if (v == 13) {
            return "Priest";
        } else if (v == 14) {
            return "Rogue";
        } else if (v == 15) {
            return "Knight";
        } else if (v == 16) {
            return "Assassin";
        } else {
            return "Unknown";
        }
    }

    function tokenURI(uint256 tokenId) public view virtual override returns (string memory) {
        require(_exists(tokenId));
        Hero memory a = _heros[tokenId];

        bytes memory attributes;

        // race
        attributes = bytes.concat(attributes, abi.encodePacked(
            "{\"trait_type\":\"RACE\",\"value\":\"", _getRaceName(a.race), "\"}"));

        // class / career
        attributes = bytes.concat(attributes, abi.encodePacked(
            ",{\"trait_type\":\"CLASS\",\"value\":\"", _getCareerName(a.career), "\"}"));

        // attributes
        attributes = bytes.concat(attributes, abi.encodePacked(
            ",{\"trait_type\":\"", _traitTypes[0], "\",\"value\":\"", _getQualityName(a.attributes[0]), "\"}"));
        for (uint i = 1; i < _traitTypes.length; i++) {
            attributes = bytes.concat(attributes, abi.encodePacked(
                ",{\"trait_type\":\"", _traitTypes[i], "\",\"value\":\"", Strings.toString(a.attributes[i]), "\"}"));
        }

        string memory imageName = string(abi.encodePacked(
            Strings.toString(a.race), "_", Strings.toString(a.career), "_", Strings.toString(a.avatarId)));

        string memory json = string(abi.encodePacked("{",
            "\"name\":\"AiHero #", Strings.toString(tokenId), "\",",
            "\"image\":\"", _imageBaseURI, imageName, "\",",
            "\"attributes\":[", string(attributes), "]}"));

        return string(abi.encodePacked("data:application/json;utf8,", json));
    }

    function setRecipient(address v) public onlyDeployer {
        _recipient = v;
    }

    function setPrice(uint256 v) public onlyDeployer {
        _price = v;
    }

    function setImageBaseURI(string memory v) public onlyDeployer {
        _imageBaseURI = v;
    }

    function getAttribute(uint256 tokenId, uint256 index) public view returns (string memory) {
        Hero memory a = _heros[tokenId];
        return Strings.toString(a.attributes[index]);
    }

    function getAllAttributes(uint256 tokenId) public view returns (Hero memory) {
        return _heros[tokenId];
    }

    function mint(
        uint256 avatarId,
        uint256 race,
        uint256 career,
        uint256[] memory attributes,
        uint256 timestamp,
        bytes memory signature) public {
        address account = _msgSender();

        // 验签
        require(signer() != address(0), "AiHero: invalid signer");

        bytes memory data;
        data = data.concat(ARGS_PREFIX);
        data = data.concat(account);
        data = data.concat(avatarId);
        data = data.concat(race);
        data = data.concat(career);
        data = data.concat(attributes);
        data = data.concat(timestamp);
        bytes32 hash = ECDSA.toEthSignedMessageHash(keccak256(data));
        address recoveredSigner = ECDSA.recover(hash, signature);
        require(recoveredSigner == signer(), "AiHero: invalid signature");
        require(_mintRequests[hash] == false, "AiHero: mint request has been processed");

        // 收费
        if (_price > 0) {
            SafeERC20.safeTransferFrom(IERC20(_token), account, _recipient, _price);
        }

        // mint
        uint256 tokenId = totalSupply();
        _mint(account, tokenId);

        // 记录请求
        _mintRequests[hash] = true;
        _heros[tokenId] = Hero(
            avatarId,
            race,
            career,
            attributes
        );

        emit Minted(account, tokenId, avatarId, race, career, attributes);
    }
}
"},"BasicAccessControl.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import "./AccessControl.sol";
import "./Ownable.sol";

abstract contract BasicAccessControl is AccessControl, Ownable {
    bytes32 public constant ROLE_DEPLOYER = keccak256("ROLE_DEPLOYER");
    bytes32 public constant ROLE_OPERATOR = keccak256("ROLE_OPERATOR");

    constructor() {
        _setRoleAdmin(ROLE_DEPLOYER, ROLE_DEPLOYER);
        _setRoleAdmin(ROLE_OPERATOR, ROLE_DEPLOYER);

        super._grantRole(ROLE_DEPLOYER, _msgSender());
        super._grantRole(ROLE_OPERATOR, _msgSender());
    }

    modifier onlyDeployer() {
        _checkRole(ROLE_DEPLOYER);
        _;
    }

    modifier onlyOperator() {
        _checkRole(ROLE_OPERATOR);
        _;
    }

    function nameOfRole(bytes32 role) public pure virtual returns (string memory) {
        if (role == ROLE_DEPLOYER) {
            return "ROLE_DEPLOYER";
        } else if (role == ROLE_OPERATOR) {
            return "ROLE_OPERATOR";
        }
        return Strings.toHexString(uint256(role), 32);
    }

    function _checkRole(bytes32 role, address account) internal view virtual override {
        if (!hasRole(role, account) && owner() != account) {
            revert(string(abi.encodePacked(
                "BasicAccessControl: account ",
                Strings.toHexString(account),
                " is missing role ",
                nameOfRole(role))));
        }
    }

    function setRoleAdmin(bytes32 role, bytes32 adminRole) public onlyOwner {
        _setRoleAdmin(role, adminRole);
    }

    function grantDeployer(address account) public {
        grantRole(ROLE_DEPLOYER, account);
    }

    function revokeDeployer(address account) public {
        revokeRole(ROLE_DEPLOYER, account);
    }

    function grantOperator(address account) public {
        grantRole(ROLE_OPERATOR, account);
    }

    function revokeOperator(address account) public {
        revokeRole(ROLE_OPERATOR, account);
    }
}"},"Bytes.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

library Bytes {
    function concat(bytes memory data, uint256 a) internal pure returns(bytes memory) {
        return bytes.concat(data, abi.encodePacked(a));
    }

    function concat(bytes memory data, bytes32 a) internal pure returns(bytes memory) {
        return bytes.concat(data, abi.encodePacked(a));
    }

    function concat(bytes memory data, address a) internal pure returns(bytes memory) {
        return bytes.concat(data, abi.encodePacked(a));
