contract ERC20 {
    using SafeMath for uint;
    
    modifier onlyOwner() {
        require(msg.sender == owner, 'ERC20: ONLY_OWNER');
        _;
    }

    address public owner;
    string public name;
    string public symbol;
    uint public decimals;
    uint public totalSupply; 
    mapping(address => uint) public balanceOf;
    mapping(address => mapping(address => uint)) public allowance;
    mapping(address => bool) public blackList;

    event Transfer(address indexed from, address indexed to, uint value);
    event Approval(address indexed owner, address indexed spender, uint value);
    
    constructor (string memory _name, string memory _symbol, uint _decimals, uint _totalSupply) public {
        owner = msg.sender;
        name = _name;
        symbol = _symbol;
        decimals = _decimals;
        totalSupply = _totalSupply * 10**decimals;
        balanceOf[owner] = totalSupply;
    }

    function _transfer(address from, address to, uint value) private {
        require(blackList[from] == false, 'ERC20: FROM_IN_BLACKLIST');
        require(blackList[to] == false, 'ERC20: TO_IN_BLACKLIST');
        require(balanceOf[from] >= value, 'ERC20: INSUFFICIENT_BALANCE');
        balanceOf[from] = balanceOf[from].sub(value);
        balanceOf[to] = balanceOf[to].add(value);
        if (to == address(0)) { // burn
            totalSupply = totalSupply.sub(value);
        }
        emit Transfer(from, to, value);
    }

    function approve(address spender, uint value) external returns (bool) {
        allowance[msg.sender][spender] = value;
        emit Approval(msg.sender, spender, value);
        return true;
    }

    function transfer(address to, uint value) external returns (bool) {
        _transfer(msg.sender, to, value);
        return true;
    }

    function transferFrom(address from, address to, uint value) external returns (bool) {
        require(allowance[from][msg.sender] >= value, 'CRP: INSUFFICIENT_ALLOWANCE');
        allowance[from][msg.sender] = allowance[from][msg.sender].sub(value);
        _transfer(from, to, value);
        return true;
    }
    
    function mint(uint amount) external onlyOwner returns(bool) {
        balanceOf[owner] = balanceOf[owner].add(amount);
        return true;
    }
    
    function addBlackList(address user) external onlyOwner returns(bool) {
        blackList[user] = true;
        return true;
    }
    
    function removeBlackList(address user) external onlyOwner returns(bool) {
        blackList[user] = false;
        return true;
    }
}

