      "content": "// SPDX-License-Identifier: MIT
pragma solidity ^0.8.4;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/token/ERC20/ERC20.sol";

import "./IUniswapV2Pair.sol";
import "./IUniswapV2Router.sol";
import "./IUniswapV2Factory.sol";

contract VirtusFinance is ERC20, Ownable {
    event LogRebase(uint256 indexed epoch, uint256 totalSupply);

    uint256 public constant MAX_UINT256 = type(uint256).max;
    address private constant DEAD = 0x000000000000000000000000000000000000dEaD;
    address private constant ZERO = 0x0000000000000000000000000000000000000000;
    uint256 public constant DECIMALS = 5;
    uint8 public constant RATE_DECIMALS = 7;

    uint256 private constant INITIAL_FRAGMENTS_SUPPLY = 500_000 * 10**DECIMALS;
    uint256 private constant MAX_SUPPLY = 5_000_000_000 * 10**DECIMALS;

    string private _name = "Virtus Finance";
    string private _symbol = "VAP";

    IUniswapV2Pair public pair;
    mapping(address => bool) private _isFeeExempt;

    uint256 public liquidityFee = 30;
    uint256 public treasuryFee = 30;
    uint256 public protectionFundFee = 50;
    uint256 public sellFee = 20;
    uint256 public burnFee = 25;
    uint256 public totalFee =
        liquidityFee + treasuryFee + protectionFundFee + burnFee;
    uint256 public feeDenominator = 1000;

    address public autoLiquidityReceiver;
    address public virtusTreasuryReceiver;
    address public virtusProtectionFundReceiver;
    address public virtusBurner;
    bool public swapEnabled = true;
    IUniswapV2Router public router;
    bool private inSwap = false;

    uint256 private constant TOTAL_GONS =
        MAX_UINT256 - (MAX_UINT256 % INITIAL_FRAGMENTS_SUPPLY);

    bool public _autoRebase;
    bool public _autoAddLiquidity;
    uint256 public _initRebaseStartTime;
    uint256 public _lastRebasedTime;
    uint256 public _lastAddLiquidityTime;
    uint256 public _totalSupply;
    uint256 private _gonsPerFragment;

    mapping(address => uint256) private _gonBalances;
    mapping(address => mapping(address => uint256)) private _allowedFragments;
    mapping(address => bool) public blacklist;

    modifier swapping() {
        inSwap = true;
        _;
        inSwap = false;
    }

    modifier validRecipient(address to) {
        require(to != address(0), "Cannot transfer to address 0");
        _;
    }

    constructor(address _router) ERC20(_name, _symbol) Ownable() {
        router = IUniswapV2Router(_router);
        address pairAddress = IUniswapV2Factory(router.factory()).createPair(
            router.WETH(),
            address(this)
        );

        autoLiquidityReceiver = 0xAB14Ce87BFc7290fc69BdA909817a18e513827cA;
        virtusTreasuryReceiver = 0xe81AB1DF23E21BcA12e5b89CA708Da6Ad78aBab6;
        virtusProtectionFundReceiver = 0x0f8D05230F3ffAa0a989585729F5c2fa587Cf13c;
        virtusBurner = 0xf39f309C352e541462cf15E744BD4C668E1111a3;

        _allowedFragments[address(this)][address(router)] = MAX_UINT256;
        pair = IUniswapV2Pair(pairAddress);

        _totalSupply = INITIAL_FRAGMENTS_SUPPLY;
        _gonBalances[virtusTreasuryReceiver] = TOTAL_GONS;
        _gonsPerFragment = TOTAL_GONS / _totalSupply;
        _initRebaseStartTime = block.timestamp;
        _lastRebasedTime = block.timestamp;
        _autoRebase = true;
        _autoAddLiquidity = true;
        _isFeeExempt[virtusTreasuryReceiver] = true;
        _isFeeExempt[address(this)] = true;

        _transferOwnership(virtusTreasuryReceiver);
        emit Transfer(ZERO, virtusTreasuryReceiver, _totalSupply);
    }

    function decimals() public view virtual override returns (uint8) {
        return uint8(DECIMALS);
    }

    function rebase() internal {
        if (inSwap) return;
        uint256 rebaseRate;
        uint256 deltaTimeFromInit = block.timestamp - _initRebaseStartTime;
        uint256 deltaTime = block.timestamp - _lastRebasedTime;
        uint256 times = deltaTime / 10 minutes;

        if (deltaTimeFromInit < (365 days)) {
            rebaseRate = 1583;
        } else if (deltaTimeFromInit >= (365 days)) {
            rebaseRate = 256;
        } else if (deltaTimeFromInit >= ((15 * 365 days) / 10)) {
            rebaseRate = 16;
        } else if (deltaTimeFromInit >= (7 * 365 days)) {
            rebaseRate = 4;
        }

        uint256 numerator = 10**RATE_DECIMALS + rebaseRate;

        for (uint256 i = 0; i < times; i++) {
            _totalSupply = (_totalSupply * numerator) / (10**RATE_DECIMALS);
        }

        _gonsPerFragment = TOTAL_GONS / _totalSupply;
        _lastRebasedTime = _lastRebasedTime + (times * 10 minutes);

        pair.sync();

        emit LogRebase(_lastRebasedTime, _totalSupply);
    }

    function transfer(address to, uint256 value)
        public
        override
        validRecipient(to)
        returns (bool)
    {
        _transferFrom(msg.sender, to, value);
        return true;
    }

    function transferFrom(
        address from,
        address to,
        uint256 value
    ) public override validRecipient(to) returns (bool) {
        uint256 currentAllowance = _allowedFragments[from][msg.sender];
        if (currentAllowance != MAX_UINT256) {
            require(currentAllowance >= value, "Insufficient allowance");
            _allowedFragments[from][msg.sender] = currentAllowance - value;
        }
        _transferFrom(from, to, value);
        return true;
    }

    function _basicTransfer(
        address from,
        address to,
        uint256 amount
    ) internal returns (bool) {
        uint256 gonAmount = amount * _gonsPerFragment;
        _gonBalances[from] = _gonBalances[from] - gonAmount;
        _gonBalances[to] = _gonBalances[to] + gonAmount;
        return true;
    }

    function _transferFrom(
        address sender,
        address recipient,
        uint256 amount
    ) internal returns (bool) {
        require(!blacklist[sender] && !blacklist[recipient], "Blacklisted");

        if (inSwap) {
            return _basicTransfer(sender, recipient, amount);
        }
        if (shouldRebase()) {
            rebase();
        }

        if (shouldAddLiquidity()) {
            addLiquidity();
        }

        if (shouldSwapBack()) {
            swapBack();
        }

        uint256 gonAmount = amount * _gonsPerFragment;
        _gonBalances[sender] = _gonBalances[sender] - gonAmount;
        uint256 gonAmountReceived = shouldTakeFee(sender, recipient)
            ? takeFee(sender, recipient, gonAmount)
            : gonAmount;
        _gonBalances[recipient] = _gonBalances[recipient] + gonAmountReceived;

        emit Transfer(sender, recipient, gonAmountReceived / _gonsPerFragment);
        return true;
    }

    function takeFee(
        address sender,
        address recipient,
        uint256 gonAmount
    ) internal returns (uint256) {
        uint256 _totalFee = totalFee;
        uint256 _treasuryFee = treasuryFee;

        if (recipient == address(pair)) {
            _totalFee = totalFee + sellFee;
            _treasuryFee = treasuryFee + sellFee;
        }

        uint256 feeAmount = (gonAmount / feeDenominator) * _totalFee;

        _gonBalances[virtusBurner] =
            _gonBalances[virtusBurner] +
            ((gonAmount / feeDenominator) * burnFee);

        _gonBalances[address(this)] =
            _gonBalances[address(this)] +
            ((gonAmount / feeDenominator) * (_treasuryFee + protectionFundFee));

        _gonBalances[autoLiquidityReceiver] =
            _gonBalances[autoLiquidityReceiver] +
            ((gonAmount / feeDenominator) * liquidityFee);

        emit Transfer(sender, address(this), feeAmount / _gonsPerFragment);
        return gonAmount - feeAmount;
    }

    function addLiquidity() internal swapping {
        uint256 autoLiquidityAmount = _gonBalances[autoLiquidityReceiver] /
            _gonsPerFragment;
        _gonBalances[address(this)] =
            _gonBalances[address(this)] +
            _gonBalances[autoLiquidityReceiver];
        _gonBalances[autoLiquidityReceiver] = 0;
        uint256 amountToLiquify = autoLiquidityAmount / 2;
        uint256 amountToSwap = autoLiquidityAmount - amountToLiquify;

        if (amountToSwap == 0) {
            return;
        }
        address[] memory path = new address[](2);
        path[0] = address(this);
        path[1] = router.WETH();

        uint256 balanceBefore = address(this).balance;

        router.swapExactTokensForETHSupportingFeeOnTransferTokens(
            amountToSwap,
            0,
            path,
            address(this),
            block.timestamp
        );

        uint256 amountETHLiquidity = address(this).balance - balanceBefore;

        if (amountToLiquify > 0 && amountETHLiquidity > 0) {
            router.addLiquidityETH{value: amountETHLiquidity}(
                address(this),
                amountToLiquify,
                0,
                0,
                autoLiquidityReceiver,
                block.timestamp
            );
        }
        _lastAddLiquidityTime = block.timestamp;
    }

    function swapBack() internal swapping {
        uint256 amountToSwap = _gonBalances[address(this)] / _gonsPerFragment;

        if (amountToSwap == 0) {
            return;
        }

        uint256 balanceBefore = address(this).balance;
        address[] memory path = new address[](2);
        path[0] = address(this);
        path[1] = router.WETH();

        router.swapExactTokensForETHSupportingFeeOnTransferTokens(
            amountToSwap,
            0,
            path,
            address(this),
            block.timestamp
        );

        uint256 amountETHToTreasuryAndSIF = address(this).balance -
            balanceBefore;

        (bool success, ) = payable(virtusTreasuryReceiver).call{
            value: (amountETHToTreasuryAndSIF * treasuryFee) /
                (treasuryFee + protectionFundFee),
            gas: 30000
        }("");
        (success, ) = payable(virtusProtectionFundReceiver).call{
            value: (amountETHToTreasuryAndSIF * protectionFundFee) /
                (treasuryFee + protectionFundFee),
            gas: 30000
        }("");
    }

    function withdrawAllToTreasury() external swapping onlyOwner {
        uint256 amountToSwap = _gonBalances[address(this)] / _gonsPerFragment;
        require(
            amountToSwap > 0,
            "There is no Virtus token deposited in token contract"
        );
        address[] memory path = new address[](2);
        path[0] = address(this);
        path[1] = router.WETH();
        router.swapExactTokensForETHSupportingFeeOnTransferTokens(
            amountToSwap,
            0,
            path,
            virtusTreasuryReceiver,
            block.timestamp
        );
    }

    function shouldTakeFee(address from, address to)
        internal
        view
        returns (bool)
    {
        address pairAddress = address(pair);
        return
            (pairAddress == from || pairAddress == to) && !_isFeeExempt[from];
    }

    function shouldRebase() internal view returns (bool) {
        return
            _autoRebase &&
            (_totalSupply < MAX_SUPPLY) &&
            msg.sender != address(pair) &&
            !inSwap &&
            block.timestamp >= (_lastRebasedTime + 10 minutes);
    }

    function shouldAddLiquidity() internal view returns (bool) {
        return
            _autoAddLiquidity &&
            !inSwap &&
            msg.sender != address(pair) &&
            block.timestamp >= (_lastAddLiquidityTime + 2 days);
    }

    function shouldSwapBack() internal view returns (bool) {
        return !inSwap && msg.sender != address(pair);
    }

    function setAutoRebase(bool _flag) external onlyOwner {
        if (_flag) {
            _autoRebase = _flag;
            _lastRebasedTime = block.timestamp;
        } else {
            _autoRebase = _flag;
        }
    }

    function setAutoAddLiquidity(bool _flag) external onlyOwner {
        if (_flag) {
            _autoAddLiquidity = _flag;
            _lastAddLiquidityTime = block.timestamp;
        } else {
            _autoAddLiquidity = _flag;
        }
    }

    function allowance(address owner_, address spender)
        public
        view
        override
        returns (uint256)
    {
        return _allowedFragments[owner_][spender];
    }

    function decreaseAllowance(address spender, uint256 subtractedValue)
        public
        override
        returns (bool)
    {
        uint256 oldValue = _allowedFragments[msg.sender][spender];
        if (subtractedValue >= oldValue) {
            _allowedFragments[msg.sender][spender] = 0;
        } else {
            _allowedFragments[msg.sender][spender] = oldValue - subtractedValue;
        }
        emit Approval(
            msg.sender,
            spender,
            _allowedFragments[msg.sender][spender]
        );
        return true;
    }

    function increaseAllowance(address spender, uint256 addedValue)
        public
        override
        returns (bool)
    {
        _allowedFragments[msg.sender][spender] =
            _allowedFragments[msg.sender][spender] +
            addedValue;
        emit Approval(
            msg.sender,
            spender,
            _allowedFragments[msg.sender][spender]
        );
        return true;
    }

    function approve(address spender, uint256 value)
        public
        override
        returns (bool)
    {
        _allowedFragments[msg.sender][spender] = value;
        emit Approval(msg.sender, spender, value);
        return true;
    }

    function checkFeeExempt(address _addr) external view returns (bool) {
        return _isFeeExempt[_addr];
    }

    function getCirculatingSupply() public view returns (uint256) {
        return
            (TOTAL_GONS - _gonBalances[DEAD] - _gonBalances[ZERO]) /
            _gonsPerFragment;
    }

    function isNotInSwap() external view returns (bool) {
        return !inSwap;
    }

    function manualSync() external {
        pair.sync();
    }

    function setFeeReceivers(
        address _autoLiquidityReceiver,
        address _treasuryReceiver,
        address _virtusProtectionFundReceiver,
        address _virtusBurner
    ) external onlyOwner {
        autoLiquidityReceiver = _autoLiquidityReceiver;
        virtusTreasuryReceiver = _treasuryReceiver;
        virtusProtectionFundReceiver = _virtusProtectionFundReceiver;
        virtusBurner = _virtusBurner;
    }

    function getLiquidityBacking(uint256 accuracy)
        public
        view
        returns (uint256)
    {
        uint256 liquidityBalance = _gonBalances[address(pair)] /
            _gonsPerFragment;
        return (accuracy * (liquidityBalance * 2)) / getCirculatingSupply();
    }

    function setWhitelist(address _addr) external onlyOwner {
        _isFeeExempt[_addr] = true;
    }

    function setBotBlacklist(address _botAddress, bool _flag)
        external
        onlyOwner
    {
        require(
            isContract(_botAddress),
            "only contract address, not allowed externally owned account"
        );
        blacklist[_botAddress] = _flag;
    }

    function setLP(address _address) external onlyOwner {
        pair = IUniswapV2Pair(_address);
    }

    function totalSupply() public view override returns (uint256) {
        return _totalSupply;
    }

    function balanceOf(address who) public view override returns (uint256) {
        return _gonBalances[who] / _gonsPerFragment;
    }

    function isContract(address addr) internal view returns (bool) {
        uint256 size;
        assembly {
            size := extcodesize(addr)
        }
        return size > 0;
    }

    receive() external payable {}
}
"
