contract GameItems is ERC1155Supply, Ownable, SignatureVerifier_V2 {
    using Address for address;
    using Strings for uint256;

    string public name;
    string public symbol;

    mapping(address => uint256) public nonces;
    mapping(uint256 => uint256) public allMaxSupply;

    event Claim(address indexed account, uint256 id, uint256 value, uint256 nonce);

    constructor(string memory _name, string memory _symbol, string memory _uri, address _signer) ERC1155(_uri) SignatureVerifier_V2(_signer) {
        name = _name;
        symbol = _symbol;
    }

    function setMaxSupply(
        uint256 id,
        uint256 value
    ) external onlyOwner {
        require(
            allMaxSupply[id] == 0,
            "Cannot be set repeatedly"
        );

        allMaxSupply[id] = value;
    }

    function claim(
        uint256 id,
        uint256 value,
        bytes calldata signature
    ) external {
        uint256 nonce = nonces[msg.sender]++;
        bytes32 messageHash = getMessageHash(msg.sender, id, value, nonce);

        require(
            verify(messageHash, signature),
            "Invalid Signature"
        );

        require(
            allMaxSupply[id] == 0 || totalSupply(id) + value <= allMaxSupply[id],
            "Greater than the maxSupply"
        );

        _mint(msg.sender, id, value, new bytes(0));

        emit Claim(msg.sender, id, value, nonce);
    }

    function mint(
        address account,
        uint256 id,
        uint256 value
    ) public virtual onlyOwner {
        require(
            allMaxSupply[id] == 0 || totalSupply(id) + value <= allMaxSupply[id],
            "Greater than the maxSupply"
        );

        _mint(account, id, value, new bytes(0));
	}
 
    function mintBatch(
        address account,
        uint256[] memory ids,
        uint256[] memory values
    ) public virtual onlyOwner {
        for (uint256 i = 0; i < ids.length; i++) {
            require(
                allMaxSupply[ids[i]] == 0 || (totalSupply(ids[i]) + values[i]) <= allMaxSupply[ids[i]],
                "Greater than the maxSupply"
            );
        }

        _mintBatch(account, ids, values, new bytes(0));
	}

    function burn(
        address account,
        uint256 id,
        uint256 value
    ) public virtual {
        require(
            account == _msgSender() || isApprovedForAll(account, _msgSender()),
            "ERC1155: caller is not owner nor approved"
        );

        _burn(account, id, value);
    }

    function burnBatch(
        address account,
        uint256[] memory ids,
        uint256[] memory values
    ) public virtual {
        require(
            account == _msgSender() || isApprovedForAll(account, _msgSender()),
            "ERC1155: caller is not owner nor approved"
        );

        _burnBatch(account, ids, values);
    }

    function getMessageHash(
        address account,
        uint256 id,
        uint256 value,
        uint256 nonce
    ) public pure returns (bytes32) {
        return keccak256(abi.encodePacked(account, id, value, nonce));
    }

