// File: FWCSwap.sol


pragma solidity ^0.8.0;








contract FWCSwap is Pausable, Ownable {
    receive() external payable {  }

    using SafeMath for uint256;
	using Address for address;

    // Address of the PancakeSwap router contract
    address public pancakeRouter;
    // Address of the PancakeSwap factory contract
    address public pancakeFactory;
    uint256 public fee;

    // The list of token addresses can be sent for exchange
    address[] private intermediaryTokens;

    // The list of token addresses can be sent for exchange
    mapping(address => bool) public TokenInList;

    // The list of token addresses can be received for exchange
    mapping(address => bool) public TokenOutList;
    

    event SwapTokensForTokensEvent(address _tokenIn, uint256 _amountIn, address _tokenOut, uint256 _amountOutMin);
    event SwapTokensForETHEvent(address _tokenIn, uint256 _amountIn, uint256 _amountOutMin);
    event SwapETHForTokensEvent(address _tokenOut, uint256 _amountOutMin);
    
    constructor(address _pancakeRouter, address _pancakeFactory, address _intermediaryToken, uint256 _fee) {
        pancakeRouter = _pancakeRouter;
        pancakeFactory = _pancakeFactory;

        intermediaryTokens.push(_intermediaryToken);

        fee = _fee;
    }

    function getIntermediaryTokens() external view returns (address[] memory) {
        return intermediaryTokens;
    }

    function pause() public onlyOwner {
        _pause();
    }

    function unpause() public onlyOwner {
        _unpause();
    }

    function changeFee(uint256 _fee) public onlyOwner {
        fee = _fee;
    }

    function enableInToken(address token) public onlyOwner {
        TokenInList[token] = true;
    }

    function disabelInToken(address token) public onlyOwner {
        TokenInList[token] = false;
    }

    function enableOutToken(address token) public onlyOwner {
        TokenOutList[token] = true;
    }

    function disabelOutToken(address token) public onlyOwner {
        TokenOutList[token] = false;
    }

    function addIntermediaryToken(address token) public onlyOwner {
        intermediaryTokens.push(token);
    }

    function removeIntermediaryToken(address token) public onlyOwner {
        for (uint i = 0; i < intermediaryTokens.length; i++) {
            if (intermediaryTokens[i] == token) {
                intermediaryTokens[i] = intermediaryTokens[intermediaryTokens.length - 1];
                intermediaryTokens.pop();
            }
        }
    }
    
    function swapTokensForTokens(address _tokenIn, uint256 _amountIn, address _tokenOut, uint256 _amountOutMin, uint deadline) external payable whenNotPaused {
        require(msg.value >= fee, "The fee is not enough");
        require(TokenInList[_tokenIn], "We do not support the token address");
        require(TokenOutList[_tokenOut], "We do not support the token address");

        // Create a path array with token addresses
        address[] memory path = getBestSwapPath(_amountIn, _tokenIn, _tokenOut);

        payable(address(this)).transfer(msg.value);

        IERC20(_tokenIn).transferFrom(msg.sender, address(this), _amountIn);

        // Approve PancakeSwap router to spend the token being swapped
        IERC20(_tokenIn).approve(pancakeRouter, _amountIn);
        
        // Call the swapExactTokensForTokens function in the PancakeSwap router
        IPancakeRouter02(pancakeRouter).swapExactTokensForTokens(
            _amountIn,
            _amountOutMin,
            path,
            msg.sender,
            deadline
        );

        emit SwapTokensForTokensEvent(_tokenIn, _amountIn, _tokenOut, _amountOutMin);
    }

    function swapTokensForETH(address _tokenIn, uint256 _amountIn, uint256 _amountOutMin, uint deadline) external payable whenNotPaused {
        require(msg.value >= fee, "The fee is not enough");
        require(TokenInList[_tokenIn], "We do not support the token address");
        
        // Create a path array with token addresses
        address[] memory path = getBestSwapPath(_amountIn, _tokenIn, IPancakeRouter02(pancakeRouter).WETH());

        payable(address(this)).transfer(msg.value);

        IERC20(_tokenIn).transferFrom(msg.sender, address(this), _amountIn);

        // Approve PancakeSwap router to spend the token being swapped
        IERC20(_tokenIn).approve(pancakeRouter, _amountIn);

        // Call the swapExactTokensForETH function in the PancakeSwap router
        IPancakeRouter02(pancakeRouter).swapExactTokensForETH(
            _amountIn,
            _amountOutMin,
            path,
            msg.sender,
            deadline
        );

        emit SwapTokensForETHEvent(_tokenIn, _amountIn, _amountOutMin);
    }

    function swapETHForTokens(address _tokenOut, uint256 _amountOutMin, uint deadline) external payable whenNotPaused {
        require(msg.value > fee, "The fee is not enough");
        require(TokenOutList[_tokenOut], "We do not support the token address");

        uint256 value = msg.value;
        value = value.sub(fee);

        // Create a path array with token addresses
        address[] memory path = getBestSwapPath(value, IPancakeRouter02(pancakeRouter).WETH(), _tokenOut);

        payable(address(this)).transfer(fee);

        // Call the swapExactETHForTokens function in the PancakeSwap router
        IPancakeRouter02(pancakeRouter).swapExactETHForTokens{value: value}(
            _amountOutMin,
            path,
            msg.sender,
            deadline
        );

        emit SwapETHForTokensEvent(_tokenOut, _amountOutMin);
    }

    function withdraw(address payable _to, uint256 _amount) public onlyOwner {
        require(_amount <= address(this).balance, "not enough balance");

        _to.transfer(_amount);
    }

    function calculateTokenAmount(address _tokenIn, uint256 _amountIn, address _tokenOut) external view returns (uint256) {
        // Create a path array with token addresses
        address[] memory path = getBestSwapPath(_amountIn, _tokenIn, _tokenOut);

        // Get the amounts out from PancakeSwap router
        uint256[] memory amountsOut = IPancakeRouter02(pancakeRouter).getAmountsOut(_amountIn, path);

        // Return the amount of the desired token
        return amountsOut[path.length - 1];
    }

    function getAmountOut(uint256 _amountIn, address[] memory _path) private view returns (uint256) {
        uint256[] memory amountsOut = IPancakeRouter02(pancakeRouter).getAmountsOut(_amountIn, _path);

        // Return the amount of the desired token
        return amountsOut[_path.length - 1];
    }

    function getBestSwapPath(uint256 _amountIn, address _tokenIn, address _tokenOut) public view returns (address[] memory) {
        uint256 amountOut = 0;
        address pair = IPancakeFactory(pancakeFactory).getPair(_tokenIn, _tokenOut);
        address[] memory path;
    
        if (pair != address(0)) {
            path = new address[](2);
            path[0] = _tokenIn;
            path[1] = _tokenOut;

            amountOut = getAmountOut(_amountIn, path);
        }

        // Iterate over the list of intermediary tokens and check for token pairs
        for (uint256 i = 0; i < intermediaryTokens.length; i++) {
            address intermediaryToken = intermediaryTokens[i];
            address intermediaryPair1 = IPancakeFactory(pancakeFactory).getPair(_tokenIn, intermediaryToken);
            address intermediaryPair2 = IPancakeFactory(pancakeFactory).getPair(intermediaryToken, _tokenOut);

            if (intermediaryPair1 != address(0) && intermediaryPair2 != address(0)) {
                // If a token pair exists between _tokenIn and the intermediary token, use it as the best path
                address[] memory newPath = new address[](3);
                newPath[0] = _tokenIn;
                newPath[1] = intermediaryToken;
                newPath[2] = _tokenOut;

                uint256 amount = getAmountOut(_amountIn, newPath);

                if (amount > amountOut) {
                    path = new address[](3);

                    path[0] = _tokenIn;
                    path[1] = intermediaryToken;
                    path[2] = _tokenOut;

                    amountOut = amount;
                }
            }
        }

        require(amountOut > 0 && path.length > 0, 'pair not found');

        return path;
    }

    function getBalance() public view returns(uint) {
        return address(this).balance;
    }
}
