contract MICNFT is ERC721, ERC721Enumerable, ERC721URIStorage, ERC721Burnable, WhiteList {
    using Counters for Counters.Counter;

    Counters.Counter private _tokenIdCounter;
    string uri = "";
    constructor() ERC721("Fight God Miracle", "FGMIC") {}

    function _baseURI() internal view override returns (string memory) {
        return uri;
    }

