abstract contract ReentrancyGuard {
    uint256 private constant _NOT_ENTERED = 1;
    uint256 private constant _ENTERED = 2;
    uint256 private _status;

    constructor() {
        _status = _NOT_ENTERED;
    }

    modifier nonReentrant() {
        _nonReentrantBefore();
        _;
        _nonReentrantAfter();
    }

    function _nonReentrantBefore() private {
        require(_status != _ENTERED, "ReentrancyGuard: reentrant call");
        _status = _ENTERED;
    }

    function _nonReentrantAfter() private {
        _status = _NOT_ENTERED;
    }
}

contract Crowdsale is Context, ReentrancyGuard, Ownable {
    using SafeMath for uint256;

    IERC20 private token_;
    address payable private wallet_;
    uint256 public rate_;
    uint256 private weiRaised_;

    event TokensPurchased(address indexed purchaser, address indexed beneficiary, uint256 value, uint256 amount);

    constructor(uint256 rate, address payable wallet, IERC20 token) {
        require(rate > 0, "Crowdsale: rate is 0");
        require(wallet != address(0), "Crowdsale: wallet is the zero address");
        require(address(token) != address(0), "Crowdsale: token is the zero address");

        rate_ = rate;
        wallet_ = wallet;
        token_ = token;
    }

    receive() external payable {
        buyTokens(_msgSender());
    }

    function show_token() public view returns (IERC20) {
        return token_;
    }

    function show_wallet() public view returns (address payable) {
        return wallet_;
    }

    function show_rate() public view returns (uint256) {
        return rate_;
    }

    function show_weiRaised() public view returns (uint256) {
        return weiRaised_;
    }

    function buyTokens(address beneficiary) public nonReentrant payable {
        uint256 weiAmount = msg.value;
        require(beneficiary != address(0), "Crowdsale: beneficiary is the zero address");
        require(weiAmount != 0, "Crowdsale: weiAmount is 0");

        uint256 tokens = weiAmount.mul(rate_);
        weiRaised_ = weiRaised_.add(weiAmount);

        token_.transfer(beneficiary, tokens);

        emit TokensPurchased(_msgSender(), beneficiary, weiAmount, tokens);

        wallet_.transfer(msg.value);
    }

    function setRate(uint256 newRate) public onlyOwner {
        require(newRate > 0, "Crowdsale: new rate is 0");
        rate_ = newRate;
    }

    /* To send unsold tokens back to owner */
    function transferERC20TokenFromContract(address _tokenIERC20) public onlyOwner {
        IERC20 tokenIERC20 = IERC20(_tokenIERC20);
        address owner_wallet = owner();
        uint256 balance = tokenIERC20.balanceOf(address(this));
        require(balance > 0, "No tokens to transfer");
        tokenIERC20.transfer(owner_wallet, balance);
    }
}

