    "contracts/CoinFlip.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import "@openzeppelin/contracts/access/Ownable.sol";

contract CoinFlip is Ownable {
    uint256 public fee = 10;
    uint public minBet = 0.01 ether;
    uint public maxBet = 2 ether;

    event BetPlaced(address indexed player, uint256 amount, uint256 side);
    event Result(address indexed player, uint256 amount, uint256 side, bool win, uint256 bit);
    event BankThresholdReached(uint256 amount);

    constructor() payable {}

    function setFee(uint256 _fee) external onlyOwner {
        fee = _fee;
    }

    function setMinBet(uint _minBet) external onlyOwner {
        minBet = _minBet;
    }

    function setMaxBet(uint _maxBet) external onlyOwner {
        maxBet = _maxBet;
    }

    function flipCoin(uint256 side) external payable {
        uint256 bet = msg.value;
        require(side == 0 || side == 1, "Invalid side. 0 - heads, 1 - tails");
        require(msg.value >= getMinBet(), "Bet amount must be greater than or equal to the minimum bet amount.");
        require(msg.value <= getMaxBet(), "Bet amount exceeds the maximum bet amount.");

        emit BetPlaced(msg.sender, msg.value, side);

        uint256 winningSide = random() % 2;
        bool win = (side == winningSide);

        uint256 commission = (msg.value * fee) / 100;
        uint256 payout = win ? msg.value * 2  - commission : 0;

        if (payout > 0) {
            payable(msg.sender).transfer(payout);
        }

        emit Result(msg.sender, payout, winningSide, win, bet);
    }

    function deposit() external payable {
        require(msg.value > 0, "Invalid deposit amount");
    }

    function random() private view returns (uint256) {
        return uint256(keccak256(abi.encodePacked(block.difficulty, block.timestamp, block.number)));
    }

    function withdraw(uint256 amount) external onlyOwner {
        uint256 toTransfer = amount;

        if (amount == 0) {
            toTransfer = address(this).balance;
        } else {
            require(amount <= address(this).balance, "Insufficient contract balance");
        }

        payable(msg.sender).transfer(toTransfer);
    }

    // Function to get the minimum bet amount
    function getMinBet() public view returns (uint256) {
        return minBet;
    }

    // Function to get the maximum bet amount (considering the contract balance)
    function getMaxBet() public view returns (uint256) {
        uint256 availableBalance = address(this).balance;
        return availableBalance < maxBet ? availableBalance : maxBet;
    }
}
