interface PriceFeedRouter {
    function WETH() external pure returns (address);
    function getAmountsOut(uint amountIn, address[] calldata path) external view returns (uint[] memory amounts);
}

// Note that MasterChef ownable and the owner wields tremendous power. The ownership
// will be transferred to a governance smart contract once reward token is sufficiently
// distributed and the community can show to govern itself.
//
// Have fun reading it. Hopefully it's bug-free. God bless.

contract TimestampedMasterChef is Ownable {
    using SafeMath for uint256;
    using SafeERC20 for IERC20;

    uint256 constant private DIVISOR = 10 ** 10;
    uint256 constant private PENT_YEAR_IN_SECONDS = 315400000;
    address public feeReceiver;
    // Info of each user.
    struct UserInfo {
      uint256 createdAt;
      uint256 amount;
      uint256 lastWithdrawal;
    }
    // Info of each pool.
    struct PoolInfo {
        bool active;
        IERC20 rewardToken; // must be liquid
        IERC20 lpToken;       // Address of LP token contract.
        IERC20 lpQuoteToken; // Quote token address of lpToken
        uint256 rewardRate;   // Reward given per seconds
        PriceFeedRouter router; // must implement PriceFeedRouter interface
    }
    // Info of each pool.
    PoolInfo[] public poolInfo;
    // Info of each user that stakes LP tokens.
    mapping (uint256 => mapping (address => UserInfo)) public userInfo;

    event Deposit(address indexed user, uint256 indexed pid, uint256 amount);
    event Withdraw(address indexed user, uint256 indexed pid, uint256 amount);
    event EmergencyWithdraw(address indexed user, uint256 indexed pid, uint256 amount);

    constructor() public {

    }

    function poolLength() external view returns (uint256) {
        return poolInfo.length;
    }

    // Add a new lp to the pool. Can only be called by the owner.
    function add(uint256 _rewardRate, IERC20 _rewardToken, IERC20 _lpToken, IERC20 _lpQuoteToken, PriceFeedRouter _router) public onlyOwner {
        require(_lpToken != _lpQuoteToken, "lp token equals quote token");

        poolInfo.push(PoolInfo({
            rewardToken: _rewardToken,
            lpToken: _lpToken,
            rewardRate: _rewardRate,
            lpQuoteToken: _lpQuoteToken,
            router: _router,
            active: true
        }));
    }

    // Update the given pool's reward rate. Can only be called by the owner.
    function setRewardRate(uint256 _pid, uint256 _rewardRate) public onlyOwner {
        poolInfo[_pid].rewardRate = _rewardRate;
    }

    function setRewardToken(uint256 _pid, IERC20 _rewardToken) public onlyOwner {
        poolInfo[_pid].rewardToken = _rewardToken;
    }

    function setQuoteToken(uint256 _pid, IERC20 _lpQuoteToken) public onlyOwner {
        poolInfo[_pid].lpQuoteToken = _lpQuoteToken;
    }

    function setRouter(uint256 _pid, PriceFeedRouter _router) public onlyOwner {
        poolInfo[_pid].router = _router;
    }

    function togglePoolStatus(uint256 _pid) public onlyOwner {
        poolInfo[_pid].active = !poolInfo[_pid].active;
    }

    function lpTokenToQuoteToken(uint256 _lpAmount, IERC20 _lpAddress, IERC20 _quoteToken) public view returns(uint256) {
        uint256 lpTotalSupply = _lpAddress.totalSupply();

        if (lpTotalSupply > 0) {
          uint256 lpQuoteTokenBalance = _quoteToken.balanceOf(address(_lpAddress));
          return _lpAmount.mul(lpQuoteTokenBalance).div(lpTotalSupply);
        }
    }

    function quoteTokenToRewardToken(uint256 _quoteTokenAmount, address _quoteTokenAddress, address _rewardToken, PriceFeedRouter _router) public view returns(uint256) {
        if (_quoteTokenAddress == _rewardToken) return _quoteTokenAmount; // already in reward token;

        address weth = _router.WETH();
        address[] memory path1 = new address [](2);
        address[] memory path2 = new address [](3);
        uint256[] memory amounts;

        if (weth == _quoteTokenAddress) {
            path1[0] = weth;
            path1[1] = _rewardToken;
            amounts = _router.getAmountsOut(_quoteTokenAmount, path1);

        } else if (weth == _rewardToken) {
            path1[0] = _quoteTokenAddress;
            path1[1] = weth;
            amounts = _router.getAmountsOut(_quoteTokenAmount, path1);

        } else {
            path2[0] = _quoteTokenAddress;
            path2[1] = weth;
            path2[2] = _rewardToken;
            amounts = _router.getAmountsOut(_quoteTokenAmount, path2);
        }

        if (amounts.length > 0) {
            return amounts[amounts.length - 1];
        }
    }

    function _roi(uint256 _amount, uint256 _createdAt, uint256 _lastWithdrawal, uint256 _rewardRate) internal view returns(uint256) {
        uint256 period = Math.min(block.timestamp, _createdAt.add(PENT_YEAR_IN_SECONDS)); // Yearly max reward;
        uint256 thisRewardPeriod = period.sub(_lastWithdrawal);
        return _amount.mul(thisRewardPeriod).mul(_rewardRate).div(DIVISOR);
    }

    function pendingReward(uint256 _pid, address _user) public view returns (uint256 pending) {
        PoolInfo storage pool = poolInfo[_pid];
        UserInfo storage user = userInfo[_pid][_user];

        if (pool.lpToken == pool.rewardToken) {
            // staking type
            pending = _roi(user.amount, user.createdAt, user.lastWithdrawal, pool.rewardRate);

        } else if (address(pool.lpQuoteToken) == address(0)) {
            // not lp token
            // use direct conversion through the router
            // no quoting needed

            uint256 quoteRoi = _roi(user.amount, user.createdAt, user.lastWithdrawal, pool.rewardRate);

            if (quoteRoi > 0) {
                pending = quoteTokenToRewardToken(quoteRoi, address(pool.lpToken), address(pool.rewardToken), pool.router);
            }

        } else {
            // it's an lp token
            uint256 quoteValue = lpTokenToQuoteToken(user.amount, pool.lpToken, pool.lpQuoteToken);
            uint256 quoteRoi = _roi(quoteValue.mul(2), user.createdAt, user.lastWithdrawal, pool.rewardRate); // 2x of quoteValue is the user lpAmount;

            if (quoteRoi > 0) {
                pending = quoteTokenToRewardToken(quoteRoi, address(pool.lpQuoteToken), address(pool.rewardToken), pool.router);
            }
        }

    }

    // Deposit LP tokens to MasterChef for reward token.
    function deposit(uint256 _pid, uint256 _amount) public {
        require(_amount > 0, "Zero deposit amount");

        PoolInfo storage pool = poolInfo[_pid];
        UserInfo storage user = userInfo[_pid][_msgSender()];

        require(pool.active, "Pool inactive");

        if (user.amount > 0) {
            withdraw(_pid, 0);
        }

        pool.lpToken.safeTransferFrom(_msgSender(), address(this), _amount);
        user.amount = user.amount.add(_amount);
        user.createdAt = block.timestamp;
        user.lastWithdrawal = block.timestamp;

        emit Deposit(_msgSender(), _pid, _amount);
    }

    // Withdraw LP tokens from MasterChef.
    function withdraw(uint256 _pid, uint256 _amount) public {
        PoolInfo storage pool = poolInfo[_pid];
        UserInfo storage user = userInfo[_pid][_msgSender()];
        require(user.amount >= _amount, "withdraw: not good");

        uint256 pending = pendingReward(_pid, _msgSender());

        if (pending > 0) {
            user.lastWithdrawal = block.timestamp;
            pool.rewardToken.safeTransfer(_msgSender(), pending);
        }

        if (_amount > 0) {
            user.amount = user.amount.sub(_amount);
             // collect fee
            uint256 fee = _amount.div(100); // 1% fee
            pool.lpToken.safeTransfer(_msgSender(), _amount.sub(fee));
            pool.lpToken.safeTransfer(feeReceiver, fee);
        }
        emit Withdraw(_msgSender(), _pid, _amount);
    }

    // Withdraw without caring about rewards. EMERGENCY ONLY.
    function emergencyWithdraw(uint256 _pid) public {
        PoolInfo storage pool = poolInfo[_pid];
        UserInfo storage user = userInfo[_pid][_msgSender()];
        uint256 _amount = user.amount;
        user.amount = 0;
        pool.lpToken.safeTransfer(_msgSender(), _amount);
        emit EmergencyWithdraw(_msgSender(), _pid, _amount);
    }

    // Withdraw reward token from MasterChef
    function emergencyRewardTokenWithdraw(uint256 _pid, uint256 _amount) public onlyOwner {
        PoolInfo storage pool = poolInfo[_pid];
        uint256 rewardTokenBalance = pool.rewardToken.balanceOf(address(this));
        require(rewardTokenBalance >= _amount, "Not enough balance");
        pool.rewardToken.safeTransfer(owner(), _amount);
    }

    function setFeeReceiver(address _feeReceiver) public onlyOwner {
        require(_feeReceiver != address(0), "Address zero");
        feeReceiver = _feeReceiver;
    }
}
