interface IKronosReferral {
    /**
     * @dev Record referral.
     */
    function recordReferral(address user, address referrer) external;

    /**
     * @dev Record referral commission.
     */
    function recordReferralCommission(address referrer, uint256 commission)
        external;

    /**
     * @dev Get the referrer address that referred the user.
     */
    function getReferrer(address user) external view returns (address);
}

contract SuperKronosNode is Ownable {
    using SafeERC20 for IERC20;

    uint256 public  MAX_LOCK_UP_TIME;

    ITreasury public immutable treasuryContract;
    struct Node {
        uint256 principal;
        uint256 depositTime;
        uint256 rewardWithdrawn;
        bool isSettled;
    }

    address public treasuryWallet;
    IERC20 public immutable KRONOS;

    uint256 public nodePrice;
    address public constant DEAD_ADDRESS =  0x000000000000000000000000000000000000dEaD;

    uint256 public  MAX_ROI;


    uint256 public paidToTreasury;

    uint256 public referralComissionRate = 50;//5%
    uint256 public treasuryComissionRate = 150;//15%
    uint256 public burnRate = 800;//80%


    uint256 public withdrawTax = 50; //5%;

    IKronosReferral immutable kronosReferral;
    mapping(address => Node[]) public nodes;
    uint256 public totalNodes;
    event RewardPaid(uint256 index, uint256 rewardInKronos);
    event onBuy(uint256 nodeId);

    constructor(
        IERC20 _KRONOS,
        IKronosReferral _kronosReferral,
        address _treasuryWallet,
        uint256 _nodePrice,
        uint256 _MAX_ROI,
        uint256 _MAX_LOCK_UP_TIME,
        ITreasury _treasuryContract
    ) {
        MAX_ROI = _MAX_ROI;
        KRONOS = _KRONOS;
        kronosReferral = _kronosReferral;
        treasuryWallet = _treasuryWallet;
        treasuryContract = _treasuryContract;
        nodePrice = _nodePrice;
        MAX_LOCK_UP_TIME = _MAX_LOCK_UP_TIME;
    }



    function  maxROI(uint256 max) public onlyOwner {
        MAX_ROI =  max;
    }

    function maxLockUpTime(uint256 time) public onlyOwner {
        MAX_LOCK_UP_TIME = time;
    }


    function setWithrawRewardTax(uint256 newRate) public onlyOwner {
        withdrawTax = newRate;
    }

    function setRates(uint256  _treasuryComissionRate,
        uint256 _referralComissionRate,
        uint256 _burnRate) public onlyOwner {

        referralComissionRate = _referralComissionRate;
        treasuryComissionRate =_treasuryComissionRate;
        burnRate = _burnRate;
    }

    function changeTreasuryWallet(address account) public onlyOwner {
        require(account != address(0), "invalid address");
        treasuryWallet = account;
    }

    function setPrice(uint256 _nodePrice) public onlyOwner {
        nodePrice = _nodePrice;
    }

    function buyNodes(address referrer) public {
        address finalReferrer = kronosReferral.getReferrer(msg.sender);
        if (finalReferrer == address(0)) {
            kronosReferral.recordReferral(msg.sender, referrer);
            finalReferrer = referrer;
        }


        IERC20(address(KRONOS)).safeTransferFrom(
            msg.sender,
            address(this),
            nodePrice
        );


        if (finalReferrer == address(0)) {
            finalReferrer = DEAD_ADDRESS;
        }

        uint256 referralComission = (nodePrice * referralComissionRate) / 1000;
        uint256 treasuryComission = (nodePrice * treasuryComissionRate) / 1000;
        uint256 burnAmount = (nodePrice * burnRate) / 1000;
        paidToTreasury +=treasuryComission;

        KRONOS.safeTransfer(finalReferrer, referralComission);
        KRONOS.safeTransfer(DEAD_ADDRESS, burnAmount);
        KRONOS.safeTransfer(treasuryWallet, treasuryComission);

        Node memory newNode = Node(nodePrice, block.timestamp, 0, false);
        Node[] storage myNodes = nodes[msg.sender];
        myNodes.push(newNode);
        nodes[msg.sender] = myNodes;
        totalNodes +=1;
        emit onBuy(nodes[msg.sender].length - 1);
    }

    function getDepositLength(address user) public view returns (uint256) {
        return nodes[user].length;
    }

    function getPendingReward(address user, uint256 index)
        public
        view
        returns (uint256 finalRewardInKronos)
    {
        Node memory node = nodes[user][index];
        uint256 totalReward = (node.principal * MAX_ROI) / 1000;
        uint256 timePassedSoFar = 0;
        if (block.timestamp > (node.depositTime + MAX_LOCK_UP_TIME)) {
            timePassedSoFar = MAX_LOCK_UP_TIME;
        } else {
            timePassedSoFar = block.timestamp - node.depositTime;
        }

        uint256 rewardEarnedSoFar = (totalReward * timePassedSoFar) /
            MAX_LOCK_UP_TIME;
        finalRewardInKronos = rewardEarnedSoFar - node.rewardWithdrawn;
    }

    function claimReward(uint256 index) public {
        uint256 finalRewardInKronos = getPendingReward(msg.sender, index);
        require(finalRewardInKronos > 0, "reward not valid");
        Node storage node = nodes[msg.sender][index];
        node.rewardWithdrawn = node.rewardWithdrawn + finalRewardInKronos;
        uint256 withdrawTaxAmount = finalRewardInKronos*withdrawTax/1000;
        payKronos(msg.sender, finalRewardInKronos-withdrawTaxAmount);
        payKronos(treasuryWallet, withdrawTaxAmount);
        paidToTreasury +=withdrawTaxAmount;

        uint256 maxReward = (node.principal * MAX_ROI) / 1000;

        if(node.rewardWithdrawn == maxReward ){
            node.isSettled = true;
            totalNodes -=1;
        }
        emit RewardPaid(index, finalRewardInKronos);
    }

    function payKronos(address user, uint256 amount) internal {
        treasuryContract.mintRewards(user, amount);
    }

    function recoverERC20(IERC20 token) public onlyOwner {
        uint256 bal = token.balanceOf(address(this));
        token.safeTransfer(msg.sender, bal);
    }
}
