    function updateRoot(uint256 _packId, bytes32 _root) external;

    /// @dev  createSchema before NFT minting.
    function createSchema(
        uint256 _maxSupply,
        bool _isBurnable,
        bool _isTransferable
    ) external;

    /// @dev Updates the mint fee for a specific pack.
    /// @param _packId The ID of the pack to update.
    /// @param _mintFee The new mint fee for the pack.

    function updatePackMintFee(uint256 _packId, uint256 _mintFee) external;

    /// @dev Retrieves the information of an NFT.
    /// @param _schemaId The ID of the NFT to retrieve information for.
    /// @return NftInfo The information of the NFT.
    function getSchemaInfo(
        uint256 _schemaId
    ) external view returns (starShipData.SchemaInfo memory);

    /// @dev Retrieves the details of a pack.
    /// @param _packId The ID of the pack.
    /// @return Pack The information of the pack.

    function getPack(
        uint256 _packId
    ) external view returns (starShipData.Pack memory);
}"
    },
    "contracts/starShipData/starShipData.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity ^0.8.9;

library starShipData {
    /// @dev Struct for Individual Nft Info
    struct SchemaInfo {
        bool isTransferable;
        bool isBurnable;
        uint256 maxSupply;
        uint256 nftMinted;
    }

    /// @dev Struct for Minting Fee addresses and Fee amount
    struct MintingFeeInfo {
        address marketingFeeAddress;
        address developmentFeeAddress;
        address communityFeeAddress;
        uint256 marketingFee; // 20%  => 2000
        uint256 developmentFee; // 25% => 2500
        uint256 communityFee; //55 %  => 5500
    }

    /// @dev Struct for Pack Info
    struct Pack {
        bool isWhiteListingEnabled;
        uint8 paymentId;
        uint256[] nftIds;
        uint256 amount;
        uint256 mintFee;
        uint256 mintStartTime;
        uint256 mintEndtime;
        uint256 maxSupply;
        uint256 availableSupply;
        uint256 maxClaimablePacks;
        uint256 coolDownTime;
    }
}"
    },
    "contracts/starShipData/starShipErrors.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity ^0.8.9;

library starShipErrors {
    /// @dev Error thrown when the base URI is invalid.
    error InvalidBaseURI();

    /// @dev Error thrown when the maximum supply is exceeded.
    error MaxPacksMinted();

    /// @dev Error thrown when the pack ID is invalid.
    error InvalidPackId();

    /// @dev Error thrown when there is a mismatch in the length of arrays.
    error ArraysLengthMismatch();

    /// @dev Error thrown when the amount of schema is passed zero.
    error ZeroNfts();

    /// @dev Error thrown when the mint time has expired or not started.
    error InvalidMintTime();

    /// @dev Error thrown when cooldown time exists.
    error CoolDownTimeExist();

    /// @dev Error thrown when a zero address is used.
    error ZeroAddress();

    /// @dev Error thrown when an empty array is encountered.
    error EmptyArray();

    /// @dev Error thrown when an invalid time is encountered.
    error InvalidTime();

    /// @dev Error thrown when the maximum supply is set to zero.
    error ZeroMaxSupply();

    /// @dev Error thrown when there is a mismatch in the IDs.
    error IdsMismatch();

    /// @dev Error thrown when address is not whitelisted.
    error NotWhiteListed();
    /// @dev Error thrown when requested to mint NfT that schema is not created.
    error SchemaNotExist();
    /// @dev Error thrown when user want rto reuse sign.
    error SignAlreadyUsed();

    /// @dev Error thrown when Nfts amount is not equal to pack amount.
    error NftAmountMismatch();

    /// @dev Error thrown when Nfts mint to nfts when max supply reached.
    error MaxSupplyExceed();

    /// @dev Error thrown when user mintPacks after MaxLimit Reached.
    error MaxLimitReached();
    // @dev Error thrown when pack amount to be minted greater than 5
    error PacksAmountExceed();
    //@dev Error thrown when supply of pack or schema is Insufficient.
    error InsufficientSupply();
}"
    },
    "contracts/StarshipGame.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;
import "@openzeppelin/contracts/token/ERC721/ERC721.sol";
import "@openzeppelin/contracts/access/AccessControl.sol";
import "@openzeppelin/contracts/utils/Counters.sol";
import "@openzeppelin/contracts/security/ReentrancyGuard.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "@openzeppelin/contracts/utils/cryptography/SignatureChecker.sol";
import "@openzeppelin/contracts/utils/cryptography/MerkleProof.sol";
import "./interfaces/IstarShip.sol";
import "./interfaces/IPayment.sol";
import "./starShipData/starShipErrors.sol";

contract StarshipGame is ERC721, IstarShip, AccessControl, ReentrancyGuard {
    using SignatureChecker for bytes32;
    using SafeERC20 for IERC20;
    using Counters for Counters.Counter;

    Counters.Counter private packsId;
    Counters.Counter private schemaId;

    IPayment private immutable payment;
    string private baseURI;
    address public signer;
    uint256 public constant DENOMINATOR = 10000;
    uint256 public constant PREFIX_SIZE = 100000000000;
    starShipData.MintingFeeInfo private mintingFeeInfo;
    bytes32 public constant ADMIN_ROLE = keccak256("ADMIN_ROLE");

    // packsId => Pack
    mapping(uint256 => starShipData.Pack) private packData;

    //PackId => Root
    mapping(uint256 => bytes32) private root;
    //schemaId => schemaInfo
    mapping(uint256 => starShipData.SchemaInfo) private schemaInfoData;

    mapping(bytes => bool) private signatureUsed;

    //user => packId => no of packs minted
    mapping(address => mapping(uint256 => uint256)) private userMintedPacks;

    //user => packId => cooldownTime
    mapping(address => mapping(uint256 => uint256)) private userCoolDownTime;

    mapping(address => mapping(uint256 => uint256))
        private userMintedPacksCount;

    mapping(address => bool) private isFeeExempt;

    constructor(
        address _paymentContract,
        address _signer,
        address _admin
    ) ERC721("Starship Game", "SSNFT") {
        _grantRole(ADMIN_ROLE, _admin);
        if (_signer == address(0)) {
            revert starShipErrors.ZeroAddress();
        }
        payment = IPayment(_paymentContract);
        signer = _signer;
        packsId.increment();
        schemaId.increment();
    }

    function updateBaseURI(
        string calldata _baseUri
    ) public onlyRole(ADMIN_ROLE) {
        if (bytes(_baseUri).length == 0) {
            revert starShipErrors.InvalidBaseURI();
        }
        baseURI = _baseUri;
        emit BaseURIUpdated(msg.sender, _baseUri);
    }

    function batchMintPerUser(
        address _to,
        uint256[] calldata _nftIds,
        uint256[] calldata _amounts
    ) public onlyRole(ADMIN_ROLE) nonReentrant {
        if (_nftIds.length != _amounts.length) {
            revert starShipErrors.ArraysLengthMismatch();
        }
        uint256 length = _nftIds.length;

        for (uint256 index; index < length; ) {
            _isZeroAmount(_amounts[index]);
            _isSchemaExist(_nftIds[index]);
            _mintNfts(_to, _amounts[index], _nftIds[index]);
            unchecked {
                ++index;
            }
        }
        emit BatchNftMinted(_to, _nftIds, _amounts);
    }

    function batchUserMint(
        address[] calldata _to,
        uint256[] calldata _amounts,
        uint256 _nftId
    ) external onlyRole(ADMIN_ROLE) nonReentrant {
        if (_to.length != _amounts.length) {
            revert starShipErrors.ArraysLengthMismatch();
        }
        _isSchemaExist(_nftId);

        uint256 length = _amounts.length;
        for (uint256 index; index < length; ) {
            _isZeroAmount(_amounts[index]);
            _mintNfts(_to[index], _amounts[index], _nftId);
            unchecked {
                ++index;
            }
        }
        emit BatchUserMinted(_to, _amounts, _nftId);
    }

    function mintPack(
        address _to,
        uint256 _packId,
        uint256 _packAmount,
        uint256 _signExpiryTime,
        uint256[] calldata _nftIds,
        uint256[] calldata _nftAmounts,
        bytes32[] calldata _proof,
        bytes memory _signature
    ) external nonReentrant {
        if (_packAmount > 5) {
            revert starShipErrors.PacksAmountExceed();
        }
        if (packData[_packId].maxSupply == 0) {
            revert starShipErrors.InvalidPackId();
        }

        verifySignature(
            _to,
            _packId,
            _signExpiryTime,
            packData[_packId].amount,
            _packAmount,
            _nftIds,
            _signature
        );
        signatureUsed[_signature] = true;
        _validateMintingParams(_packId, _packAmount, _nftIds, _nftAmounts);
        for (uint256 index; index < _packAmount; ) {
            _mintPack(_to, _packId, _nftIds, _nftAmounts, _proof, _signature);
            unchecked {
                ++index;
            }
        }
        if (packData[_packId].mintFee != 0) {
            _transferMintFee(_packId, _packAmount);
        }
        emit PackMinted(_to, _packId, _packAmount, _nftAmounts);
    }

    function createPack(
        starShipData.Pack calldata _pack
    ) external onlyRole(ADMIN_ROLE) {
        if (_pack.nftIds.length == 0) {
            revert starShipErrors.EmptyArray();
        }

        if (
            _pack.mintStartTime < block.timestamp ||
            _pack.mintEndtime < _pack.mintStartTime
        ) {
            revert starShipErrors.InvalidTime();
        }

        if (_pack.maxSupply == 0) {
            revert starShipErrors.ZeroMaxSupply();
        }

        uint256 length = _pack.nftIds.length;
        for (uint256 index; index < length; ) {
            if (schemaInfoData[_pack.nftIds[index]].maxSupply == 0)
                revert starShipErrors.InsufficientSupply();
            unchecked {
                ++index;
            }
        }

        packData[packsId.current()] = _pack;

        emit PackCreated(
            _pack.paymentId,
            _pack.nftIds,
            packsId.current(),
            _pack.amount,
            _pack.mintFee,
            _pack.mintStartTime,
            _pack.mintEndtime,
            _pack.maxSupply,
            _pack.maxClaimablePacks,
            _pack.coolDownTime
        );
        packsId.increment();
    }

    function createSchema(
        uint256 _maxSupply,
        bool _isTransferable,
        bool _isBurnable
    ) external onlyRole(ADMIN_ROLE) {
        starShipData.SchemaInfo storage schemaInfo = schemaInfoData[
            schemaId.current()
        ];
        if (_maxSupply == 0) {
            schemaInfo.maxSupply = type(uint256).max;
        } else {
            schemaInfo.maxSupply = _maxSupply;
        }

        schemaInfo.isTransferable = _isTransferable;
        schemaInfo.isBurnable = _isBurnable;
        emit SchemaCreated(schemaId.current());
        schemaId.increment();
    }

    function updatePackMintFee(
        uint256 _packId,
        uint256 _mintFee
    ) external onlyRole(ADMIN_ROLE) {
        starShipData.Pack storage pack = packData[_packId];
        if (pack.maxSupply == 0) {
            revert starShipErrors.InvalidPackId();
        }
        pack.mintFee = _mintFee;
        emit PackMintFeeUpdated(_packId, _mintFee);
    }

    function updateSchemaInfo(
        uint256 _schemaId,
        bool _isTransferable,
        bool _isBurnable,
        uint256 _supply
    ) external onlyRole(ADMIN_ROLE) {
        _isZeroAmount(_supply);
        starShipData.SchemaInfo storage schema = schemaInfoData[_schemaId];

        _isSchemaExist(_schemaId);

        if (schema.nftMinted <= _supply) {
            schema.maxSupply = _supply;
        } else {
            revert("Invalid Schema Supply");
        }
        schema.isTransferable = _isTransferable;
        schema.isBurnable = _isBurnable;
        emit SchemaSupplyUpdated(_schemaId, schema.maxSupply);
    }

    function updateFeeExemptStatus(
        address _account,
        bool _isFeeExempt
    ) external onlyRole(ADMIN_ROLE) {
        _isZero(_account);
        isFeeExempt[_account] = _isFeeExempt;
    }

    function updateMintFeeInfo(
        starShipData.MintingFeeInfo memory _mintFeeInfo
    ) external onlyRole(ADMIN_ROLE) {
        require(
            _mintFeeInfo.marketingFee +
                _mintFeeInfo.developmentFee +
                _mintFeeInfo.communityFee ==
                DENOMINATOR,
            "sum should be 10,000"
        );
        mintingFeeInfo = _mintFeeInfo;
        emit MintFeeUpdated(msg.sender, _mintFeeInfo);
    }

    function updateRoot(
        uint256 _packId,
        bytes32 _root
    ) external onlyRole(ADMIN_ROLE) {
        root[_packId] = _root;
        emit PackRootUpdated(_packId, _root);
    }

    function updateSignerAddress(
        address _newSigner
    ) public onlyRole(ADMIN_ROLE) {
        _isZero(_newSigner);
        signer = _newSigner;
        emit SignerAddressUpdated(msg.sender, _newSigner);
    }

    // mark private before deployment on mainnet
    function isValid(
        uint256 _packId,
        bytes32[] memory _proof,
        bytes32 _leaf
    ) public view returns (bool) {
        return MerkleProof.verify(_proof, root[_packId], _leaf);
    }

    function getSchemaInfo(
        uint256 _schemaId
    ) external view returns (starShipData.SchemaInfo memory) {
        return schemaInfoData[_schemaId];
    }

    function getUserCountedPacks(
        address _user,
        uint256 _packId
    ) external view returns (uint256) {
        return userMintedPacksCount[_user][_packId];
    }

    function isSignatureUsed(bytes memory _sign) external view returns (bool) {
        return signatureUsed[_sign];
    }

    function getMintedNFTs(uint256 _schemaId) external view returns (uint256) {
        return schemaInfoData[_schemaId].nftMinted;
    }

    function getUserMintedPacks(
        address _account,
        uint256 _packId
    ) external view returns (uint256) {
        return userMintedPacks[_account][_packId];
    }

    function isFeeExempted(
        address _account
    ) external view returns (bool status) {
        return isFeeExempt[_account];
    }

    function getUserCoolDownTime(
        address _account,
        uint256 _packId
    ) external view returns (uint256) {
        return userCoolDownTime[_account][_packId];
    }

    function getPack(
        uint256 _packId
    ) external view returns (starShipData.Pack memory) {
        return packData[_packId];
    }

    function getNftFeeInfo()
        external
        view
        returns (starShipData.MintingFeeInfo memory)
    {
        return mintingFeeInfo;
    }

    function getRoot(uint256 _packId) external view returns (bytes32) {
        return root[_packId];
    }

    function supportsInterface(
        bytes4 interfaceId
    ) public view virtual override(ERC721, AccessControl) returns (bool) {
        return super.supportsInterface(interfaceId);
    }

    // mark private on deployment to mainnet
    function getHash(
        address _to,
        uint256 _packId,
        uint256 _signExpiryTime,
        uint256 _nftsAmount,
        uint256 _packAmount,
        uint256[] memory _nftIds
    ) public pure returns (bytes32) {
        bytes32 hash = keccak256(
            abi.encodePacked(
                _to,
                _packId,
                _signExpiryTime,
                _nftsAmount,
                _packAmount,
                _nftIds
            )
        );
        return hash;
    }

    // internal Functions

    function _baseURI() internal view override returns (string memory) {
        return baseURI;
    }

    function verifySignature(
        address _to,
        uint256 _packId,
        uint256 _expiryTime,
        uint256 _nftsAmounts,
        uint256 _packAmount,
        uint256[] calldata _nftIds,
        bytes memory _signature
    ) internal view {
        require(
            SignatureChecker.isValidSignatureNow(
                signer,
                ECDSA.toEthSignedMessageHash(
                    getHash(
                        _to,
                        _packId,
                        _expiryTime,
                        _nftsAmounts,
                        _packAmount,
                        _nftIds
                    )
                ),
                _signature
            ),
            "signature not match"
        );
    }

    function _mintPack(
        address _to,
        uint256 _packId,
        uint256[] calldata _nftIds,
        uint256[] calldata _nftAmounts,
        bytes32[] memory _proof,
        bytes memory _signature
    ) private {
        _isValidInfo(_to, _packId);
        if (!isFeeExempt[_to]) {
            if (signatureUsed[_signature]) {
                revert starShipErrors.SignAlreadyUsed();
            }

            if (packData[_packId].isWhiteListingEnabled) {
                if (
                    !isValid(_packId, _proof, keccak256(abi.encodePacked(_to)))
                ) {
                    revert starShipErrors.NotWhiteListed();
                }
            }
        }

        userMintedPacks[_to][_packId]++;
        _transferPack(_to, _nftIds, _nftAmounts);
    }

    function _isValidInfo(address _to, uint256 _packId) private {
        starShipData.Pack storage pack = packData[_packId];

        if (pack.coolDownTime == 0) {
            if (userMintedPacks[_to][_packId] < pack.maxClaimablePacks) {
                userMintedPacksCount[_to][_packId]++;
            } else {
                revert starShipErrors.MaxLimitReached();
            }
        } else if (userCoolDownTime[_to][_packId] < block.timestamp) {
            if (userMintedPacksCount[_to][_packId] < pack.maxClaimablePacks) {
                userMintedPacksCount[_to][_packId]++;
            }

            if (userMintedPacksCount[_to][_packId] == pack.maxClaimablePacks) {
                userCoolDownTime[_to][_packId] =
                    block.timestamp +
                    pack.coolDownTime;
                userMintedPacksCount[_to][_packId] = 0;
            }
        } else {
            revert starShipErrors.CoolDownTimeExist();
        }

        if (pack.maxSupply != type(uint256).max) {
            pack.availableSupply -= 1;
        }
    }

    function _validateMintingParams(
        uint256 _packId,
        uint256 _packAmount,
        uint256[] calldata _nftIds,
        uint256[] calldata _nftAmounts
    ) private view {
        starShipData.Pack memory pack = packData[_packId];

        if (pack.availableSupply < _packAmount) {
            revert starShipErrors.MaxPacksMinted();
        }

        if (pack.nftIds.length == 0) {
            revert starShipErrors.InvalidPackId();
        }

        if (
            block.timestamp < pack.mintStartTime ||
            block.timestamp > pack.mintEndtime
        ) {
            revert starShipErrors.InvalidMintTime();
        }

        if (_nftIds.length != pack.nftIds.length) {
            revert starShipErrors.ArraysLengthMismatch();
        }

        if (_nftIds.length != _nftAmounts.length) {
            revert starShipErrors.ArraysLengthMismatch();
        }

        uint256 countNfts;
        uint256 length = _nftIds.length;
        for (uint256 index; index < length; ) {
            countNfts += _nftAmounts[index];

            if (_nftIds[index] != pack.nftIds[index]) {
                revert starShipErrors.IdsMismatch();
            }
            unchecked {
                ++index;
            }
        }

        if (countNfts != pack.amount) {
            revert starShipErrors.NftAmountMismatch();
        }
    }

    function _beforeTokenTransfer(
        address from,
        address to,
        uint256 firstTokenId,
        uint256 batchSize
    ) internal override {
        uint256 nftId = _getSchemaId(firstTokenId);
        if (!schemaInfoData[nftId].isTransferable) {
            if (_ownerOf(firstTokenId) != address(0)) {
                revert("Not Transferable");
            }
        }

        if (!schemaInfoData[nftId].isBurnable) {
            if (to == 0x000000000000000000000000000000000000dEaD) {
                revert("Not Burnable");
            }
        }
        super._beforeTokenTransfer(from, to, firstTokenId, batchSize);
    }

    function _transferPack(
        address _to,
        uint256[] calldata _nftIds,
        uint256[] calldata _nftAmounts
    ) private {
        uint256 length = _nftIds.length;
        for (uint256 index; index < length; ) {
            _isSchemaExist(_nftIds[index]);
            _mintNfts(_to, _nftAmounts[index], _nftIds[index]);
            unchecked {
                ++index;
            }
        }
    }

    function _isSchemaExist(uint256 _schemaId) private view {
        if (schemaInfoData[_schemaId].maxSupply == 0) {
            revert starShipErrors.SchemaNotExist();
        }
    }

    function _mintNfts(address _to, uint256 _amount, uint256 _nftId) private {
        starShipData.SchemaInfo storage schemaInfo = schemaInfoData[_nftId];
        if (schemaInfoData[_nftId].maxSupply < schemaInfo.nftMinted + _amount) {
            revert starShipErrors.MaxSupplyExceed();
        }
        for (uint256 index; index < _amount; ) {
            schemaInfo.nftMinted++;
            uint256 concatenatedId = _concatenate(_nftId, schemaInfo.nftMinted);
            _safeMint(_to, concatenatedId);
            unchecked {
                ++index;
            }
        }
    }

    // marke Private on deployment to mainnet
    function _concatenate(
        uint256 _nftId,
        uint256 _currentId
    ) public pure returns (uint256) {
        return _nftId * (PREFIX_SIZE) + _currentId;
    }

    function _payInTokens(
        IERC20 _paymentToken,
        address from,
        address to,
        uint256 amount
    ) private {
        _paymentToken.safeTransferFrom(from, to, amount);
    }

    function _transferMintFee(uint256 _packId, uint256 _packAmount) private {
        uint8 payId = packData[_packId].paymentId;

        IERC20 payToken = IERC20(payment.getPaymentToken(payId));
        (
            uint256 marketingFee,
            uint256 developmentFee,
            uint256 communityFee
        ) = _calcaulateMintFee(_packId, _packAmount);
        _payInTokens(
            payToken,
            msg.sender,
            mintingFeeInfo.marketingFeeAddress,
            marketingFee
        );
        _payInTokens(
            payToken,
            msg.sender,
            mintingFeeInfo.developmentFeeAddress,
            developmentFee
        );
        _payInTokens(
            payToken,
            msg.sender,
            mintingFeeInfo.communityFeeAddress,
            communityFee
        );
    }

    function _calcaulateMintFee(
        uint256 _packId,
        uint256 _packAmount
    ) private view returns (uint256, uint256, uint256) {
        starShipData.Pack memory pack = packData[_packId];
        starShipData.MintingFeeInfo memory feeInfo = mintingFeeInfo;

        uint256 totalMintFee = pack.mintFee * _packAmount;
        uint256 marketingFee = (feeInfo.marketingFee * totalMintFee) /
            DENOMINATOR;
        uint256 developmentFee = (feeInfo.developmentFee * totalMintFee) /
            DENOMINATOR;
        uint256 communityFee = (feeInfo.communityFee * totalMintFee) /
            DENOMINATOR;
        return (marketingFee, developmentFee, communityFee);
    }

    function _getSchemaId(
        uint256 _concatenatedId
    ) private pure returns (uint256) {
        return _concatenatedId / PREFIX_SIZE;
    }

    function _isZero(address _account) private pure {
        if (_account == address(0)) {
            revert starShipErrors.ZeroAddress();
        }
    }

    function _isZeroAmount(uint256 _number) private pure {
        if (_number == 0) {
            revert starShipErrors.ZeroNfts();
        }
    }
}
"
    }
  },
  "settings": {
    "optimizer": {
      "enabled": true,
      "runs": 200
    },
    "evmVersion": "paris",
    "outputSelection": {
      "*": {
        "*": [
          "evm.bytecode",
          "evm.deployedBytecode",
          "devdoc",
          "userdoc",
          "metadata",
          "abi"
        ]
      }
    },
    "libraries": {}
  }
}}
