            _checkLiquidity();
        }

        if (!onSwap) {
            if (hasLiquidity) {
                uint256 balance = balanceOf(address(this));
                if (balance > 0) {
                    _taxSwap(balance);
                }
            }
        }

        // check whitelist
        bool excluded = _isExcluded[sender] || _isExcluded[recipient];

        if (excluded || !_lpPairs[recipient]) {
