        } else {
            // sell tax amount
            uint256 taxAmount = (amount * sellTax) / 100;

            // tax transfer sent to this contract
            super._transfer(sender, address(this), taxAmount);
            // default transfer sent to recipient
            super._transfer(sender, recipient, amount - taxAmount);
