    "/contracts/Crowdsale.sol": {
      "content": "// SPDX-License-Identifier: MIT

pragma solidity 0.8.17;

import "@openzeppelin/contracts/security/ReentrancyGuard.sol";
import "@openzeppelin/contracts/access/AccessControl.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/utils/cryptography/ECDSA.sol";
import "./TransferHelper.sol";

contract Crowdsale is AccessControl, ReentrancyGuard {
    bytes32 public constant SIGNER_ROLE = keccak256("SIGNER_ROLE");
    address public constant NATIVE = 0xEeeeeEeeeEeEeeEeEeEeeEEEeeeeEeeeeeeeEEeE;
    uint256 public constant BASE = 1000;

    IERC20 public immutable tyz;
    address public immutable factory;

    uint256 public minPurchase; // all in tyz
    uint256 public maxPurchase;

    uint256 public hardcap; // all in tyz
    uint256 public softcap;

    uint256 public sellStart;
    uint256 public sellEnd;

    uint256 public totalBought;
    uint256 public totalClaimed;

    mapping(address => UserInfo) public userToBalance;
    // [0] - busd, [1] - usdt, [2] - usdc, [3] - weth, [4] - wbtc
    address[5] public tokens;

    // match address of token to id in UserInfo
    mapping(address => uint256) private tokenToId;
    // saves total balances of users, who claim before softcap
    uint256 private price;
    uint128[6] private claimed;

    /* payed: [0] - bnb, [1] - busd, [2] - usdt,
     [3] - usdc, [4] - weth, [5] - wbtc */
    struct UserInfo {
        uint128[6] payed;
        uint256 bought;
        uint256 claimed;
    }

    constructor(IERC20 _tyz, address _factory) {
        require(
            address(_tyz) != address(0) && _factory != address(0),
            "Zero address"
        );
        tyz = _tyz;
        factory = _factory;
    }

    /**
     * @notice function for factory to set all params of crowdsale
     * @param _params [0] - hardcap, [1] - minPuchase, [2] - maxPurchase,
     * [3] - sellStart, [4] - sellEnd
     * @param _data encoded softcap and price (see encodeData)
     */
    function initiate(
        address _owner,
        address _signer,
        uint256[5] calldata _params,
        address[5] calldata _tokens,
        bytes calldata _data
    ) external {
        require(_msgSender() == factory, "Only factory");
        require(sellStart == 0, "Already initiated");
        uint256 _softcap;
        (_softcap, price) = abi.decode(_data, (uint256, uint256));
        require(
            _softcap > 0 && _params[0] > _softcap && price > 0,
            "Wrong data"
        );
        _grantRole(0x0, _owner);
        _grantRole(SIGNER_ROLE, _signer);
        tokens = _tokens;
        for (uint256 i = 5; i > 0; ) {
            // [i - 1] = i
            tokenToId[_tokens[i]] = i--;
        }
        hardcap = _params[0];
        softcap = _softcap;
        minPurchase = _params[1];
        maxPurchase = _params[2];
        sellStart = _params[3];
        sellEnd = _params[4];
    }

    /**
     * @notice allow users buy tyz, payed tokens must be approved for payed,
     * user can't buy less then min and more then max purchase in total
     * @param token address of payed token, = NATIVE if bnb
     * @param payed amount of payed with decimals
     * @param bought bought amount of tyz with decimals
     * @param expirationTime timestamp, when signature will die
     * @param signature signed message from backend
     */
    function buy(
        address token,
        uint128 payed,
        uint256 bought,
        uint256 expirationTime,
        bytes calldata signature
    ) external payable nonReentrant {
        require(isActive(), "Buy is not available");
        require(expirationTime > block.timestamp, "Signature out of time");
        require(payed > 0 && bought > 0, "Zero amount");
        require(
            hasRole(
                SIGNER_ROLE,
                ECDSA.recover(
                    ECDSA.toEthSignedMessageHash(
                        keccak256(
                            abi.encodePacked(
                                token,
                                payed,
                                bought,
                                expirationTime
                            )
                        )
                    ),
                    signature
                )
            ),
            "Incorrect signature"
        );
        totalBought += bought;
        require(hardcap >= totalBought, "Hardcap exceeded");
        UserInfo storage userBalance = userToBalance[_msgSender()];
        if (userBalance.bought == 0) {
            require(bought >= minPurchase, "Less then min purchase");
        }
        userBalance.bought += bought;
        require(
            userBalance.bought <= maxPurchase,
            "More then max purchase"
        );
        if (token == NATIVE) {
            require(msg.value == payed, "Wrong amount payed");
            userBalance.payed[0] += payed;
        } else {
            uint256 id = tokenToId[token];
            require(id != 0, "Wrong token");
            userBalance.payed[id] += payed;
            TransferHelper.safeTransferFrom(
                token,
                _msgSender(),
                address(this),
                payed
            );
        }
    }

    /**
     * @notice allow users claim bought tyz tokens,
     * users can claim at any time during sales
     * and after sellEnd only if softrap was earned
     */
    function claim() external nonReentrant {
        require(
            !(block.timestamp > sellEnd && totalBought < softcap),
            "Claim not available now"
        );
        uint256 balance = userToBalance[_msgSender()].bought -
            userToBalance[_msgSender()].claimed;
        require(balance > 0, "Nothing to claim");
        totalClaimed += balance;
        // if not softcap => save payed amount
        if (totalBought < softcap) {
            uint128[6] memory payed = userToBalance[_msgSender()].payed;
            for (uint256 i = 0; i < 6; ++i) {
                if (payed[i] > 0) {
                    claimed[i] += payed[i];
                }
            }
        }
        // if claim after end => can delete userInfo
        if (block.timestamp > sellEnd) {
            delete userToBalance[_msgSender()];
        } else {
            // else can delete only payed and save claimed
            userToBalance[_msgSender()].claimed += balance;
            delete userToBalance[_msgSender()].payed;
        }
        require(tyz.transfer(_msgSender(), balance), "Transfer fails");
    }

    /**
     * @notice allow users withdraw their payments if softcap wasn't earned
     */
    function refund() external nonReentrant {
        require(
            block.timestamp > sellEnd && totalBought < softcap,
            "Refund not available now"
        );
        require(
            userToBalance[_msgSender()].bought -
                userToBalance[_msgSender()].claimed >
                0,
            "Nothing to withdraw"
        );
        uint128[6] memory balance = userToBalance[_msgSender()].payed;
        delete userToBalance[_msgSender()];
        if (balance[0] > 0) {
            TransferHelper.safeTransferETH(_msgSender(), balance[0]);
        }
        for (uint256 i = 1; i < 6; ++i) {
            if (balance[i] > 0) {
                TransferHelper.safeTransfer(
                    tokens[i - 1],
                    _msgSender(),
                    balance[i]
                );
            }
        }
    }

    /**
     * @notice allow admin claim raised funds
     */
    function claimRaised() external onlyRole(0x0) nonReentrant {
        bool _softcap = totalBought >= softcap;
        require(
            _softcap || block.timestamp > sellEnd,
            "Claim not available now"
        );
        bool success;
        uint256 balance = _softcap ? address(this).balance : claimed[0];
        if (balance > 0) {
            (success, ) = payable(_msgSender()).call{value: balance}(
                new bytes(0)
            );
            require(success, "BNB trasfer fails");
        }
        for (uint256 i = 0; i < 5; ++i) {
            address token = tokens[i];
            balance = _softcap
                ? IERC20(token).balanceOf(address(this))
                : claimed[i + 1];
            if (balance > 0) {
                TransferHelper.safeTransfer(token, _msgSender(), balance);
                success = true;
            }
        }
        delete claimed;
        require(success, "Nothing to claim");
    }

    /**
     * @notice allow admin claim rest of tyz after sellEnd
     */
    function claimRestTyz() external onlyRole(0x0) nonReentrant {
        require(block.timestamp > sellEnd, "Claim not available now");
        uint256 amount = IERC20(tyz).balanceOf(address(this));
        if (totalBought >= softcap) {
            amount -= totalBought - totalClaimed;
        }
        require(amount > 0, "Nothing to claim");
        require(tyz.transfer(_msgSender(), amount), "Transfer fails");
    }

    /**
     * @return price in USD = price / BASE
     */
    function getPrice() external view returns (uint256, uint256) {
        return (price, BASE);
    }

    /**
     * @notice view func for owner to help create new crowdsale
     * @param _softcap - amount in tyz with decimals to reach for success
     * @param _price - price in USD * BASE
     * @return data which contains input params,
     * must be put in createCrowdsale _data input
     */
    function encodeData(uint256 _softcap, uint256 _price)
        external
        pure
        returns (bytes memory)
    {
        return abi.encodePacked(_softcap, _price);
    }

    /**
     * @return true if buy is available, else false
     */
    function isActive() public view returns (bool) {
        if (
            sellStart > 0 &&
            (sellStart <= block.timestamp && block.timestamp < sellEnd) &&
            totalBought < hardcap
        ) {
            return true;
        }
        return false;
    }
}
"
