// File: stake.sol

//SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;





contract Stake is Ownable(msg.sender), ERC1155Holder {
    uint256 public BNB_FEE = 50000000000000000;
    uint256 public constant REWARD = 6000 ether;
    uint256 public constant NFTREWARD = 100000000000000000000;
    uint256 public constant MAX_NODES = 100;
    address public constant TREASURY = 0x85fcc39ab2D015817b540580A977a6434ae50215;
    uint256 public nodesOpened = 0;
    mapping(address => Node) public node;
    IERC20 public zoinToken;
    IERC1155 public zoinNFT;

    struct Node {
        uint256 end;
        bool claimed;
        bool NFT;
    }
    
    constructor(address tokenContractAddress, address NFTContractAddress) {
        zoinToken = IERC20(tokenContractAddress);
        zoinNFT = IERC1155(NFTContractAddress);
    }

    function stake() public payable {
        require(MAX_NODES > nodesOpened, "Max Nodes Opened");
        require(msg.value == BNB_FEE, "Insufficient BNB");
        require(node[msg.sender].end == 0, "Node Opened");
        nodesOpened += 1;
        require(zoinToken.transferFrom(msg.sender, address(this), 5000000000000000000000), "Deposit Failed");
        node[msg.sender].end = block.timestamp + 30 days;
    }

    function stakeWithNFT() public payable {
        require(MAX_NODES > nodesOpened, "Max Nodes Opened");
        require(msg.value == BNB_FEE, "Insufficient BNB");
        require(node[msg.sender].end == 0, "Node Opened");
        nodesOpened += 1;
        require(zoinToken.transferFrom(msg.sender, address(this), 5000000000000000000000), "Deposit Failed");
        zoinNFT.safeTransferFrom(msg.sender, address(this), 0, 1, bytes(""));
        node[msg.sender].NFT = true;
        node[msg.sender].end = block.timestamp + 30 days;
    }

    function claim() public {
        require(node[msg.sender].end != 0, "Node Unopened");
        require(node[msg.sender].end < block.timestamp, "Insufficient Period");
        require(node[msg.sender].claimed == false, "Node Claimed");
        node[msg.sender].claimed = true;
        zoinToken.transfer(msg.sender, REWARD);
        if (node[msg.sender].NFT) {
            zoinToken.transfer(msg.sender, NFTREWARD);
            zoinNFT.safeTransferFrom(address(this), msg.sender, 0, 1, bytes(""));
        }
    }

    function claimBNB() external onlyOwner {
        payable(TREASURY).transfer(address(this).balance);
    }

    function changeFee(uint256 fee) external onlyOwner {
        BNB_FEE = fee;
    }

    function transferTokens(uint256 amount) external onlyOwner {
        zoinToken.transfer(msg.sender, amount);
    }
}
