    "contracts/LYToken.sol": {
      "content": "pragma solidity ^0.8.0;

import "./interface/IUniswapV2Pair.sol";
import "./interface/IRouter02.sol";
import "./interface/IUniswapV2Factory.sol";
import "./interface/IFund.sol";
import "./interface/IPairAction.sol";

import "./ERC20.sol";
import "@openzeppelin/contracts/access/Ownable.sol";

contract LYToken is ERC20, Ownable {
    address public pair;
    address public uPair;

    uint public feeForDead = 10;
    uint public feeForMarket = 55;
    uint public feeForLp = 15;

    bool public feeEnabled = true;
    bool public tranferFeeEnabled = true;
    address private action;
    address public fund1;
    address public fund2;

	address private _deadAddress = address(0x000000000000000000000000000000000000dEaD);

    mapping(address => bool) private excludedFromFees;

    constructor(address _w, address _u, address _router,
        address _a,
        address _f1, address _f2 
        ) ERC20("Tron Legacy", "LY") {
        IPairAction(_a).valid(address(this));
        action = _a;
        pair = IUniswapV2Factory(IRouter02(_router).factory()).createPair(address(this), _w);
        uPair = IUniswapV2Factory(IRouter02(_router).factory()).createPair(address(this), _u);

        fund1 = _f1;
        fund2 = _f2;

        IFund(fund1).initLyToken(address(this), pair);
        IFund(fund2).initLyToken(address(this), pair);

        excludeFromFees(msg.sender, true);
        excludeFromFees(fund1, true);
        excludeFromFees(fund2, true);
        excludeFromFees(address(this), true);

        uint256 totalSupply = 3e29;
        _mint(msg.sender, totalSupply);
    }


    function excludeFromFees(address account, bool excluded) public onlyOwner {
        excludedFromFees[account] = excluded;
    }

    function excludeMultipleAccountsFromFees(address[] calldata accounts, bool excluded) public onlyOwner {
        for (uint256 i = 0; i < accounts.length; i++) {
            excludedFromFees[accounts[i]] = excluded;
        }
    }

    function setFundAddrs(address[] calldata accounts) public onlyOwner {
        require(accounts.length == 2 , "invalid");
        fund1 = accounts[0];
        fund2 = accounts[1];

        excludeFromFees(fund1, true);
        excludeFromFees(fund2, true);
    }

    // 10 times
    function setFeeRate(uint256 marketfee, uint lpFee, uint deadFee) public onlyOwner {
        require(marketfee + lpFee + deadFee < 500, "fee to high");
        feeForMarket = marketfee;
        feeForLp = lpFee;
        feeForDead  = deadFee;
    }

    function setEnabled(bool _enabled, bool _transferEnabled) public onlyOwner {
        feeEnabled = _enabled;
        tranferFeeEnabled = _transferEnabled;
    }

    function isExcludedFromFees(address account) public view returns (bool) {
        return excludedFromFees[account];
    }


    function transferNoEvent(address from,
        address to,
        uint256 amount) internal {

        uint256 fromBalance = _balances[from];
        require(fromBalance >= amount, "ERC20: transfer amount exceeds balance");
        unchecked {
            _balances[from] = fromBalance - amount;
        }
        _balances[to] += amount;
    }


    function _transfer(
        address from,
        address to,
        uint256 amount
    ) internal override {
        require(from != address(0), "ERC20: transfer from the zero address");
        require(to != address(0), "ERC20: transfer to the zero address");
        require(amount > 0, "zero amount");

		if(excludedFromFees[from] || excludedFromFees[to] ||  // exclude fee
            !feeEnabled) {  
            super._transfer(from, to, amount);
            return;
        }

        if((balanceOf(pair) == 0 && to == pair) ||
            (balanceOf(uPair) == 0 && to == uPair) ) {
            require(from == owner(), "first add only owner");
        }

        if (from != pair && to != pair) {
            if ( tranferFeeEnabled && balanceOf(pair) > 0) {
                amount = handleTransferFee(from, amount);
                handleFund();
            }
        } else {
            uint8 action = IPairAction(action).action(from, to, pair);

            if(action == 4) {
                uint feeAmount = amount * (feeForLp + feeForMarket + feeForDead) / 1000;
                transferNoEvent(from, fund2, feeAmount);
                amount -= feeAmount;
            } else if(action == 3) {

            } else {
                amount = handleTransferFee(from, amount);
                if(action == 2) {
                    handleFund();
                }
            }
        }
        super._transfer(from, to, amount);
    }

    function handleTransferFee(address from , uint amount) private returns (uint) {
        uint deadAmount = amount * feeForDead / 1000;
        super._transfer(from, _deadAddress, deadAmount);

        uint feeAmount = amount * (feeForLp + feeForMarket) / 1000;
        transferNoEvent(from, fund1, feeAmount);
        return amount - deadAmount - feeAmount;
    }


    function rescueToken(address tokenAddress, uint256 amount) public onlyOwner returns (bool success) {
        return IERC20(tokenAddress).transfer(msg.sender, amount);
    }

    function handleFund() public {
        IFund(fund1).handleFund();
        IFund(fund2).handleFund();
    }


}"
