    function totalSupply() external view returns (uint256);

    function balanceOf(address account) external view returns (uint256);

    function transfer(address recipient, uint256 amount) external returns (bool);

    function allowance(address owner, address spender) external view returns (uint256);
   
    function approve(address spender, uint256 amount) external returns (bool);

    function transferFrom(
        address sender,
        address recipient,
        uint256 amount
    ) external returns (bool);
    
    function amountForEth(uint256 ethAmount) external view returns(uint256 tokenAmount);
}

abstract contract BPContract{
     function protect( address sender, address receiver, uint256 amount ) external virtual;
}



contract CryptoFarm is Context, IERC20, IERC20Metadata,Ownable {
    
    using SafeMath for uint256;
    
    
    BPContract public BP;
    bool public bpEnabled;
    bool public BPDisabledForever = false;

    
    mapping(address => uint256) private _balances;
    mapping(address => bool) private _inBalcklist;
    

    mapping(address => mapping(address => uint256)) private _allowances;

    uint256 private _totalSupply;
    string  private _name = "CryptoFarm";
    string  private _symbol = "CFC";
    uint256 private _initSupply = 1000000000*10**18;
    uint256 public  leastTokenForEthFee = 100000*10**18;
    
   // uint256 public dexTaxFee = 5; //take fee while sell token to dex
    uint256 public dexBuyBackFee = 4;
    uint256 public dexDevFee = 1;
    address immutable public  pairAddress;
    address immutable public  routerAddress;

    address payable public buyBackAddress = payable(0x3E5b5774f978Bc2Ce7B316aEB7F93F43eB73cc03);
    address payable public devWalletAddress = payable(0xf2cbB41BFc1cA754533fF1aed3D2D94b29ACA1aF) ;
    address payable public gameBankAddress;
    bool private startMarketing = false;

    
    
    mapping(address => bool) private _isExcludedFromFee;
    


    /**
     * @dev Sets the values for {name} and {symbol}.
     *
     * The default value of {decimals} is 18. To select a different value for
     * {decimals} you should overload it.
     *
     * All two of these values are immutable: they can only be set once during
     * construction.
     */
    constructor() {
        payable(msg.sender);
        payable(address(this));
        _mint(msg.sender, _initSupply);
        
        IUniswapV2Router02 _uniswapV2Router = IUniswapV2Router02(
             0x10ED43C718714eb63d5aA57B78B54704E256024E
        );
    
       
        pairAddress = IUniswapV2Factory(_uniswapV2Router.factory())
        .createPair(address(this), _uniswapV2Router.WETH());

        routerAddress = address(_uniswapV2Router);
        _approve(address(this), address(_uniswapV2Router), ~uint256(0));
        
        _isExcludedFromFee[owner()] = true;
        _isExcludedFromFee[buyBackAddress] = true;
        _isExcludedFromFee[devWalletAddress] = true;
        _isExcludedFromFee[gameBankAddress] = true;
    }
    
    function multiBlacklist(address[] memory addresses) external onlyOwner {
        for (uint256 i = 0; i < addresses.length; i++) {
            require(addresses[i] != pairAddress, "Can't blacklist pair addres");
            _inBalcklist[addresses[i]] = true;
        }
    }

    function multiRemoveFromBlacklist(address[] memory addresses) external onlyOwner {
        for (uint256 i = 0; i < addresses.length; i++) {
            _inBalcklist[addresses[i]] = false;
        }
    }
    
    
    
    function setBPAddrss(address _bp) external onlyOwner {
        require(address(BP)== address(0), "Can only be initialized once");
        BP = BPContract(_bp);
    }
    
    function setBpEnabled(bool _enabled) external onlyOwner {
       bpEnabled = _enabled;
    }

    function setBotProtectionDisableForever() external onlyOwner{
       require(BPDisabledForever == false);
       BPDisabledForever = true;
    }

   
   function setMarketStaus(bool _staus) public onlyOwner{
       require(startMarketing != true , "Can only be initialized once");
       startMarketing = _staus;
   }
   
   function setAddressInBlacklists(address account, bool _staus) public onlyOwner{
       _inBalcklist[account] = _staus;
   }
    
    function excludeFromFee(address account) public onlyOwner {
        _isExcludedFromFee[account] = true;
    }

    function includeInFee(address account) public onlyOwner {
        _isExcludedFromFee[account] = false;
    }
    

    function setDevFee(uint256 _devFee) public onlyOwner{
        require(_devFee < 10,"can only set below 10");
        dexDevFee = _devFee;
    }
    
    function setBuyBackFee(uint256 _buyBackFee) public onlyOwner{
        require(_buyBackFee < 10,"can only set below 10");
        dexBuyBackFee = _buyBackFee;
    }
    
     function setBuybackAddress(address _buybackAddress) public onlyOwner {
        buyBackAddress = payable(_buybackAddress);
    }
    
     function setDevWalletAddress(address _devWalletAddress) public onlyOwner {
        devWalletAddress = payable(_devWalletAddress);
    }
    
    function setGameBankAddress(address _gameBankAddress) public onlyOwner{
        gameBankAddress = payable(_gameBankAddress);
    }
    
    
    
    function isExcludedFromFee(address account) public view returns (bool) {
        return _isExcludedFromFee[account];
    }
    
    
    function amountForEth(uint256 ethAmount) public view returns(uint256 tokenAmount){
        address _token0Address = IUniswapV2Pair(pairAddress).token0();
        address wethAddress = IUniswapV2Router02(routerAddress).WETH();
        
        (uint112 _reserve0,uint112 _reserve1,) = IUniswapV2Pair(pairAddress).getReserves();
        uint256 _tokenAmount;
        uint256 _wethAmount;
        if(_token0Address==wethAddress){
            _wethAmount = _reserve0;
            _tokenAmount = _reserve1;
        }
        else{
            _wethAmount = _reserve1;
