        uint256 amount
    ) internal virtual {
        if (bpEnabled && !BPDisabledForever){
            BP.protect(sender, recipient, amount);
          }
        
        
        require(sender != address(0), "ERC20: transfer from the zero address");
        require(recipient != address(0), "ERC20: transfer to the zero address");
        require(startMarketing == true, "Market is not starting now");
        require(!_inBalcklist[sender] && !_inBalcklist[recipient],"in BL");

        uint256 senderBalance = _balances[sender];
        require(senderBalance >= amount, "ERC20: transfer amount exceeds balance");
        
        

        unchecked {
            _balances[sender] = senderBalance.sub(amount);
        }
        
        bool takeFee = true;
        if (_isExcludedFromFee[sender]) {
            takeFee = false;
        }
        if(recipient == gameBankAddress){
            takeFee = false;
        }

        
        if(recipient==pairAddress&&takeFee){
            uint256 BuyBackFee = amount.mul(dexBuyBackFee).div(100);
            uint256 devFee  = amount.mul(dexDevFee).div(100);
            
            _balances[buyBackAddress] = _balances[buyBackAddress].add(BuyBackFee);
            _balances[devWalletAddress] = _balances[devWalletAddress].add(devFee);
            
            emit Transfer(sender, buyBackAddress, BuyBackFee);
            amount = amount.sub(BuyBackFee);
            
            emit Transfer(sender, devWalletAddress, devFee);
            amount = amount.sub (devFee);
        }
        
        _balances[recipient] = _balances[recipient].add(amount);
        emit Transfer(sender, recipient, amount);
    }

