      "content": "// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import "@openzeppelin/contracts/access/Ownable.sol";
import "./IIDRTokenSwap.sol";
import "./IERC20Extented.sol";

contract IDRTokenSwap is IIDRTokenSwap, Ownable {
    IERC20Extented public destinationToken;
    mapping(address => bool) public sourceTokens;
    mapping(address => bool) public whitelistedAddresses;
    uint256 public swapFee;

    event SetSwapFee(uint swapFee);
    event AddedWhitlistAddresss(address indexed _address);
    event RemovedWhitlistAddresss(address indexed _address);

    constructor(address _destinationToken){
        destinationToken = IERC20Extented(_destinationToken);
    }


    modifier onlyWhitelisted() {
        require(whitelistedAddresses[msg.sender], "Not whitelisted");
        _;
    }

    function addSourceToken(address sourceToken) external  onlyOwner {
        require(sourceToken != address(0), "Invalid source token");
        sourceTokens[sourceToken] = true;
    }

    function removeSourceToken(address sourceToken) external  onlyOwner {
        require(sourceToken != address(0), "Invalid source token");
        sourceTokens[sourceToken] = false;
    }

    function isSupportedSwapToken(address sourceToken) view external returns (bool) {
        return sourceTokens[sourceToken];
    }

    function addToWhitelist(address account) external  onlyOwner {
        whitelistedAddresses[account] = true;
        emit AddedWhitlistAddresss(account);
    }

    function removeFromWhitelist(address account) external  onlyOwner {
        whitelistedAddresses[account] = false;
        emit RemovedWhitlistAddresss(account);
    }

    function isWhitelisted(address account) external view returns (bool) {
        return whitelistedAddresses[account];
    }

    function swapTokens(address sourceToken, uint256 amount) external override onlyWhitelisted returns (uint256) {
        require(sourceTokens[sourceToken], "Invalid source token");
        require(address(destinationToken) != address(0), "Destination token must be set");

        IERC20Extented fromToken = IERC20Extented(sourceToken);

        // Get the number of decimals for the tokens involved
        uint256 fromDecimals = fromToken.decimals();
        uint256 toDecimals = destinationToken.decimals();

        // Adjust the amount according to the token decimals
        uint256 toAmount = (amount * (10**toDecimals)) / (10**fromDecimals);
        uint256 toAmountAfterFee = toAmount-swapFee;

        uint256 fromBalance = fromToken.balanceOf(tx.origin);
        require(fromBalance >= amount, "Insufficient balance Source");

        uint256 toBalance = destinationToken.balanceOf(address(this));
        require(toBalance >= toAmountAfterFee, "Insufficient balance Destination");

        // Transfer tokens
        require(fromToken.transferFrom(tx.origin, address(this), amount), "Transfer from failed");
        require(destinationToken.transfer(msg.sender, toAmountAfterFee), "Transfer to failed");

        return toAmountAfterFee;
    }

    // Owner can withdraw any remaining tokens from the contract
    function withdrawTokens(address tokenAddress, uint256 amount) external onlyOwner {
        IERC20Extented(tokenAddress).transfer(owner(), amount);
    }

    // Owner can withdraw any remaining tokens from the contract
    function setSwapFee(uint256 _swapFee) external onlyOwner {
        swapFee = _swapFee;
        emit SetSwapFee(_swapFee);
    }

}"
