// SPDX-License-Identifier: MIT
pragma solidity 0.8.0;

interface ICakePool {
    function userInfo(
        address _user
    ) external view returns (uint256, uint256, uint256, uint256, uint256, uint256, uint256, bool, uint256);

    function getPricePerFullShare() external view returns (uint256);

    function totalLockedAmount() external view returns (uint256);

    function totalShares() external view returns (uint256);

    function BOOST_WEIGHT() external view returns (uint256);

    function MAX_LOCK_DURATION() external view returns (uint256);
}

contract TotalLockAmountCaller {
    address public cakePool = 0x45c54210128a065de780C4B0Df3d16664f7f859e;
    uint256 public cachedTotalLockedAmount;

    event FetchedTotalLockedAmount(uint256 totalLockedAmount);

    function fetchNewTotalLockedCake() external returns (uint256) {

        cachedTotalLockedAmount = ICakePool(cakePool).totalLockedAmount();

        emit FetchedTotalLockedAmount(cachedTotalLockedAmount);

        return cachedTotalLockedAmount;
    }
}
