// File: contracts/interfaces/IMarketplaceAssets.sol


pragma solidity ^0.8.7;
interface IMarketplaceAssets {
    struct MainAssets {
        uint256 tokenId;
        string assetId;
        address owner;
        string asset_name;
        uint256 asset_type;
        uint256 price;
        bool saleable;
        string uri;
    }
    struct MainAssetsNew {
        uint256 tokenId;
        string assetId;
        address owner;
        string asset_name;
        uint256 asset_type;
        uint256 price;
        bool saleable;
        uint256 sellType;
    }
    struct MainAssetsInput  {
        string assetId;
        string asset_name;
        uint256 asset_type;
    }
    struct History  {
        uint256 tokenId;
        address from;
        address to;
        uint256 time;
    }
    
  // LAND can be assigned by the owner
   //function addNewAssetByUser(address,string memory ,string memory ,uint256 ,string memory ) external returns(uint256);
    
  //  function getFullData() external view returns (MainAssets[]  memory ) ;
    //nft event
    event createTokenEvent(address sender,address owner ,uint256 tokenId,string _assetId,string _assetName,uint256 _assetType,string _tokenURI);
    event setAccessListAddressEvent(address sender,address _addr, bool act);
    event setTokenNameEvent (address sender,uint256 tokenId,string _name);
    event setTokenAssetTypeEvent(address sender,uint256 tokenId,uint256 _assetType);
    event setTokenURIEvent(address sender,uint256 tokenId,string _tokenURI);
    
    //market contract Event 
    event buyAssetEvent(address buyer,address seller,uint256 price , uint256 tokenId,uint256 amountForOwner,uint256 royalty);
    event addAssetToMarketEvent(address sender,uint256 tokenId, uint256 price);
    event removeAssetFromEvent(address sender,uint256 tokenId);
    event setAssetNameEvent(address sender,uint256 index,string _name);
    event setTokenOwnerEvent(address sender,uint256 tokenId,address newOwner) ;
    
}
