    mapping(uint256 => uint256) public nftBalance;

    ICloudNFT cloudNFT;
    IReferral nftReferral =
        IReferral(0x6BB6324B2CF6AcBE70CE8c7E4f8B489C18af5542);
    uint256 directCommission = 3;
    uint256 public fixedCloudTokenPrice = 0;
    uint256 public minCloudTokenPrice = 1e7;
    uint256 public maxCloudTokenPrice = 3e7;

    event Transfered(
        address indexed purchaser,
        address indexed referral,
        uint256 amount
    );

    IBEP20 public usdtToken;
    IBEP20 public cloudToken;
    address public cloudTokenLP;

    constructor() public {
        usdtToken = IBEP20(0x55d398326f99059fF775485246999027B3197955);
        cloudNFT = ICloudNFT(0x4a4eD2069ACE9D0835a03716174C3c8F8882DC93);
        cloudToken = IBEP20(0x5C10E4660eC45a89dE85a1FD1B22355B0398fB66);
        cloudTokenLP = address(0x476fFCef308b8F373684Bee2082789e5Bd1BaD83);

        startTime = 1677152436;
        endTime = startTime + 1000 days;

        nftBalance[0] = 50;
        nftBalance[1] = 50;
        nftBalance[2] = 50;
        nftBalance[3] = 50;
        nftBalance[4] = 50;
        nftBalance[5] = 50;

        nftPrice[0] = 100;
        nftPrice[1] = 500;
        nftPrice[2] = 1000;
        nftPrice[3] = 5000;
        nftPrice[4] = 10000;
        nftPrice[5] = 50000;
    }

    fallback() external payable {}

    receive() external payable {}

    function onERC721Received(
        address operator,
        address from,
        uint256 tokenId,
        bytes calldata data
    ) external pure returns (bytes4) {
        return
            bytes4(
                keccak256("onERC721Received(address,address,uint256,bytes)")
            );
    }

    function cloudTokenPrice() public view returns (uint256) {
        if (fixedCloudTokenPrice > 0) return fixedCloudTokenPrice;
        (uint256 _reserve0, uint256 _reserve1, ) = IPancakePair(cloudTokenLP)
            .getReserves();
        (uint256 _rewardRev, uint256 _usdRev) = address(cloudToken) <
            address(usdtToken)
            ? (_reserve0, _reserve1)
            : (_reserve1, _reserve0);

        return _usdRev.mul(1e8).div(_rewardRev);
    }

    //buy tokens
    function buy(uint256 nftType, uint256 quantity) public {
        require(block.timestamp >= startTime, "not started");
        require(block.timestamp <= endTime, "finished");
        require(quantity > 0, "quantity must be greater than zero");
        require(nftType >= 0 && nftType <= 5, "nftType is invalid");
        require(nftBalance[nftType] >= quantity);
        require(
            cloudTokenPrice() >= minCloudTokenPrice,
            "cloudTokenPrice must be greater then minCloudTokenPrice"
        );
        require(
            cloudTokenPrice() <= maxCloudTokenPrice,
            "cloudTokenPrice must be less then maxCloudTokenPrice"
        );
        require(nftBalance[nftType] >= quantity, "not enough nft!");

        uint256 totalAmount = nftPrice[nftType]
            .mul(quantity)
            .mul(1e18)
            .mul(1e8)
            .div(cloudTokenPrice());
        cloudToken.safeTransferFrom(msg.sender, address(this), totalAmount);

        // pay direct Commission amount for referrer
        address referrer = nftReferral.getReferrer(msg.sender);
        if (referrer != address(0)) {
            uint256 directCommissionAmount = totalAmount
                .mul(directCommission)
                .div(100);
            cloudToken.transfer(referrer, directCommissionAmount);
            nftReferral.recordReferralCommission(
                referrer,
                directCommissionAmount.mul(cloudTokenPrice()).div(1e8)
            );
        }
        for (uint256 i = 0; i < quantity; i++) {
            cloudNFT.mint(msg.sender, "",uint8(nftType));
        }
        nftBalance[nftType] = nftBalance[nftType] - quantity;
    }

    function setDirectCommission(uint256 _directCommission) public onlyOwner {
        directCommission = _directCommission;
    }

    function setNftReferral(IReferral _nftReferral) public onlyOwner {
        nftReferral = _nftReferral;
    }

    function setPrice(uint256 nftType, uint256 price) public onlyOwner {
        require(nftType >= 0 && nftType <= 5, "nftType is invalid");

        nftPrice[nftType] = price;
    }

    function setBalance(uint256 nftType, uint256 balance) public onlyOwner {
        require(nftType >= 0 && nftType <= 5, "nftType is invalid");

        nftBalance[nftType] = balance;
    }

    function setStartTime(uint256 _startTime) public onlyOwner {
        startTime = _startTime;
    }

    function setEndTime(uint256 _endTime) public onlyOwner {
        endTime = _endTime;
    }

    function setCloudNFT(ICloudNFT _cloudNFT) public onlyOwner {
        cloudNFT = _cloudNFT;
    }

    function setCloudToken(IBEP20 _cloudToken) public onlyOwner {
        cloudToken = _cloudToken;
    }

    function setCloudTokenLP(address _cloudTokenLP) external onlyOwner {
        cloudTokenLP = _cloudTokenLP;
    }

    function setFixedCloudTokenPrice(uint256 _fixedCloudTokenPrice)
        external
        onlyOwner
    {
        fixedCloudTokenPrice = _fixedCloudTokenPrice;
    }

    function setMaxCloudTokenPrice(uint256 _maxCloudTokenPrice)
        external
        onlyOwner
    {
        maxCloudTokenPrice = _maxCloudTokenPrice;
    }

    function setMinCloudTokenPrice(uint256 _minCloudTokenPrice)
        external
        onlyOwner
    {
        minCloudTokenPrice = _minCloudTokenPrice;
    }

    // Recover lost bnb and send it to the contract owner
    function recoverLostBNB() public onlyOwner {
        address payable _owner = msg.sender;
        _owner.transfer(address(this).balance);
    }

    // Ensure requested tokens aren't users WEX tokens
    function recoverLostTokensExceptOurTokens(address _token, uint256 amount)
        public
        onlyOwner
    {
        IBEP20(_token).transfer(msg.sender, amount);
    }
}
