/**
 * @dev Swap two tokens with predefined rate
 *
 */
contract TokenSwaps is Ownable, ReentrancyGuard {

    IERC20Metadata public newToken      = IERC20Metadata(0x2749C9f2f8568d389BBF61ed77784A43C3cD3E19);
    IERC20Metadata public oldToken    = IERC20Metadata(0xc7981767f644C7F8e483DAbDc413e8a371b83079);

    uint256 public totalOldTokenSwap;
    uint256 public ratio = 5;
    uint256 public bonusFinishTime;
    uint256 public bonusPercent;

    event Swap(address indexed account, uint256 amountOld, uint256 amountNew);

    function ownerWithdrawTokens(address addressToken) public onlyOwner{    
        IERC20Metadata(addressToken).transfer(msg.sender, IERC20Metadata(addressToken).balanceOf(address(this)));
    }

    function ownerSetupSwapRatio(uint256 newRatio) public onlyOwner{
        require(newRatio != 0, "Swap ratio must not be zero");
        ratio = newRatio;
    }

    function ownerSetupSwapBonus(uint256 newBonusFinishTime, uint256 newBonusPercent) public onlyOwner{
        bonusFinishTime = newBonusFinishTime;
        bonusPercent = newBonusPercent;
    }

    function swap(uint256 amountOldToken) public nonReentrant{

        // Deposit old token
        oldToken.transferFrom(msg.sender, address(this), amountOldToken);
        totalOldTokenSwap += amountOldToken;

        // Receive new token
        uint256 amountNewToken = amountOldToken / ratio;
        if(block.timestamp <= bonusFinishTime){
            amountNewToken += (amountNewToken * bonusPercent / 1000);
        }
        newToken.transfer(msg.sender, amountNewToken);

        emit Swap(msg.sender, amountOldToken, amountNewToken);
    }
}
