contract ELVNTier is ERC721Enumerable, Ownable {
    using Strings for uint256;

    mapping(uint => uint) public claimedStakingRewards;
    mapping(uint => uint) public previousStakingRewards;
    mapping(uint => uint) public changeTimestamp;
    mapping(uint => uint) public tierLevelList;
    mapping(uint => uint) public stakingValue;
    uint public downgradeFeeStep;
    uint public downgradeFee;
    mapping(uint => uint) public stakingRate;
    mapping(uint => uint) public cost;

    gELVN public gELVNToken;

    constructor(address payable _gELVNAddress, string memory baseURI_) ERC721("11Minutes Tier NFT", "ELVNTIER") public{
        gELVNToken = gELVN(_gELVNAddress);
        setBaseURI(baseURI_);

        downgradeFeeStep = 5;
        downgradeFee = 25;
        
        stakingRate[1] = 3;
        stakingRate[2] = 4;
        stakingRate[3] = 7; 
        stakingRate[4] = 12; 
        stakingRate[5] = 16; 
        stakingRate[6] = 20; 
        stakingRate[7] = 27; 
        stakingRate[8] = 35; 

        cost[1] = 1750 ether;
        cost[2] = 8800 ether;
        cost[3] = 17500 ether; 
        cost[4] = 35000 ether; 
        cost[5] = 87500 ether; 
        cost[6] = 175000 ether; 
        cost[7] = 350000 ether; 
        cost[8] = 700000 ether; 
        
    }

    modifier validLevel(uint _tier) {
        require(_tier >= 1 && _tier <= 8, "ELVNTier: Invalid tier!");
        _;
    }

    function mint(address _to, uint _level) public validLevel(_level) {
        uint _cost = cost[_level];
        require(gELVNToken.totalAvailableBalance(msg.sender) >= _cost, "ELVNTier: Not enough money for this tier!");

        uint _tokenId = totalSupply() + 1;
        // token ids starts with 1
        gELVNToken.unvestedPayment(msg.sender, _cost);
        _mint(_to, _tokenId);
        changeTimestamp[_tokenId] = block.timestamp;
        stakingValue[_tokenId] = _cost;
        tierLevelList[_tokenId] = _level;
    }

    function tierLevel(uint _tokenId) public view returns(uint) {
        return tierLevelList[_tokenId];
    }

    function stakingRewards(uint _tokenId) public view returns(uint) {
        uint _stakingDays = (block.timestamp - changeTimestamp[_tokenId]) / 60 / 60 / 24;
        uint _stakingLevel = tierLevelList[_tokenId];
        uint _stakingRewards = (stakingValue[_tokenId] * stakingRate[_stakingLevel] / 365 / 100 * _stakingDays) + previousStakingRewards[_tokenId] - claimedStakingRewards[_tokenId];
        return _stakingRewards;
    }

    function upgrade(uint _tokenId, uint _level) external validLevel(_level) {
        require(msg.sender == ownerOf(_tokenId) || msg.sender == owner());
        require(tierLevelList[_tokenId] < _level, "ELVNTier: You can't upgrade to lower level!");
        uint _upgradeCost = cost[_level] - stakingValue[_tokenId];
        require(gELVNToken.totalAvailableBalance(msg.sender) >= _upgradeCost, "ELVNTier: The payment is too low!");
        gELVNToken.unvestedPayment(msg.sender, _upgradeCost);
        previousStakingRewards[_tokenId] = stakingRewards(_tokenId);
        claimedStakingRewards[_tokenId] = 0;
        changeTimestamp[_tokenId] = block.timestamp;
        setTierLevel(_tokenId, _level);
        stakingValue[_tokenId] = cost[_level];
    }

    function setTierLevel(uint _tokenId, uint _level) internal {
        tierLevelList[_tokenId] = _level;
    }

    function claimStakingRewards(uint _tokenId) public {
        address _owner = ownerOf(_tokenId);
        require(_owner == msg.sender, "ELVNTier: You are not the owner of this token!");
        uint _currentRewards = stakingRewards(_tokenId);
        uint _usedUnvestedBalance = gELVNToken.usedUnvestedBalance(_owner);
        if(_currentRewards <= _usedUnvestedBalance) {
            gELVNToken.paymentIncome(_owner, _currentRewards);
        } else {
            uint _transferAmount = _currentRewards - _usedUnvestedBalance;
            gELVNToken.paymentIncome(_owner, _usedUnvestedBalance);
            gELVNToken.transfer(_owner, _transferAmount);
        }

        previousStakingRewards[_tokenId] = 0;
        claimedStakingRewards[_tokenId] = _currentRewards;
    }

    function downgrade(uint _tokenId, uint _level) public {
        require(tierLevelList[_tokenId] != 1, "ELVNTier: You can't downgrade this token!");
        address _owner = ownerOf(_tokenId);
        require(_owner == msg.sender, "ELVNTier: You are not the owner of this token!");
        require(tierLevelList[_tokenId] > _level, "ELVNTier: You can't downgrade to a higher level!");
        uint _monthPast = (block.timestamp - changeTimestamp[_tokenId]) / 2629743; // there are 2629743 seconds in a month
        uint _previousDowngradeFee = downgradeFee;
        if(_monthPast >= 5) {
            downgradeFee = 0;
        } else {
            downgradeFee -=  (_monthPast * downgradeFeeStep);
        }
        
        uint _downgradeValue = stakingValue[_tokenId] - cost[_level];
        address serviceAddr = gELVNToken.serviceAddr();
        uint _downgradeFeeTotal = downgradeFee * _downgradeValue / 100;

        if(_downgradeFeeTotal > 0) {
            gELVNToken.transfer(serviceAddr, _downgradeFeeTotal);
        }

        uint _usedUnvestedBalance = gELVNToken.usedUnvestedBalance(_owner);
        uint _claimAmount = _downgradeValue - _downgradeFeeTotal;
        if(_claimAmount <= _usedUnvestedBalance) {
            gELVNToken.paymentIncome(_owner, _claimAmount);
        } else {
            uint _transferAmount = _claimAmount - _usedUnvestedBalance;
            gELVNToken.paymentIncome(_owner, _usedUnvestedBalance);
            gELVNToken.transfer(_owner, _transferAmount);
        }
        previousStakingRewards[_tokenId] = stakingRewards(_tokenId);
        claimedStakingRewards[_tokenId] = 0;
        setTierLevel(_tokenId, _level);
        stakingValue[_tokenId] = cost[_level];
        downgradeFee =_previousDowngradeFee;
    }

    function walletOfOwner(address _owner)
        external
        view
        returns (uint256[] memory)
    {
        uint256 ownerTokenCount = balanceOf(_owner);
        uint256[] memory tokenIds = new uint256[](ownerTokenCount);
        for (uint256 i; i < ownerTokenCount; i++) {
            tokenIds[i] = tokenOfOwnerByIndex(_owner, i);
        }
        return tokenIds;
    }

    function setBaseURI(string memory _newURI) public onlyOwner {
        __baseURI = _newURI;
    }

    function setCost(uint _newCost, uint _tier) external onlyOwner validLevel(_tier) {
        cost[_tier] = _newCost;
    }

    function setStakingRate(uint _newRate, uint _tier) external onlyOwner validLevel(_tier) {
        stakingRate[_tier] = _newRate;
    }

    function setDowngradeFee(uint _new) external onlyOwner {
        downgradeFee = _new;
    }

    function setDowngradeStep(uint _new) external onlyOwner {
        downgradeFeeStep = _new;
    }

    function tokenURI(uint256 tokenId)
        public
        view
        virtual
        override
        returns (string memory)
    {
        require(
            _exists(tokenId),
            "ERC721Metadata: URI query for nonexistent token"
        );

        string memory currentBaseURI = _baseURI();
        uint256 _tierLevel = tierLevelList[tokenId];
        return
            bytes(currentBaseURI).length > 0
                ? string(
                    abi.encodePacked(
                        currentBaseURI,
                        _tierLevel.toString(),
                        ".json"
                    )
                )
                : "";
    }

    function withdraw(address _tokenAddress, uint _amount) external onlyOwner  {
        IERC20 tokenContract = IERC20(_tokenAddress);
        tokenContract.transfer(msg.sender, _amount);
    }
}"}}
