contract MetatronPay is Ownable, Pausable {
    using Address for address payable;
    using SafeBEP20 for IBEP20;

    struct UserDetails {
        address token;
        uint256 DepositAmount;
        uint256 ClaimAmount;
    }

    IBEP20 public AUREA;
    IBEP20 public META;
    IBEP20 public USDT;
    address public signer;
    uint256 public expiry = 300;

    mapping(bytes32 => bool) private msgHash;
    mapping(address => bool) public isAuthorized;
    mapping(address => mapping(address => UserDetails)) public userInfo;

    event SetSigner(address indexed owner, address indexed signer);
    event AddToken(
        address indexed user,
        address indexed token,
        uint256 indexed amount,
        uint256 time
    );
    event Deposit(
        address indexed depositor,
        address indexed depositToken,
        uint256 indexed depositAmount,
        uint256 time
    );
    event Claim(
        address indexed user,
        address indexed ClaimToken,
        uint256 indexed claimAmount,
        uint256 time
    );
    event Receive(address indexed user, uint256 indexed amount, uint256 time);
    event Retrieve(
        address indexed token,
        address indexed user,
        uint256 indexed amount,
        uint256 time
    );

    constructor(
        address _signer,
        address AUREAtoken,
        address METAtoken,
        address USDTtoken
    ) {
        signer = _signer;
        AUREA = IBEP20(AUREAtoken);
        META = IBEP20(METAtoken);
        USDT = IBEP20(USDTtoken);
        isAuthorized[AUREAtoken] = true;
        isAuthorized[METAtoken] = true;
        isAuthorized[USDTtoken] = true;
    }

    function setAuthorization(address _token, bool status) external onlyOwner {
        isAuthorized[_token] = status;
    }

    function setAureaToken(address _token, bool status) external onlyOwner {
        AUREA = IBEP20(_token);
        isAuthorized[_token] = status;
    }

    function setMetaToken(address _token, bool status) external onlyOwner {
        META = IBEP20(_token);
        isAuthorized[_token] = status;
    }

    function setUSDTtoken(address _token, bool status) external onlyOwner {
        USDT = IBEP20(_token);
        isAuthorized[_token] = status;
    }

    function setExpiry(uint256 _newExpiry) external onlyOwner {
        require(_newExpiry > 0, "Invalid Expiry");
        expiry = _newExpiry;
    }

    function setSigner(address _signer) external onlyOwner {
        require(_signer != address(0), "Invalid Signer Address");
        signer = _signer;
        emit SetSigner(msg.sender, _signer);
    }

    receive() external payable {
        emit Receive(msg.sender, msg.value, block.timestamp);
    }

    function pause() external onlyOwner {
        _pause();
    }

    function unpause() external onlyOwner {
        _unpause();
    }

    function addToken(address token, uint256 amount) external onlyOwner {
        require(isAuthorized[token], "Invalid token address");
        require(amount > 0, "Invalid amount");
        IBEP20(token).safeTransferFrom(msg.sender, address(this), amount);
        emit AddToken(msg.sender, address(token), amount, block.timestamp);
    }

    function deposit(address _token, uint256 _amount) external whenNotPaused {
        require(isAuthorized[_token], "Unauthorized Token");
        require(_amount > 0, "Invalid amount");
        UserDetails storage user = userInfo[msg.sender][_token];
        user.token = _token;
        user.DepositAmount += _amount;
        IBEP20(_token).safeTransferFrom(msg.sender, address(this), _amount);
        emit Deposit(msg.sender, address(_token), _amount, block.timestamp);
    }

    function claim(
        address _user,
        address _token,
        uint256 amount,
        uint8 v,
        bytes32 r,
        bytes32 s,
        uint256 deadline
    ) external whenNotPaused {
        require(isAuthorized[_token], "Unauthorized Token");
        require(
            block.timestamp <= deadline && deadline <= block.timestamp + expiry,
            "Metatronpay: Expired deadline"
        );
        bytes32 messageHash = toSigEthMsg(_user, _token, amount, deadline);
        require(!msgHash[messageHash], "claim: signature duplicate");
        msgHash[messageHash] = true;
        address src = verifySignature(messageHash, v, r, s);
        require(signer == src, " claim: unauthorized");
        UserDetails storage user = userInfo[msg.sender][_token];
        user.token = _token;
        user.ClaimAmount += amount;
        IBEP20(_token).safeTransfer(_user, amount);
        emit Claim(_user, address(_token), amount, block.timestamp);
    }

    function verifySignature(
        bytes32 _messageHash,
        uint8 v,
        bytes32 r,
        bytes32 s
    ) public pure returns (address signatureAddress) {
        bytes32 hash = ECDSA.toEthSignedMessageHash(_messageHash);
        signatureAddress = ECDSA.recover(hash, v, r, s);
    }

    function toSigEthMsg(
        address _receiver,
        address token,
        uint256 amount,
        uint256 time
    ) public view returns (bytes32 messageHash) {
        messageHash = keccak256(
            abi.encodePacked(address(this), _receiver, token, amount, time)
        );
    }

    function retrieve(
        address _token,
        address to,
        uint256 amount
    ) external onlyOwner {
        require(
            to != address(0) && amount > 0,
            "Metatronpay :Invalid Address || amount"
        );

        if (_token == address(0)) {
            require(
                address(this).balance >= amount,
                "Metatronpay:Invalid Amount"
            );
            require(
                payable(to).send(amount),
                "Metatronpay : Transaction failed"
            );
        } else {
            require(
                IBEP20(_token).balanceOf(address(this)) >= amount,
                "Metatronpay:Invalid Amount"
            );
            IBEP20(_token).safeTransfer(to, amount);
        }
        emit Retrieve(_token, to, amount, block.timestamp);
    }
}
