                    // as the one above.
                    let cc := add(add(add(_bytes, lengthmod), mul(0x20, iszero(lengthmod))), _start)
                } lt(mc, end) {
                    mc := add(mc, 0x20)
                    cc := add(cc, 0x20)
                } {
                    mstore(mc, mload(cc))
                }

                mstore(tempBytes, _length)

                //update free-memory pointer
                //allocating the array padded to 32 bytes like the compiler does now
                mstore(0x40, and(add(mc, 31), not(31)))
            }
            //if we want a zero-length slice let's just return a zero-length array
            default {
                tempBytes := mload(0x40)
                //zero out the 32 bytes slice we are about to return
                //we need to do it because Solidity does not garbage collect
                mstore(tempBytes, 0)

                mstore(0x40, add(tempBytes, 0x20))
            }
        }

        return tempBytes;
    }

    function toAddress(bytes memory _bytes, uint256 _start) internal pure returns (address) {
        require(_bytes.length >= _start + 20, "toAddress_outOfBounds");
        address tempAddress;

        assembly {
            tempAddress := div(mload(add(add(_bytes, 0x20), _start)), 0x1000000000000000000000000)
        }

        return tempAddress;
    }

    function toUint8(bytes memory _bytes, uint256 _start) internal pure returns (uint8) {
        require(_bytes.length >= _start + 1 , "toUint8_outOfBounds");
        uint8 tempUint;

        assembly {
            tempUint := mload(add(add(_bytes, 0x1), _start))
        }

        return tempUint;
    }

    function toUint16(bytes memory _bytes, uint256 _start) internal pure returns (uint16) {
        require(_bytes.length >= _start + 2, "toUint16_outOfBounds");
        uint16 tempUint;

        assembly {
            tempUint := mload(add(add(_bytes, 0x2), _start))
        }

        return tempUint;
    }

    function toUint32(bytes memory _bytes, uint256 _start) internal pure returns (uint32) {
        require(_bytes.length >= _start + 4, "toUint32_outOfBounds");
        uint32 tempUint;

        assembly {
            tempUint := mload(add(add(_bytes, 0x4), _start))
        }

        return tempUint;
    }

    function toUint64(bytes memory _bytes, uint256 _start) internal pure returns (uint64) {
        require(_bytes.length >= _start + 8, "toUint64_outOfBounds");
        uint64 tempUint;

        assembly {
            tempUint := mload(add(add(_bytes, 0x8), _start))
        }

        return tempUint;
    }

    function toUint96(bytes memory _bytes, uint256 _start) internal pure returns (uint96) {
        require(_bytes.length >= _start + 12, "toUint96_outOfBounds");
        uint96 tempUint;

        assembly {
            tempUint := mload(add(add(_bytes, 0xc), _start))
        }

        return tempUint;
    }

    function toUint128(bytes memory _bytes, uint256 _start) internal pure returns (uint128) {
        require(_bytes.length >= _start + 16, "toUint128_outOfBounds");
        uint128 tempUint;

        assembly {
            tempUint := mload(add(add(_bytes, 0x10), _start))
        }

        return tempUint;
    }

    function toUint256(bytes memory _bytes, uint256 _start) internal pure returns (uint256) {
        require(_bytes.length >= _start + 32, "toUint256_outOfBounds");
        uint256 tempUint;

        assembly {
            tempUint := mload(add(add(_bytes, 0x20), _start))
        }

        return tempUint;
    }

    function toBytes32(bytes memory _bytes, uint256 _start) internal pure returns (bytes32) {
        require(_bytes.length >= _start + 32, "toBytes32_outOfBounds");
        bytes32 tempBytes32;

        assembly {
            tempBytes32 := mload(add(add(_bytes, 0x20), _start))
        }

        return tempBytes32;
    }

    function equal(bytes memory _preBytes, bytes memory _postBytes) internal pure returns (bool) {
        bool success = true;

        assembly {
            let length := mload(_preBytes)

            // if lengths don't match the arrays are not equal
            switch eq(length, mload(_postBytes))
            case 1 {
                // cb is a circuit breaker in the for loop since there's
                //  no said feature for inline assembly loops
                // cb = 1 - don't breaker
                // cb = 0 - break
                let cb := 1

                let mc := add(_preBytes, 0x20)
                let end := add(mc, length)

                for {
                    let cc := add(_postBytes, 0x20)
                // the next line is the loop condition:
                // while(uint256(mc < end) + cb == 2)
                } eq(add(lt(mc, end), cb), 2) {
                    mc := add(mc, 0x20)
                    cc := add(cc, 0x20)
                } {
                    // if any of these checks fails then arrays are not equal
                    if iszero(eq(mload(mc), mload(cc))) {
                        // unsuccess:
                        success := 0
                        cb := 0
                    }
                }
            }
            default {
                // unsuccess:
                success := 0
            }
        }

        return success;
    }

    function equalStorage(
        bytes storage _preBytes,
        bytes memory _postBytes
    )
        internal
        view
        returns (bool)
    {
        bool success = true;

        assembly {
            // we know _preBytes_offset is 0
            let fslot := sload(_preBytes.slot)
            // Decode the length of the stored array like in concatStorage().
            let slength := div(and(fslot, sub(mul(0x100, iszero(and(fslot, 1))), 1)), 2)
            let mlength := mload(_postBytes)

            // if lengths don't match the arrays are not equal
            switch eq(slength, mlength)
            case 1 {
                // slength can contain both the length and contents of the array
                // if length < 32 bytes so let's prepare for that
                // v. http://solidity.readthedocs.io/en/latest/miscellaneous.html#layout-of-state-variables-in-storage
                if iszero(iszero(slength)) {
                    switch lt(slength, 32)
                    case 1 {
                        // blank the last byte which is the length
                        fslot := mul(div(fslot, 0x100), 0x100)

                        if iszero(eq(fslot, mload(add(_postBytes, 0x20)))) {
                            // unsuccess:
                            success := 0
                        }
                    }
                    default {
                        // cb is a circuit breaker in the for loop since there's
                        //  no said feature for inline assembly loops
                        // cb = 1 - don't breaker
                        // cb = 0 - break
                        let cb := 1

                        // get the keccak hash to get the contents of the array
                        mstore(0x0, _preBytes.slot)
                        let sc := keccak256(0x0, 0x20)

                        let mc := add(_postBytes, 0x20)
                        let end := add(mc, mlength)

                        // the next line is the loop condition:
                        // while(uint256(mc < end) + cb == 2)
                        for {} eq(add(lt(mc, end), cb), 2) {
                            sc := add(sc, 1)
                            mc := add(mc, 0x20)
                        } {
                            if iszero(eq(sload(sc), mload(mc))) {
                                // unsuccess:
                                success := 0
                                cb := 0
                            }
                        }
                    }
                }
            }
            default {
                // unsuccess:
                success := 0
            }
        }

        return success;
    }
}

abstract contract StakingPoolStorage {
    using BytesLib for bytes;
    using SafeERC20 for IERC20;

    uint256 internal constant DENOMINATOR_TIME_OF_A_MONTH = 2592000; // 30 days

    uint256 public constant DATE_LOCK = 31536000; // 365 days

    struct BlockProfit {
        uint256 minStake;
        uint256 rewardPerMonth;
    }

    struct Referral {
        address parent;
        address[] children;
        uint256 totalStakingChild;
        uint256 totalSystemStaking;
    }

    struct Star {
        uint8 requireBlockProfit;
        uint8 requireChild;
        uint256 requireTotalStakingChild;
        uint256 requireSystemStaking;
        uint8 referralProfit;
        uint8 compoundInterestRate; // chỉ số lãi kép
        uint256 starUpReward;
    }

    struct Claimed {
        uint256 claimedBlockProfit;
        uint256 claimedReferral;
        uint256 claimedCompoundInterest;
    }

    enum LockType {
        Unlocked,
        LockedAll,
        LockedStakeProfit
    }

    /**
     * This mapping is used to keep track of the index for the stakers.
     */
    mapping(address => uint256) internal _lastStakeTime;

    mapping(address => uint256) internal _lastClaimTime;

    mapping(address => uint8) internal _stakerBlockIndex;

    mapping(address => Referral) internal _referrals;

    mapping(address => uint256) internal _oldReward;

    mapping(address => uint8) internal _maxStar;

    mapping(address => uint256) internal _stakesSummary;

    mapping(uint8 => BlockProfit) internal _blockProfits;

    mapping(uint8 => Star) internal _stars;

    mapping(address => Claimed) internal _claimed;

    mapping(address => bool) internal _unstake;

    // 0: not block, 1: block all, 2: only receive ref and star reward
    mapping(address => LockType) internal _blackList;

    // need to save: current stake, last stake time to withdraw, total stake, stake history

    /**
     * Metadata: current number of staked tokens of all stakers.
     * `totalStalked` is increased when a `stake` call succeeded, and
     * decreased when a `withdraw` or `exit` call succeeded.
     */
    uint256 public totalStaking;

    /**
     * Metadata: current number of stakers.
     */
    uint256 public totalStakers;
    /**
     * Min token amount each stake
     */
    uint256 public minStake;

    /**
     * Max total token amount of staker staked
     */
    uint256 public maxStake;

    /**
     * Limit total token staked in pool
     */
    uint256 public totalTokenInPool;

    /**
     * Date start event
     */
    uint256 public startDate;

    /**
     * Date end event
     */
    uint256 public endDate;

    event Staked(address indexed staker, uint256 amount);
    event WithdrawnPrincipal(address indexed staker, uint256 stakeAmount);
    event WithdrawnStakeReward(address indexed staker, uint256 rewardAmount);
    event WithdrawnStarReward(address indexed staker, uint256 starRewardAmount);
    event TransferReferral(
        address from,
        address indexed receiver,
        uint256 rewardAmount
    );

    modifier isPoolStarted() {
        require(
            block.timestamp >= startDate,
            "StakingRewards: pool has not been started yet"
        );
        _;
    }

    modifier isNotInBlackList(address staker) {
        require(
            _blackList[staker] == LockType.Unlocked,
            "StakingRewards: staker is blacklisted"
        );
        _;
    }
}

contract StakingPool is
    StakingPoolStorage,
    AccessControl,
    ERC1363Payable,
    ReentrancyGuard,
    Pausable
{
    using BytesLib for bytes;
    using SafeERC20 for IERC20;

    address internal delegateAddress;

    constructor(
        IERC1363 acceptedToken_,
        uint256 totalTokenInPool_,
        uint256 startDate_,
        uint256 endDate_
    ) ERC1363Payable(acceptedToken_) {
        require(
            totalTokenInPool_ > 0,
            "StakingRewards: total token in pool must be greater than 0"
        );
        require(
            endDate_ > startDate_,
            "StakingRewards: end date must be after start date"
        );
        minStake = 1000e18;
        maxStake = 2000000e18;
        totalTokenInPool = totalTokenInPool_;
        startDate = startDate_;
        endDate = endDate_;
        _blockProfits[1] = BlockProfit(1000e18, 30);
        _blockProfits[2] = BlockProfit(10000e18, 40);
        _blockProfits[3] = BlockProfit(100000e18, 50);
        _blockProfits[4] = BlockProfit(200000e18, 60);
        _blockProfits[5] = BlockProfit(500000e18, 70);
        _blockProfits[6] = BlockProfit(1000000e18, 80);
        _blockProfits[7] = BlockProfit(maxStake + 1, 0);
        _stars[1] = Star(1, 0, 0, 0, 80, 50, 0);
        _stars[2] = Star(2, 3, 0, 200000e18, 50, 30, 0);
        _stars[3] = Star(3, 5, 200000e18, 1000000e18, 30, 20, 20000e18);
        _stars[4] = Star(4, 8, 500000e18, 2500000e18, 20, 10, 50000e18);
        _stars[5] = Star(5, 10, 1000000e18, 5000000e18, 10, 5, 100000e18);
        _stars[6] = Star(5, 12, 2000000e18, 10000000e18, 5, 5, 200000e18);
        _grantRole(DEFAULT_ADMIN_ROLE, msg.sender);
    }

    function setDelegateAddress(
        address _newDelegate
    ) external onlyRole(DEFAULT_ADMIN_ROLE) {
        delegateAddress = _newDelegate;
    }

    function setStarAttribute(
        uint8 starIndex,
        uint8 requireBlockProfit,
        uint8 requireChild,
        uint256 requireTotalStakingChild,
        uint256 requireSystemStaking,
        uint8 referralProfit,
        uint8 compoundInterestRate,
        uint256 starUpReward
    ) external onlyRole(DEFAULT_ADMIN_ROLE) {
        _stars[starIndex] = Star(
            requireBlockProfit,
            requireChild,
            requireTotalStakingChild,
            requireSystemStaking,
            referralProfit,
            compoundInterestRate,
            starUpReward
        );
    }

    function setBlockProfitAttribute(
        uint8 blockIndex,
        uint256 minStake,
        uint256 rewardPerMonth
    ) external onlyRole(DEFAULT_ADMIN_ROLE) {
        _blockProfits[blockIndex] = BlockProfit(minStake, rewardPerMonth);
    }

    function stakingToken() public view returns (IERC20) {
        return IERC20(acceptedToken());
    }

    /**
     * Pause pool
     */
    function stop() external onlyRole(DEFAULT_ADMIN_ROLE) {
        _pause();
    }

    /**
     * @dev This method is called after `onApprovalReceived`.
     *  Note: remember that the token contract address is always the message sender.
     * @param sender address The address which called `approveAndCall` function
     * @param amount uint256 The amount of tokens to be spent
     */
    function _approvalReceived(
        address sender,
        uint256 amount,
        bytes memory data
    ) internal override {
        (bool success, bytes memory returndata) = delegateAddress.delegatecall(
            abi.encodeWithSignature(
                "_stake(address,uint256,address)",
                sender,
                amount,
                data.toAddress(0)
            )
        );

        if (!success) {
            assembly {
                revert(add(returndata, 32), mload(returndata))
            }
        }
    }

    /**
     * Transfer the current amount of rewards to staker.
     */
    function withdrawStakeReward()
        public
        isPoolStarted
        whenNotPaused
        isNotInBlackList(msg.sender)
    {
        (bool success, bytes memory returndata) = delegateAddress.delegatecall(
            abi.encodeWithSignature("withdrawStakeReward()")
        );

        if (!success) {
            assembly {
                revert(add(returndata, 32), mload(returndata))
            }
        }
    }

    function withdrawPrincipal()
        external
        isPoolStarted
        whenNotPaused
        isNotInBlackList(msg.sender)
    {
        require(
            _stakesSummary[msg.sender] > 0,
            "StakingRewards: the caller is not a staker"
        );

        require(
            block.timestamp > _lastStakeTime[msg.sender] + DATE_LOCK,
            "StakingRewards: lock time is not over yet"
        );

        require(
            !_unstake[msg.sender],
            "StakingRewards: you already unstake before"
        );

        _oldReward[msg.sender] += _calculateStakeRewards(msg.sender);

        stakingToken().safeTransfer(msg.sender, _stakesSummary[msg.sender]);
        _stakerBlockIndex[msg.sender] = 0;
        _unstake[msg.sender] = true;
        totalStakers--;
        emit WithdrawnPrincipal(msg.sender, _stakesSummary[msg.sender]);
        _stakesSummary[msg.sender] = 0;
    }

    function isPassStar(
        address staker,
        uint8 star
    ) internal view returns (bool) {
        return (_referrals[staker].children.length >=
            _stars[star].requireChild &&
            _referrals[staker].totalStakingChild >=
            _stars[star].requireTotalStakingChild &&
            _stakerBlockIndex[staker] >= _stars[star].requireBlockProfit &&
            _referrals[staker].totalSystemStaking >=
            _stars[star].requireSystemStaking);
    }

    function calculateStarRewards(
        address staker
    ) public view returns (uint256, uint8) {
        uint256 starRewards;
        uint8 star = _maxStar[staker];
        for (uint8 i = _maxStar[staker] + 1; i <= 6; i++) {
            if (isPassStar(staker, i)) {
                star = i;
                starRewards += _stars[i].starUpReward;
            } else {
                break;
            }
        }
        return (starRewards, star);
    }

    function withdrawStarReward() public isPoolStarted whenNotPaused {
        require(
            _blackList[msg.sender] != LockType.LockedAll,
            "StakingRewards: You don't have permission to withdraw star reward"
        );
        (uint256 starRewards, uint8 star) = calculateStarRewards(msg.sender);
        require(starRewards > 0, "StakingRewards: You don't have star reward");
        stakingToken().safeTransfer(msg.sender, starRewards);
        _maxStar[msg.sender] = star;
        emit WithdrawnStarReward(msg.sender, starRewards);
    }

    /**
     * Calculate the amount of rewards for a Stake `stake_` since last time the staker withdrawn his rewards.
     */
    function _calculateStakeRewards(
        address staker
    ) internal view virtual returns (uint256) {
        uint256 timestamp = block.timestamp;
        if (timestamp > _lastStakeTime[staker] + DATE_LOCK) {
            timestamp = _lastStakeTime[staker] + DATE_LOCK;
        }

        uint256 rewards;

        //calculator seconds
        uint256 numSeconds = timestamp - _lastClaimTime[staker];
        if (numSeconds > 0) {
            rewards +=
                (numSeconds *
                    _blockProfits[_stakerBlockIndex[staker]].rewardPerMonth *
                    _stakesSummary[staker]) /
                (DENOMINATOR_TIME_OF_A_MONTH * 1000);
        }

        return rewards;
    }

    /**
     * Withdraw all remaining tokens in the pool
     */
    function transferToOwner() external onlyRole(DEFAULT_ADMIN_ROLE) {
        stakingToken().safeTransfer(
            msg.sender,
            stakingToken().balanceOf(address(this))
        );
    }

    function supportsInterface(
        bytes4 interfaceId
    )
        public
        view
        virtual
        override(ERC1363Payable, AccessControl)
        returns (bool)
    {
        return
            interfaceId == type(IERC1363Receiver).interfaceId ||
            interfaceId == type(IERC1363Spender).interfaceId ||
            interfaceId == type(IAccessControl).interfaceId ||
            interfaceId == type(IERC165).interfaceId ||
            super.supportsInterface(interfaceId);
    }

    function lockStaker(
        address staker,
        LockType lockType
    ) external onlyRole(DEFAULT_ADMIN_ROLE) isNotInBlackList(staker) {
        require(
            lockType == LockType.LockedAll ||
                lockType == LockType.LockedStakeProfit,
            "StakingRewards: invalid lock staker type"
        );
        if (_stakesSummary[staker] > 0) {
            totalStakers--;
        }
        _blackList[staker] = lockType;
    }

    function unlockStaker(
        address staker
    ) external onlyRole(DEFAULT_ADMIN_ROLE) {
        require(
            _blackList[staker] != LockType.Unlocked,
            "StakingRewards: staker is not blacklisted"
        );
        if (_stakesSummary[staker] > 0) {
            totalStakers++;
        }
        _blackList[staker] = LockType.Unlocked;
    }

    /**
     * Return the current amount of rewards of a staker.
     */
    function earnableOf(
        address staker
    ) external view isPoolStarted returns (uint256) {
        return _calculateStakeRewards(staker) + _oldReward[staker];
    }

    /**
     * Return the current amount of stakes of the caller.
     */
    function stakeOf(
        address staker
    ) public view isPoolStarted returns (uint256) {
        return _stakesSummary[staker];
    }

    function blockProfitOf(
        address staker
    ) public view returns (uint8, uint256, uint256) {
        return (
            _stakerBlockIndex[staker],
            _blockProfits[_stakerBlockIndex[staker]].minStake,
            _blockProfits[_stakerBlockIndex[staker]].rewardPerMonth
        );
    }

    function claimedOf(
        address staker
    ) public view returns (uint256, uint256, uint256) {
        return (
            _claimed[staker].claimedBlockProfit,
            _claimed[staker].claimedReferral,
            _claimed[staker].claimedCompoundInterest
        );
    }

    function referralOf(
        address staker
    )
        public
        view
        returns (address, address[] memory, uint256[] memory, uint256, uint256)
    {
        uint256[] memory childrenStake = new uint256[](
            _referrals[staker].children.length
        );
        for (uint256 i = 0; i < _referrals[staker].children.length; i++) {
            childrenStake[i] = _stakesSummary[_referrals[staker].children[i]];
        }

        return (
            _referrals[staker].parent,
            _referrals[staker].children, // array children
            childrenStake,
            _referrals[staker].totalStakingChild, // total F1
            _referrals[staker].totalSystemStaking // total below F1
        );
    }

    function blacklistOf(address staker) public view returns (LockType) {
        return _blackList[staker];
    }

    function finishStakeTimeOf(address staker) public view returns (uint256) {
        return _lastStakeTime[staker] + DATE_LOCK;
    }
}
