contract AdminContract is Ownable {
    mapping(address => bool) public governanceContracts;

    event GovernanceContractAdded(address addr);
    event GovernanceContractRemoved(address addr);

    modifier onlyGovernance() {
        require(governanceContracts[msg.sender], "Isn't governance address");
        _;
    }

    function addAddress(address addr) public onlyOwner returns (bool success) {
        if (!governanceContracts[addr]) {
            governanceContracts[addr] = true;
            emit GovernanceContractAdded(addr);
            success = true;
        }
    }

    function removeAddress(address addr)
        public
        onlyOwner
        returns (bool success)
    {
        if (governanceContracts[addr]) {
            governanceContracts[addr] = false;
            emit GovernanceContractRemoved(addr);
            success = true;
        }
    }
}

abstract contract JunkToken is ERC20 {
    function mint(address _to, uint256 _amount)
    public
    virtual
    returns (bool)
    {}
}

contract DR_Farming is Ownable, IERC721Receiver  {
    using SafeMath for uint256;
    using SafeERC20 for IERC20;
    using EnumerableSet for EnumerableSet.UintSet;
    using EnumerableMap for EnumerableMap.UintToAddressMap;

    struct Pool {
        uint256 hashPower;
        uint256 lastBlock;
        uint256 accPerShare;
    }

    struct User {
        uint256 hashPower;
        uint256 rewards;
        uint256 loss;
        uint256 collected;
    }

    uint256 public startBlockNumber;
    uint256 public endBlockNumber;
    uint256 public tokensPerBlock;

    IERC721 public robots;
    JunkToken junk;

    Pool public pool;

    mapping(address => User) internal users;
    mapping(uint256 => uint256) public hashPower;


    // Mapping from holder address to their (enumerable) set of owned tokens
    mapping (address => EnumerableSet.UintSet) private _holderTokens;
    // Enumerable mapping from token ids to their owners
    EnumerableMap.UintToAddressMap private _tokenOwners;

    event Deposit(address indexed user, uint256 indexed tokenId);
    event Withdraw(address indexed user, uint256 tokenId);
    event Harvest(address indexed user, uint256 amount);

    constructor(
        JunkToken _junk,
        IERC721 _robots,
        uint256 _startBlockNumber,
        uint256 _endBlockNumber,
        uint256 _tokensPerBlock
    ) public {
        junk = _junk;
        robots = _robots;
        startBlockNumber = _startBlockNumber;
        endBlockNumber = _endBlockNumber;
        tokensPerBlock = _tokensPerBlock;
        pool.lastBlock = getLastBlockNumber();
    }

    function balanceOf(address owner) public view returns (uint256) {
        require(owner != address(0), "ERC721: balance query for the zero address");
        return _holderTokens[owner].length();
    }

    function ownerOf(uint256 tokenId) public view returns (address) {
        return _tokenOwners.get(tokenId, "ERC721: owner query for nonexistent token");
    }

    function tokenOfOwnerByIndex(address owner, uint256 index) public view returns (uint256) {
        return _holderTokens[owner].at(index);
    }

    function tokenByIndex(uint256 index) public view returns (uint256) {
        (uint256 tokenId, ) = _tokenOwners.at(index);
        return tokenId;
    }

    function deposit(uint256 _tokenId) public {
        require(robots.ownerOf(_tokenId) == msg.sender, "Wrong token id");
        User storage user = users[msg.sender];

        pool.accPerShare = getAccPerShare();
        pool.lastBlock = getLastBlockNumber();

        robots.safeTransferFrom(msg.sender, address(this), _tokenId);

        _holderTokens[msg.sender].add(_tokenId);
        _tokenOwners.set(_tokenId, msg.sender);

        makeHarvest();

        pool.hashPower = pool.hashPower.add(hashPower[_tokenId]);

        user.hashPower = user.hashPower.add(hashPower[_tokenId]);
        user.loss = user.hashPower.mul(pool.accPerShare).div(1e12);
        emit Deposit(msg.sender, _tokenId);
    }

    function withdraw(uint256 _tokenId) public {
        require(msg.sender == ownerOf(_tokenId), "Wrong token id");
        User storage user = users[msg.sender];
        require(balanceOf(msg.sender) > 0, "You don't have any tokens");
        pool.accPerShare = getAccPerShare();
        pool.lastBlock = getLastBlockNumber();

        robots.safeTransferFrom(address(this), msg.sender, _tokenId);
        _holderTokens[msg.sender].remove(_tokenId);
        _tokenOwners.remove(_tokenId);

        makeHarvest();
        pool.hashPower = pool.hashPower.sub(hashPower[_tokenId]);
        user.hashPower = user.hashPower.sub(hashPower[_tokenId]);
        if (pool.hashPower > 0 && user.rewards > user.collected) {
            // accPerShare += (rewards - collected) / balance
            pool.accPerShare = pool.accPerShare.add(
                user.rewards.sub(user.collected).mul(1e12).div(pool.hashPower)
            );
        }
        emit Withdraw(msg.sender, _tokenId);
    }

    function harvest() public {
        User storage user = users[msg.sender];
        require(makeHarvest() > 0, "You dont have any rewards");

        pool.accPerShare = getAccPerShare();
        pool.lastBlock = getLastBlockNumber();
        user.loss = user.hashPower.mul(pool.accPerShare).div(1e12);
    }

    function makeHarvest()
    internal
    returns (uint256) {
        User storage user = users[msg.sender];

        uint256 pending = getPending(msg.sender);
        if (!(pending > 0 || user.rewards > 0)) {
            return 0;
        }
        user.rewards = user.rewards.add(pending);

        uint256 reward = user.rewards;

        if (!(reward > 0) || reward < user.collected) {
            return 0;
        }
        reward = reward.sub(user.collected);
        mintToken(msg.sender, reward);
        user.collected = user.collected.add(reward);
        emit Harvest(msg.sender, reward);
        return reward;
    }

    function getLastBlockNumber() public view returns (uint256) {
        if (block.number < startBlockNumber) {
            return startBlockNumber;
        }
        if (block.number > endBlockNumber) {
            return endBlockNumber;
        }
        return block.number;
    }

    function getAccPerShare()
    public
    view
    returns (uint256) {
        uint256 blockNumber = getLastBlockNumber();
        if (blockNumber == startBlockNumber || !(pool.hashPower > 0)) {
            return 0;
        }
        // poolAccPershare + (blockNumber - poolLastBlock) * tokensPerBlock / poolBalance
        return
        pool.accPerShare.add(
            blockNumber
            .sub(pool.lastBlock)
            .mul(tokensPerBlock)
            .mul(1e12)
            .div(pool.hashPower)
        );
    }

    function getPending(address _user)
    internal
    view
    returns (uint256)
    {
        User storage user = users[_user];
        uint256 pending = user.hashPower.mul(getAccPerShare()).div(1e12);
        if (pending > user.loss) {
            return pending.sub(user.loss);
        }
        return 0;
    }

    function getReward(address _user)
    internal
    view
    returns (uint256)
    {
        User storage user = users[_user];
        uint256 pending = getPending(_user);
        if (!(pending > 0 || user.rewards > 0)) {
            return 0;
        }
        uint256 reward = user.rewards.add(pending);
        if (user.collected > reward) {
            return 0;
        }
        return reward.sub(user.collected);
    }

    function getUser(address _user)
    public
    view
    returns (
        uint256 userHashPower,
        uint256 robotsCount,
        uint256 claimable,
        uint256 pending,
        uint256 totalHashPower,
        uint256 lastBlock,
        uint256 tpb
    )
    {
        User storage user = users[_user];
        userHashPower = user.hashPower;
        robotsCount = balanceOf(_user);
        pending = user.rewards.add(getPending(_user)).sub(user.collected);
        claimable = getReward(_user);
        totalHashPower = pool.hashPower;
        lastBlock = block.number;
        tpb = tokensPerBlock;
    }

    function setEndBlock(uint256 _endBlock) public onlyOwner {
        endBlockNumber = _endBlock;
    }

    function setTokensPerBlock(uint256 _tokensPerBlock) public onlyOwner {
        massUpdate();
        tokensPerBlock = _tokensPerBlock;
    }

    function setHashPower(uint256 from, uint256[] memory _hashPower) public onlyOwner {
        require(from + _hashPower.length < 10101, "Wrong params length");
        for (uint256 i = 0; i < _hashPower.length; i++) {
            hashPower[from + i] = _hashPower[i];
        }
    }

    function massUpdate() public onlyOwner {
        pool.accPerShare = getAccPerShare();
        pool.lastBlock = getLastBlockNumber();
    }

    function mintToken(address _user, uint256 _amount) internal {
        junk.mint(_user, _amount);
    }

    function onERC721Received(address, address, uint256, bytes memory) public virtual override returns (bytes4) {
        return this.onERC721Received.selector;
    }
}
