pragma solidity ^0.8.0;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/math/SafeMath.sol";
import "@openzeppelin/contracts/utils/structs/EnumerableSet.sol";
import "@openzeppelin/contracts/security/ReentrancyGuard.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "./utils/TokenWithdrawableBridge.sol";
import "./interface/IWastedStaking.sol";
import "./interface/IWastedWarrior.sol";

contract WastedStaking is ReentrancyGuard, Ownable, TokenWithdrawableBridge {
    using SafeMath for uint256;
    using SafeERC20 for IERC20;

    event PoolCreated(
        uint256 poolId,
        uint256 typeId,
        uint256 minAmount,
        uint256 lockTimes,
        uint256 apr,
        uint256 time_start,
        uint256 time_end
    );

    event PoolUpdated(
        uint256 poolId,
        uint256 typeId,
        uint256 minAmount,
        uint256 lockTimes,
        uint256 apr,
        uint256 time_start,
        uint256 time_end
    );

    event Upgraded(
        uint256 currentPoolId,
        uint256 newPoolId,
        address staker,
        uint256 amount,
        uint256 claimTime
    );

    event Staked(
        uint256 poolId,
        address staker,
        uint256 amount,
        uint256 claimTime
    );
    event Unstake(uint256 poolId, address staker, uint256 amount);
    event Claimed(
        uint256 poolId,
        address staker,
        uint256 totalClaim,
        uint256 claimTime
    );

    struct Pool {
        uint256 typeId;
        uint256 minAmount;
        uint256 lockTimes;
        uint256 apr;
        uint256 time_start;
        uint256 time_end;
    }

    struct Staker {
        uint256 staked;
        uint256 claimTime;
        uint256 stakeTime;
    }

    Pool[] public _pools;
    mapping(uint256 => mapping(address => Staker)) public stakers; // poolId -> address staker -> staker info
    mapping(uint256 => uint256) public stakedOf;

    uint256[] public arrayHelper;
    IERC20 public acceptedToken =
        IERC20(0xd306c124282880858a634E7396383aE58d37c79c);
    uint256 constant PERCENT = 100;
    uint256 public forfeitInPercent = 0;
    mapping(address => bool) public isStaking;

    constructor() {}

    function setAcceptedToken(IERC20 _token) external onlyOwner {
        acceptedToken = _token;
    }

    function createPool(
        uint256 typeId,
        uint256 minAmount,
        uint256 lockTimes,
        uint256 apr,
        uint256 time_start,
        uint256 time_end
    ) external onlyOwner {
        require(lockTimes > 0 && apr > 0, "invalid pool info");
        _pools.push(
            Pool(typeId, minAmount, lockTimes, apr, time_start, time_end)
        );

        uint256 poolId = _pools.length - 1;

        emit PoolCreated(
            poolId,
            typeId,
            minAmount,
            lockTimes,
            apr,
            time_start,
            time_end
        );
    }

    function setForfeitInPercent(uint256 _forfeitInPercent) external onlyOwner {
        require(_forfeitInPercent < 100, "invalid");
        forfeitInPercent = _forfeitInPercent;
    }

    function updatePool(
        uint256 poolId,
        uint256 typeId,
        uint256 minAmount,
        uint256 lockTimes,
        uint256 apr,
        uint256 time_start,
        uint256 time_end
    ) external onlyOwner {
        require(lockTimes > 0 && apr > 0, "invalid pool info");

        Pool storage pool = _pools[poolId];
        pool.typeId = typeId;
        pool.minAmount = minAmount;
        pool.lockTimes = lockTimes;
        pool.apr = apr;
        pool.time_start = time_start;
        pool.time_end = time_end;

        emit PoolUpdated(
            poolId,
            typeId,
            minAmount,
            lockTimes,
            apr,
            time_start,
            time_end
        );
    }

    function upgrade(
        uint256 currentPoolId,
        uint256 newPoolId,
        uint256 amount
    ) external nonReentrant {
        Staker storage staker = stakers[currentPoolId][msg.sender];
        Pool memory pool = _pools[newPoolId];
        uint256 stakedAmount = staker.staked;
        uint256 totalCollect = amount.sub(stakedAmount);
        uint256 currentTime = block.timestamp;
        uint256 totalStakeTime = currentTime.sub(staker.stakeTime);
        uint256 claimTime = currentTime.add(pool.lockTimes.sub(totalStakeTime));

        require(stakedAmount > 0 && totalCollect > 0, "invalid");
        require(amount >= pool.minAmount, "not enough");
        require(staker.claimTime > block.timestamp, "claim instead");

        acceptedToken.safeTransferFrom(msg.sender, address(this), totalCollect);
        stakers[newPoolId][msg.sender] = Staker(amount, claimTime, currentTime);

        // remove staker from old pool
        staker.claimTime = 0;
        staker.staked = 0;
        staker.stakeTime = 0;

        emit Upgraded(currentPoolId, newPoolId, msg.sender, amount, claimTime);
    }

    function stake(uint256 poolId, uint256 amount) external nonReentrant {
        Pool memory pool = _pools[poolId];
        uint256 claimTime = block.timestamp.add(pool.lockTimes);

        require(
            block.timestamp >= pool.time_start &&
                block.timestamp <= pool.time_end.sub(pool.lockTimes),
            "pool not active"
        );
        require(!isStaking[msg.sender], "unstake first");
        require(amount >= pool.minAmount, "not enough");

        acceptedToken.safeTransferFrom(msg.sender, address(this), amount);

        stakers[poolId][msg.sender] = Staker(
            amount,
            claimTime,
            block.timestamp
        );

        stakedOf[poolId] = stakedOf[poolId].add(1);
        isStaking[msg.sender] = true;

        emit Staked(poolId, msg.sender, amount, claimTime);
    }

    function unstake(uint256 poolId) external {
        address caller = msg.sender;
        Staker storage staker = stakers[poolId][caller];
        uint256 amount = staker.staked;
        uint256 forfeit = amount.mul(forfeitInPercent).div(100);
        uint256 totalAmount = amount.sub(forfeit);

        require(staker.staked > 0, "not staked yet");
        require(staker.claimTime >= block.timestamp, "claim instead");

        acceptedToken.transfer(caller, totalAmount);

        staker.staked = 0;
        staker.claimTime = 0;
        staker.stakeTime = 0;
        isStaking[msg.sender] = false;

        emit Unstake(poolId, caller, totalAmount);
    }

    function claim(uint256 poolId) external {
        address caller = msg.sender;
        Staker storage staker = stakers[poolId][caller];
        Pool memory pool = _pools[poolId];
        uint256 actualLockTime = block.timestamp.sub(staker.stakeTime);
        uint256 totalClaim = _calculateTotalClaim(
            pool.apr,
            staker.staked,
            actualLockTime
        );
        require(staker.claimTime < block.timestamp, "not eligible");

        acceptedToken.transfer(caller, totalClaim);

        staker.staked = 0;
        staker.claimTime = 0;
        staker.stakeTime = 0;
        isStaking[msg.sender] = false;

        emit Claimed(poolId, caller, totalClaim, block.timestamp);
    }

    function _calculateTotalClaim(
        uint256 apr,
        uint256 amount,
        uint256 actualLockTime
    ) public pure returns (uint256) {
        uint256 profit = apr.mul(amount).mul(actualLockTime).div(
            31536000 * 100
        );
        uint256 totalClaim = amount.add(profit);
        return totalClaim;
    }
}
