pragma solidity ^0.8.0;

contract ClaimContract is Ownable{
    using SafeMath for uint256;

    address public stakingContract;
    IERC20 public stakingTrackerToken;
    IERC20 public rewardToken;
    uint256 public rewardAmount;
    uint256 public periodEndTime;

    mapping(address => uint256) public userTrackerBalances;
    mapping(address => uint256) public userTotalSupplies;

    constructor(
        address _stakingContract,
        address _owner,
        IERC20 _stakingTrackerToken,
        IERC20 _rewardToken,
        uint256 _rewardAmount,
        uint256 _periodEndTime,
        address[] memory _stakeholders,
        uint256[] memory _trackerBalances,
        uint256[] memory _totalSupplies
    ) {
        transferOwnership(_owner);
        stakingContract = _stakingContract;
        stakingTrackerToken = _stakingTrackerToken;
        rewardToken = _rewardToken;
        rewardAmount = _rewardAmount;
        periodEndTime = _periodEndTime;

        for (uint256 i = 0; i < _stakeholders.length; i++) {
            userTrackerBalances[_stakeholders[i]] = _trackerBalances[i];
            userTotalSupplies[_stakeholders[i]] = _totalSupplies[i];
        }
    }

    function claimReward(address user) external {
        require(msg.sender == stakingContract, "Only the staking contract can call this function");
        require(block.timestamp > periodEndTime, "Reward period not ended yet");

        uint256 userTrackerBalance = userTrackerBalances[user];
        uint256 periodTotalSupply = userTotalSupplies[user];

        require(userTrackerBalance > 0, "No tracker tokens to claim rewards");

        uint256 rewardShare = userTrackerBalance.mul(rewardAmount).div(periodTotalSupply);

        // Update userTrackerBalances[user] to zero after the user claims their rewards
        userTrackerBalances[user] = 0;

        require(rewardToken.transfer(user, rewardShare), "Reward transfer failed");
    }

    function rewardBalance(address user) public view returns (uint256) {
        uint256 userTrackerBalance = userTrackerBalances[user];
        uint256 periodTotalSupply = userTotalSupplies[user];

        if (userTrackerBalance == 0 || periodTotalSupply == 0) {
            return 0;
        }

        return userTrackerBalance.mul(rewardAmount).div(periodTotalSupply);
    }

    function recoverERC20(
        address tokenAddress,
        uint256 tokenAmount,
        address recipient
    ) external onlyOwner {
        IERC20(tokenAddress).transfer(recipient, tokenAmount); 
    }

    
}


contract AGTStakingContract is ERC20, Ownable, ReentrancyGuard {
    using SafeMath for uint256;

    uint256 public totalStaked;
    uint256 public totalRewardsPaid;
    IERC20 public agtToken;
    IERC20 public revenueToken;
    uint256 public stakingStartTime;
    uint256 public stakingEndTime;
    mapping(address => uint256) public stakedBalance;
    mapping(address => uint256) public stakedTokens;
    mapping(address => bool) public hasStaked;
    mapping(address => uint256) public unstakedTime;
    mapping(uint256 => address) public claimContracts;
    address[] public registeredStakeholders;
    uint256 public currentKey = 1;


    mapping(address => bool) public stakeholderRegistered;

    constructor(IERC20 _agtToken, IERC20 _revenueToken, string memory name, string memory symbol, uint256 startTime, uint256 endTime) ERC20 (name, symbol) {
        agtToken = _agtToken;
        revenueToken = _revenueToken;
        name = "AGT Staking Tracker";
        symbol = "AGTTrack";
        stakingStartTime = startTime;
        stakingEndTime = endTime;
    }

    function transfer(address /*_recipient*/, uint256 /*_amount*/) public pure override returns (bool) {
    // Tracker token transfers are not allowed.
    revert("Tracker token transfers are not allowed");
    }

    function transferFrom(address /*sender*/, address /*recipient*/, uint256 /*_amount*/) public pure override returns (bool) {
        // Tracker token transfers are not allowed.
        revert("Tracker token transfers are not allowed");
    }

    function depositRevenue(uint256 amountOfRevenue) external onlyOwner {
        address newContractAddress = createClaimContract(amountOfRevenue);
        totalRewardsPaid = totalRewardsPaid + amountOfRevenue;

        require(revenueToken.transfer(newContractAddress, amountOfRevenue), "Transfer Failed");
    }
    
    function createClaimContract(uint256 rewardAmount) internal returns (address) {
        uint256[] memory trackerBalances = new uint256[](registeredStakeholders.length);
        uint256[] memory totalSupplies = new uint256[](registeredStakeholders.length);

        uint256 periodTotalSupply = totalSupply();

        // Create a dynamic array to hold the list of stakeholders
        address[] memory stakeholdersList = new address[](registeredStakeholders.length);
        uint256 index = 0;

        // Iterate through the stakeholderRegistered mapping and populate the stakeholdersList array
        for (uint256 i = 0; i < registeredStakeholders.length; i++) {
            address user = registeredStakeholders[i];
            stakeholdersList[index] = user;
            index++;
        }

        for (uint256 i = 0; i < registeredStakeholders.length; i++) {
            address user = registeredStakeholders[i];
            trackerBalances[i] = balanceOf(user);
            totalSupplies[i] = periodTotalSupply;
        }

        ClaimContract claimContract = new ClaimContract(
        address(this),
        address(owner()),
        IERC20(address(this)),
        IERC20(address(revenueToken)),
        rewardAmount,
        stakingEndTime,
        stakeholdersList,
        trackerBalances,
        totalSupplies
        );

        claimContracts[currentKey] = address(claimContract);

        currentKey += 1;

        return address(claimContract);
    }

    function setRevenueToken(IERC20 _revenueToken) external onlyOwner {
        revenueToken = _revenueToken;
    }

    function setStakingPeriod(
        uint256 startTime,
        uint256 endTime,
        bool resetTracker
    ) public onlyOwner {
        require(endTime > startTime, "Invalid staking period");

        // Reset staking start and end times
        stakingStartTime = startTime;
        stakingEndTime = endTime;

        //Reset Tracker Token Balances
        if(resetTracker == true){
            resetTrackerTokens();
        }
    }

    function resetTrackerTokens() internal {

        // Calculate the staking period length in days
        uint256 stakingPeriodLength = (stakingEndTime - stakingStartTime) / 1 hours;

        for (uint256 i = 0; i < registeredStakeholders.length; i++) {
            address user = registeredStakeholders[i];
            uint256 currentStakedAmount = stakedBalance[user];

            // Check if the user has unstaked their tokens or not
            if (unstakedTime[user] < stakingEndTime && currentStakedAmount > 0) {
                // Burn the existing tracker tokens
                uint256 trackerTokensToBurn = balanceOf(user);
                _burn(user, trackerTokensToBurn);

                // Calculate the new tracker token amount based on the total staked amount and staking period
                uint256 newTrackerTokenAmount = currentStakedAmount * stakingPeriodLength;
                _mint(user, newTrackerTokenAmount);
            }
        }
    }

    function resetTrackerTokensManual(uint256 start, uint256 end) external onlyOwner {

        require(end >= start, "End must be greater than or equal to Start");

        // Calculate the staking period length in hours
        uint256 stakingPeriodLength = (stakingEndTime - stakingStartTime) / 1 hours;

        if (registeredStakeholders.length < end) {
            end = registeredStakeholders.length;
        }

        for (uint256 i = start; i < end; i++) {
            address user = registeredStakeholders[i];
            uint256 currentStakedAmount = stakedBalance[user];

            // Check if the user has unstaked their tokens or not
            if (unstakedTime[user] < stakingEndTime && currentStakedAmount > 0) {
                // Burn the existing tracker tokens
                uint256 trackerTokensToBurn = balanceOf(user);
                _burn(user, trackerTokensToBurn);

                // Calculate the new tracker token amount based on the total staked amount and staking period
                uint256 newTrackerTokenAmount = currentStakedAmount * stakingPeriodLength;
                _mint(user, newTrackerTokenAmount);
            }
        }
    }

    function stake(uint256 amount) external nonReentrant {        
        require(amount > 0, "Amount must be greater than zero");
        require(agtToken.balanceOf(msg.sender) >= amount, "Insufficient balance");

        if (!stakeholderRegistered[msg.sender]) {
            stakeholderRegistered[msg.sender] = true;
            registeredStakeholders.push(msg.sender);
        }

        uint256 trackingTotal = 0;
        if(block.timestamp <= stakingEndTime && block.timestamp >= stakingStartTime) {
            // Only calculate remaining staking period if the staking period has not ended
            uint256 remainingStakingPeriod = (stakingEndTime - block.timestamp) / 1 hours;
            trackingTotal = amount * remainingStakingPeriod;
        }

        if(block.timestamp <= stakingStartTime) {
            // Only calculate remaining staking period if the staking period has not ended
            uint256 remainingStakingPeriod = (stakingEndTime - stakingStartTime) / 1 hours;
            trackingTotal = amount * remainingStakingPeriod;
        }

        agtToken.transferFrom(msg.sender, address(this), amount);
        stakedBalance[msg.sender] = stakedBalance[msg.sender].add(amount);
        stakedTokens[msg.sender] = stakedTokens[msg.sender].add(amount);
        totalStaked = totalStaked.add(amount);
        hasStaked[msg.sender] = true;

        if(trackingTotal > 0) {
            // Only mint tracker tokens if the staking period has not ended
            _mint(msg.sender, trackingTotal);
        }

        emit Staked(msg.sender, amount);
    }

    function unstake() external nonReentrant {
        require(block.timestamp >= stakingEndTime, "Staking period has not ended yet");
        uint256 amount = stakedBalance[msg.sender];
        require(amount > 0, "No staked tokens to withdraw");
        require(agtToken.balanceOf(address(this)) >= amount, "Insufficient balance in staking contract");

        // Update the user's unstakedTime to the current block timestamp
        unstakedTime[msg.sender] = block.timestamp;

        agtToken.transfer(msg.sender, amount);
        stakedBalance[msg.sender] = 0;
        totalStaked = totalStaked.sub(amount);

        // Burn the tracker tokens
        uint256 trackerTokensToBurn = balanceOf(msg.sender);
        _burn(msg.sender, trackerTokensToBurn);

        emit Unstaked(msg.sender, amount);
    }

    

    function claimReward(uint256 key) external nonReentrant {
        require(claimContracts[key] != address(0), "No claim contract for this period");

        ClaimContract claimContract = ClaimContract(claimContracts[key]);
        claimContract.claimReward(msg.sender);
    }

    function checkUnstakeStatus() external view returns (bool) {
        if(block.timestamp >= stakingEndTime){
            return true;
        }
        else {
            return false;
        }
    }

    function getUnclaimedRewardsContracts(address user) external view returns (uint256[] memory) {
        uint256 count = 0;
        // Count the number of claim contracts with unclaimed rewards
        for (uint256 i = 0; i < currentKey; i++) {
            address claimContractAddress = claimContracts[i];
            if (claimContractAddress != address(0)) {
                ClaimContract claimContract = ClaimContract(claimContractAddress);
                if (claimContract.rewardBalance(user) > 0) {
                    count++;
                }
            }
        }

        // Create an array of claim contract keys with unclaimed rewards
        uint256[] memory unclaimedRewardsContracts = new uint256[](count);
        uint256 index = 0;
        for (uint256 i = 0; i < currentKey; i++) {
            address claimContractAddress = claimContracts[i];
            if (claimContractAddress != address(0)) {
                ClaimContract claimContract = ClaimContract(claimContractAddress);
                if (claimContract.rewardBalance(user) > 0) {
                    unclaimedRewardsContracts[index] = i;
                    index++;
                }
            }
        }

        return unclaimedRewardsContracts;
    }


    function endStakingPeriod() external onlyOwner {
        stakingEndTime = block.timestamp;
    }

    function getUserStakeDetails(address _user)
    external
    view
    returns (
        uint256 staked,
        uint256 rewardsAvailable,
        uint256 unstakeTime
    )
    {
        staked = stakedBalance[_user];
        rewardsAvailable = getRewardBalanceForAllPeriods(_user);
        unstakeTime = unstakedTime[_user];
    }

    function getRewardBalanceForAllPeriods(address user) internal view returns (uint256) {
        uint256 totalRewardBalance = 0;

        for (uint256 i = 0; i < currentKey; i++) {
            address claimContractAddress = claimContracts[i];
            if (claimContractAddress != address(0)) {
                ClaimContract claimContract = ClaimContract(claimContractAddress);
                totalRewardBalance = totalRewardBalance.add(claimContract.rewardBalance(user));
            }
        }

        return totalRewardBalance;
    }

    function getGlobalStakingDetails()
        external
        view
        returns (
            uint256 startTime,
            uint256 endTime,
            uint256 totalStakedAmount,
            uint256 totalPaidRewards
        )
    {
        startTime = stakingStartTime;
        endTime = stakingEndTime;
        totalStakedAmount = totalStaked;
        totalPaidRewards = totalRewardsPaid;
    }

    function recoverERC20(
        address tokenAddress,
        uint256 tokenAmount,
        address recipient
    ) external onlyOwner {
        //require(
        //    tokenAddress != address(agtToken),
        //    "Cannot withdraw the staking token"
        //);
        //
        IERC20(tokenAddress).transfer(recipient, tokenAmount);
        emit Recovered(tokenAddress, tokenAmount, recipient);
    }

    event RevenueUpdated(uint256 previousRevenue, uint256 newRevenue);
    event userRewardUpdated(
        address indexed user,
        uint256 stakedAmount,
        uint256 previousReward,
        uint256 newReward
    );
    event Staked(address indexed user, uint256 amount);
    event Unstaked(address indexed user, uint256 amount);
    event RewardsClaimed(address indexed user, uint256 reward);
 
    event Recovered(address token, uint256 amount, address recipient);
    event LogUnstake(address indexed account, uint256 amount);
}
