pragma solidity ^0.8.7;

import './BEP20.sol';
import './BEP20Burnable.sol';
import './BEP20Capped.sol';
import './BEP20Pausable.sol';
import './BEP20PausableAccounts.sol';
import './Roles.sol';

/**
 * @dev 'G1XToken' is a 'BEP20' token, that is capped, able to burn tokens and able to be paused.
 * It has a Owner and it has roles (Admin, Burner, Minter and Pauser) for specific proposes:
 * - The Owner can change the admin role of a role;
 * - An Admin can grant and revoke roles;
 * - A Burner can burn tokens;
 * - A Minter can create tokens;
 * - A Pauser can pause/unpause the contract.
 */
contract G1XToken is
    BEP20,
    BEP20Burnable,
    BEP20Capped,
    BEP20Pausable,
    BEP20PausableAccounts,
    Roles
{
    string private constant __name = 'G1X Token';
    string private constant __symbol = 'G1X';
    uint8 private constant __decimals = 18;
    uint256 private constant __cap = 5000000000 * (10**18); // 5.000.000.000 tokens = 5000000000 * (10**18)

    constructor() BEP20(__name, __symbol) BEP20Capped(__cap) {
        //
    }

    /**
     * Returns the number of decimals places used in the token representation.
     *
     * @dev See 'BEP20.decimals'
     *
     * @return uint8
     */
    function decimals()
        public
        view
        virtual
        override
        returns (uint8)
    {
        return __decimals;
    }

    /**
     * @dev Creates 'amount' tokens and assigns them to 'msg.sender', increasing
     * the total supply. Returns 'true' on success.
     *
     * See 'G1XToken._mint'.
     *
     * Requirements:
     * - 'msg.sender' must have the Minter role.
     *
     * @param amount - the number of tokens to assign
     *
     * @return bool
     */
    function mint(uint256 amount) public onlyMinter returns (bool) {
        _mint(_msgSender(), amount);
        return true;
    }

    /**
     * @dev Creates 'amount' tokens and assigns them to 'account', increasing
     * the total supply. Returns 'true' on success.
     *
     * See 'G1XToken._mint'.
     *
     * Requirements:
     * - 'msg.sender' must have the Minter role.
     *
     * @param account - the account that receives the tokens
     * @param amount - the number of tokens to assign
     *
     * @return bool
     */
    function mint(address account, uint256 amount) public onlyMinter returns (bool) {
        _mint(account, amount);
        return true;
    }

    /**
     * @dev Creates 'amount' tokens and assigns them to 'account', increasing
     * the total supply. Also it can put the 'account' in pause.
     * Returns 'true' on success.
     *
     * See 'G1XToken._mint'.
     *
     * Requirements:
     * - 'msg.sender' must have the Minter role.
     *
     * @param account - the account that receives the tokens
     * @param amount - the number of tokens to assign
     * @param shouldPauseAccount - bool to determine if the account should be put in pause
     *
     * @return bool
     */
    function mint(address account, uint256 amount, bool shouldPauseAccount) public onlyMinter returns (bool) {
        if (shouldPauseAccount) {
            _checkRole(PAUSER_ROLE, _msgSender());
        }

        _mint(account, amount);

        // @dev If 'account' is not paused and 'shouldPauseAccount' is true,
        // then the 'account' will be paused.
        if (shouldPauseAccount && ! isAccountPaused(account)) {
            pauseAccount(account);
        }

        return true;
    }

    /**
     * @dev Destroys 'amount' tokens of 'account'. Returns 'true' on success.
     *
     * See 'BEP20._burn'.
     *
     * Requirements:
     * - 'msg.sender' must have the Burner role.
     *
     * @param account - the account that receives the tokens
     * @param amount - the number of tokens to assign
     *
     * @return bool
     */
    function burn(address account, uint256 amount) public onlyBurner returns (bool) {
        _burn(account, amount);
        return true;
    }

    /**
     * @dev Destroys 'amount' tokens of 'account'.
     * Also it can put the 'account' in pause.
     * Returns 'true' on success.
     *
     * See 'BEP20._burn'.
     *
     * Requirements:
     * - 'msg.sender' must have the Burner role.
     *
     * @param account - the account that receives the tokens
     * @param amount - the number of tokens to assign
     * @param shouldPauseAccount - bool to determine if the account should be put in pause
     *
     * @return bool
     */
    function burn(address account, uint256 amount, bool shouldPauseAccount) public onlyBurner returns (bool) {
        if (shouldPauseAccount) {
            _checkRole(PAUSER_ROLE, _msgSender());
        }

        _burn(account, amount);

        // @dev If 'account' is not paused and 'shouldPauseAccount' is true,
        // then the 'account' will be paused.
        if (shouldPauseAccount && ! isAccountPaused(account)) {
            pauseAccount(account);
        }

        return true;
    }

    /**
     * @dev Pause the contract. Returns 'true' on success.
     *
     * See `BEP20Pausable._pause`.
     *
     * Requirements:
     * - 'msg.sender' must have the Pauser role.
     * - the contract must not be paused.
     *
     * @return bool
     */
    function pause() public whenNotPaused onlyPauser returns (bool) {
        _pause();
        return true;
    }

    /**
     * @dev Unpause the contract. Returns 'true' on success.
     *
     * See `BEP20Pausable._unpause`.
     *
     * Requirements:
     * - 'msg.sender' must have the Pauser role.
     * - the contract must be paused.
     *
     * @return bool
     */
    function unpause() public whenPaused onlyPauser returns (bool) {
        _unpause();
        return true;
    }

    /**
     * @dev Pause the account. Returns 'true' on success.
     *
     * See `BEP20PausableAccounts._pauseAccount`.
     *
     * Requirements:
     * - 'msg.sender' must have the Pauser role.
     * - the account must not be paused.
     *
     * @return bool
     */
    function pauseAccount(address account) public whenAccountNotPaused(account) onlyPauser returns (bool) {
        _pauseAccount(account);
        return true;
    }

    /**
     * @dev Unpause the account. Returns 'true' on success.
     *
     * See `BEP20PausableAccounts._unpauseAccount`.
     *
     * Requirements:
     * - 'msg.sender' must have the Pauser role.
     * - the account must be paused.
     *
     * @return bool
     */
    function unpauseAccount(address account) public whenAccountPaused(account) onlyPauser returns (bool) {
        _unpauseAccount(account);
        return true;
    }

    /**
     * @dev Sets `adminRole` as ``role``'s admin role.
     *
     * See `AccessControl._setRoleAdmin`
     *
     * Requirements:
     * - 'msg.sender' must be the Owner.
     */
    function setRoleAdmin(bytes32 role, bytes32 adminRole) external onlyOwner  {
        _setRoleAdmin(role, adminRole);
    }

    /**
     * @dev Override '_beforeTokenTransfer' because both 'BEP20' and 'BEP20Pausable'
     * declare the exact same function. Can only exist one source of truth.
     * Since we are using 'BEP20Pausable' we will defer to its '_beforeTokenTransfer' function.
     *
     * See 'BEP20Pausable._beforeTokenTransfer'.
     *
     * @param from - account from where the the tokens are transferred
     * @param to - account to where the tokens are transferred
     * @param value - the amount of tokens being transferred
     */
    function _beforeTokenTransfer(address from, address to, uint256 value)
        internal
        override (BEP20, BEP20Pausable, BEP20PausableAccounts)
    {
        BEP20._beforeTokenTransfer(from, to, value);

        require(! paused(), 'BEP20Pausable: token transfer while paused');

        require(
            ! isAccountPaused(_msgSender()),
            'BEP20PausableAccounts: token transfer while account paused'
        );
    }

    /**
     * @dev Override '_mint' because both 'BEP20' and 'BEP20Capped'
     * declare the exact same. Can only exist one source of truth.
     * Since we are using 'BEP20Capped' we will defer to its '_mint' function.
     *
     * See 'BEP20Capped._mint'.
     *
     * @param account - the account that receives the tokens
     * @param amount - the number of tokens to assign
     */
    function _mint(address account, uint256 amount)
        internal
        override (BEP20, BEP20Capped)
    {
        BEP20Capped._mint(account, amount);
    }
}"
