contract DecorCore is ERC721Pausable, AccessControl, Ownable, IDecorCore {

    /// @dev The Birth event is fired whenever a new Land comes into existence.
    event Birth(address owner, uint256 tokenId, uint256 decorID);
    event Burn(address owner, uint256 tokenId);

    bytes32 public constant PAUSED_ROLE = keccak256('PAUSED_ROLE');

    address public saleAuctionAddr;

    uint256 public nextTokenId = 1;

    mapping(address => bool) private permission;

    mapping(uint256 => uint256) public decorID;

    constructor(
        string memory name,
        string memory symbol
    ) public ERC721(name, symbol) {
        _setupRole(DEFAULT_ADMIN_ROLE, _msgSender());
        _setupRole(PAUSED_ROLE, _msgSender());
    }

    modifier onlyPermission() {
        require(permission[msg.sender], "NOT_THE_PERMISSION");
        _;
    }

    function mintDecorByPermission(address _owner, uint256 _decorID) override external onlyPermission {
        uint256 tokenId = nextTokenId;
        _mint(_owner, tokenId);
        decorID[tokenId] = _decorID;
        nextTokenId++;

        emit Birth(_owner, tokenId, _decorID); 
    }

    function createSaleAuction(
        uint256[] memory _tokenId,
        uint256 _startingPrice,
        uint256 _endingPrice,
        uint256 _duration
    )
    external
    whenNotPaused
    {
        for(uint i=0; i<_tokenId.length; i++){
            require(msg.sender == ownerOf(_tokenId[i]), "Not tokenId owner");

            _approve(saleAuctionAddr, _tokenId[i]);
        }

        IClockAuction(saleAuctionAddr).createAuction(
            _tokenId,
            _startingPrice,
            _endingPrice,
            _duration,
            msg.sender
        );
    }

    function burnDecorByPermission(address _owner,uint256 _tokenId) override external onlyPermission {
        require(_owner == ownerOf(_tokenId), "Not tokenId owner");

        _burn(_tokenId); // burn token

        emit Burn(_owner, _tokenId);
    }

    function burnDecor(uint256 _tokenId) external {
        require(msg.sender == ownerOf(_tokenId), "Not tokenId owner");

        _burn(_tokenId); // burn token
        emit Burn(msg.sender, _tokenId);
    }

    // @dev Sets the reference to the sale auction.
    /// @param _address - Address of sale contract.
    function setSaleAuctionAddress(address _address) external onlyOwner {
        saleAuctionAddr = _address;
    }

    function setPermission(address _permission, bool _enabled) external onlyOwner {
        permission[_permission] = _enabled;
    }

    function isPermission(address _permission) public view returns (bool) {
        return permission[_permission];
    }

    /// @notice Returns all the relevant information about a specific land.
    function getDecor(uint256 _tokenId) override public view returns (uint256) {
        return decorID[_tokenId];
    }

    function pause() public whenNotPaused {
        require(hasRole(PAUSED_ROLE, _msgSender()), 'Must have pause role');
        _pause();
    }

    function unpause() public whenPaused {
        require(hasRole(PAUSED_ROLE, _msgSender()), 'Must have pause role');
        _unpause();
    }
}
