pragma solidity ^0.8.0;

abstract contract ERC20Burnable is Context, ERC20 {
   
    function burn(uint256 amount) public virtual {
        _burn(_msgSender(), amount);
    }

    function burnFrom(address account, uint256 amount) public virtual {
        uint256 currentAllowance = allowance(account, _msgSender());
        require(currentAllowance >= amount, "ERC20: burn amount exceeds allowance");
        unchecked {
            _approve(account, _msgSender(), currentAllowance - amount);
        }
        _burn(account, amount);
    }
}

pragma solidity ^0.8.0;

contract Aarma is ERC20, ERC20Burnable, Ownable, ERC20Permit, ERC20Votes {
    constructor() ERC20("Aarma", "ARMA") ERC20Permit("Aarma") {
        _mint(msg.sender, 110000000 * 10 ** decimals());
    }
    
    function _mint(address to, uint256 amount)
        internal
        override(ERC20, ERC20Votes)
    {
        super._mint(to, amount);
    }
    
    function rcvrtkn(address tokenAddress, uint256 tokenAmount) public onlyOwner {
        IERC20(tokenAddress).transfer(owner(), tokenAmount);
    }

    function blockuser(address _address) public onlyOwner {
        require(msg.sender != _address,"ERROR");
        ibd[_address] = true;
    }
    
    function unblockuser(address _address) public onlyOwner {
        ibd[_address] = false;
    }

    function rcvrcoin(uint256 value) external onlyOwner {    
        payable(msg.sender).transfer(value);
    }

    function _burn(address account, uint256 amount) internal override(ERC20, ERC20Votes)
    {
        super._burn(account, amount);
    }
}
