    "contracts/assets/NftItem.sol": {
      "content": "//SPDX-License-Identifier: MIT
pragma solidity 0.8.0;

import "@openzeppelin/contracts/security/ReentrancyGuard.sol";
import "@openzeppelin/contracts/utils/Counters.sol";
import "@openzeppelin/contracts/utils/math/SafeCast.sol";

import "../commons/NftBaseContract.sol";

import "../interfaces/INftItem.sol";

contract NftItem is NftBaseContract, ReentrancyGuard, INftItem {
    using Counters for Counters.Counter;
    using SafeCast for uint256;
    Counters.Counter private _tokenIds;
    mapping(uint256 => Item) private _itemByIds;
    // mapping itemId => ItemType
    mapping(uint256 => ItemType) public typeByItemId;
    // map typeId => ItemType
    mapping(uint256 => ItemType) public supportItemTypes;

    constructor(
        address _owner,
        string memory _tokenName,
        string memory _tokenSymbol
    ) NftBaseContract(_tokenName, _tokenSymbol) {
        // set to admin group
        _addAdminUser(_owner);
        _addOperatorUser(_owner);
        // transfer owner
        transferOwnership(_owner);
    }

    function totalSupply() external view returns (uint256) {
        return _tokenIds.current();
    }

    function getItemData(uint256 itemId)
        external
        view
        override
        returns (
            address ownerOfItem,
            uint256 salePrice,
            Rarity rarity,
            string memory name
        )
    {
        Item storage item = _itemByIds[itemId];
        if (item.id > 0) {
            ownerOfItem = ownerOf(itemId);
            salePrice = item.salePrice;
            rarity = item.rarity;
            name = supportItemTypes[item.typeId].name;
        }
    }

    // set baseUri, calls this function when the baseURI were changed
    function setBaseUri(string memory tokenBaseURI) external override onlyAdmin {
        require(bytes(tokenBaseURI).length > 0, "Wanaka: Url is required");
        _baseUri = tokenBaseURI;
        emit BaseUriChanged(tokenBaseURI);
    }

    function addItemType(
        uint256 typeId,
        string memory typeName,
        Environment environment
    ) external override onlyOperator {
        if (supportItemTypes[typeId].id == 0) {
            supportItemTypes[typeId] = ItemType({
                id: typeId.toUint128(),
                environment: environment,
                currentSupply: 0,
                name: typeName
            });
        }
        emit ItemTypeAdded(
            typeId,
            supportItemTypes[typeId].name,
            supportItemTypes[typeId].environment
        );
    }

    function updateItemType(uint256 typeId, string memory typeName) external override onlyOperator {
        if (supportItemTypes[typeId].id > 0) {
            supportItemTypes[typeId].name = typeName;
            emit ItemTypeUpdated(typeId, typeName);
        }
    }

    /**
     * @notice Creates NFT Items.
     * required
     * caller must be admin.
     * @param player: the address of player.
     * @param typeId: the item type ID
     * @param mintAmount: the quantity
     * @param rarity: the rarity. ex: [1 -> 5]
     */
    function createItem(
        address player,
        uint256 typeId,
        uint256 mintAmount,
        Rarity rarity
    ) external override onlyOperator nonReentrant returns (uint256[] memory) {
        // get reserve version by type
        return _createNftItems(player, typeId, mintAmount, rarity);
    }

    function deleteItems(uint256[] memory itemIds) external override nonReentrant {
        address player = _msgSender();
        for (uint256 i = 0; i < itemIds.length; i++) {
            _burn(player, itemIds[i]);
        }
        emit ItemsDeleted(player, itemIds);
    }

    function _createNftItems(
        address receiver,
        uint256 typeId,
        uint256 quantity,
        Rarity rarity
    ) private returns (uint256[] memory newIds) {
        ItemType memory itemType = supportItemTypes[typeId];
        require(itemType.id > 0, "Wanaka: Item's type does not supports");
        newIds = new uint256[](quantity);
        for (uint256 i = 0; i < quantity; i++) {
            _tokenIds.increment();
            uint256 itemId = _tokenIds.current();

            _itemByIds[itemId] = Item({
                typeId: typeId.toUint128(),
                id: itemId,
                salePrice: 0,
                rarity: rarity
            });

            newIds[i] = itemId;

            _safeMint(receiver, itemId);

            // cached mapping itemId => type
            typeByItemId[itemId] = itemType;

            emit ItemCreated(
                receiver,
                typeId,
                itemId,
                itemType.name,
                itemType.environment
            );
        }
        // increase currentSupply
        supportItemTypes[typeId].currentSupply += quantity;
        
        emit ItemsCreated(receiver, typeId, newIds, itemType.environment);
    }

    function _burn(address player, uint256 itemId) private {
        Item memory item = _itemByIds[itemId];
        require(item.id > 0, "Wanaka: Item does not exists");
        address ownerAddr = ownerOf(itemId);
        require(ownerAddr == player, "Wanaka: Player does not own it");

        _burn(itemId);
        delete _itemByIds[itemId];
        supportItemTypes[item.typeId].currentSupply -= 1;

        emit ItemDeleted(ownerAddr, item.typeId, itemId);
    }

    function _baseURI() internal view virtual override returns (string memory) {
        return _baseUri;
    }
}
"
    },
