    "contracts/commons/NftBaseContract.sol": {
      "content": "//SPDX-License-Identifier: MIT

pragma solidity 0.8.0;

import "@openzeppelin/contracts/token/ERC721/ERC721.sol";
import "@openzeppelin/contracts/utils/introspection/ERC165Storage.sol";

import "../commons/PermissionRight.sol";
import "../commons/Withdrawable.sol";

contract NftBaseContract is ERC721, ERC165Storage, Withdrawable, PermissionRight {
    string internal _baseUri;
    uint256 public constant BPS = 10000;
    address payable public feeRecipient;
    uint256 public feeForSecondaryMarket;
    bytes4 private constant _INTERFACE_ID_FEES = 0xb7799584;

    event FeeRecipientChanged(address payable indexed recipient, uint256 feeBps);

    constructor(string memory name, string memory symbol) ERC721(name, symbol) {
        _registerInterface(_INTERFACE_ID_FEES);
    }

    /**
     * @dev See {IERC165-supportsInterface}.
     */
    function supportsInterface(bytes4 interfaceId)
        public
        view
        virtual
        override(ERC721, ERC165Storage)
        returns (bool)
    {
        return
            ERC165Storage.supportsInterface(interfaceId) || ERC721.supportsInterface(interfaceId);
    }

    function setFeeRecipients(address _feeRecipient, uint256 _feeBps) external onlyAdmin {
        require(_feeBps <= BPS, "Wanaka: _feeBps is over the maximum");
        feeRecipient = payable(_feeRecipient);
        feeForSecondaryMarket = _feeBps;
        emit FeeRecipientChanged(feeRecipient, feeForSecondaryMarket);
    }

    function getFeeRecipients(uint256 id) public view virtual returns (address payable[] memory) {
        address payable[] memory _feeRecipients = new address payable[](1);
        if (feeRecipient == address(0)) {
            _feeRecipients[0] = payable(owner());
        } else {
            _feeRecipients[0] = payable(feeRecipient);
        }
        return _feeRecipients;
    }

    function getFeeBps(uint256 id) public view virtual returns (uint256[] memory) {
        uint256[] memory _feeBps = new uint256[](1);
        _feeBps[0] = feeForSecondaryMarket;
        return _feeBps;
    }
}
"
    },
    "contracts/interfaces/INftItem.sol": {
      "content": "//SPDX-License-Identifier: MIT

pragma solidity 0.8.0;

interface INftItem {
    /// @title The Item's environment
    enum Environment {
        Basic,
        Lake,
        Forest,
        Sea
    }

    enum Rarity {
        COMMON,
        UNCOMMON,
        RARE,
        LEGENDARY,
        MYTHICAL,
        IMMORTAL
    }

    struct Item {
        uint128 typeId;
        Rarity rarity;
        uint256 id;
        uint256 salePrice;
    }

    struct ItemType {
        uint128 id;
        Environment environment;
        uint256 currentSupply;
        string name;
    }

    /**
     * @notice Sets base URl.
     *
     * Requirements:
     * - caller must be admin.
     */
    function setBaseUri(string memory tokenBaseURI) external;

    /**
     * @notice Adds supported item.
     *
     * Requirements:
     * - caller must be admin.
     * - typeId: the ID of item type. ex: 1
     * - typeName: the name of item type. ex: Pig
     */
    function addItemType(
        uint256 typeId,
        string memory typeName,
        Environment environment
    ) external;

    /**
     * @notice Updates supported item.
     *
     * Requirements:
     * - caller must be admin.
     * - typeId: the ID of item type. ex: 1
     * - typeName: the name of item type. ex: Pig
     */
    function updateItemType(uint256 typeId, string memory typeName) external;

    function getItemData(uint256 itemId)
        external
        view
        returns (
            address ownerOfItem,
            uint256 salePrice,
            Rarity rarity,
            string memory name
        );

    /**
     * @notice Mints items with rarity from game operator.
     * - caller must be admin.
     */
    function createItem(
        address player,
        uint256 typeId,
        uint256 mintAmount,
        Rarity rarity
    ) external returns (uint256[] memory);

    /**
     * @notice Burns items and returns true if it's completed.
     * required
     * caller must be owner of items
     * - itemIds: the list of item type ID. ex: [1, 2]
     */
    function deleteItems(uint256[] memory itemIds) external;

    event ItemTypeAdded(uint256 indexed typeId, string name, Environment environment);
    event ItemTypeUpdated(uint256 indexed typeId, string name);

    event ItemCreated(
        address indexed account,
        uint256 indexed typeId,
        uint256 indexed itemId,
        string name,
        Environment environment
    );
    event ItemsCreated(
        address indexed account,
        uint256 indexed typeId,
        uint256[] nftIds,
        Environment environment
    );
    event ItemDeleted(address indexed account, uint256 indexed typeId, uint256 indexed itemId);
    event ItemsDeleted(address indexed account, uint256[] itemIds);
    event BaseUriChanged(string tokenBaseURI);
}
"
    },
    "@openzeppelin/contracts/token/ERC721/ERC721.sol": {
      "content": "// SPDX-License-Identifier: MIT

pragma solidity ^0.8.0;

import "./IERC721.sol";
import "./IERC721Receiver.sol";
import "./extensions/IERC721Metadata.sol";
import "../../utils/Address.sol";
import "../../utils/Context.sol";
import "../../utils/Strings.sol";
import "../../utils/introspection/ERC165.sol";

/**
 * @dev Implementation of https://eips.ethereum.org/EIPS/eip-721[ERC721] Non-Fungible Token Standard, including
 * the Metadata extension, but not including the Enumerable extension, which is available separately as
 * {ERC721Enumerable}.
 */
contract ERC721 is Context, ERC165, IERC721, IERC721Metadata {
    using Address for address;
    using Strings for uint256;

    // Token name
    string private _name;

    // Token symbol
    string private _symbol;

    // Mapping from token ID to owner address
    mapping (uint256 => address) private _owners;

    // Mapping owner address to token count
    mapping (address => uint256) private _balances;

    // Mapping from token ID to approved address
    mapping (uint256 => address) private _tokenApprovals;

    // Mapping from owner to operator approvals
    mapping (address => mapping (address => bool)) private _operatorApprovals;

    /**
     * @dev Initializes the contract by setting a `name` and a `symbol` to the token collection.
     */
    constructor (string memory name_, string memory symbol_) {
        _name = name_;
        _symbol = symbol_;
    }

    /**
     * @dev See {IERC165-supportsInterface}.
     */
    function supportsInterface(bytes4 interfaceId) public view virtual override(ERC165, IERC165) returns (bool) {
        return interfaceId == type(IERC721).interfaceId
            || interfaceId == type(IERC721Metadata).interfaceId
            || super.supportsInterface(interfaceId);
    }

    /**
     * @dev See {IERC721-balanceOf}.
     */
    function balanceOf(address owner) public view virtual override returns (uint256) {
        require(owner != address(0), "ERC721: balance query for the zero address");
        return _balances[owner];
    }

    /**
     * @dev See {IERC721-ownerOf}.
     */
    function ownerOf(uint256 tokenId) public view virtual override returns (address) {
        address owner = _owners[tokenId];
        require(owner != address(0), "ERC721: owner query for nonexistent token");
        return owner;
    }

    /**
     * @dev See {IERC721Metadata-name}.
     */
    function name() public view virtual override returns (string memory) {
        return _name;
    }

    /**
     * @dev See {IERC721Metadata-symbol}.
     */
    function symbol() public view virtual override returns (string memory) {
        return _symbol;
    }

    /**
     * @dev See {IERC721Metadata-tokenURI}.
     */
    function tokenURI(uint256 tokenId) public view virtual override returns (string memory) {
        require(_exists(tokenId), "ERC721Metadata: URI query for nonexistent token");

        string memory baseURI = _baseURI();
        return bytes(baseURI).length > 0
            ? string(abi.encodePacked(baseURI, tokenId.toString()))
            : '';
    }

    /**
     * @dev Base URI for computing {tokenURI}. Empty by default, can be overriden
     * in child contracts.
     */
    function _baseURI() internal view virtual returns (string memory) {
        return "";
    }

    /**
     * @dev See {IERC721-approve}.
     */
    function approve(address to, uint256 tokenId) public virtual override {
        address owner = ERC721.ownerOf(tokenId);
        require(to != owner, "ERC721: approval to current owner");

        require(_msgSender() == owner || isApprovedForAll(owner, _msgSender()),
            "ERC721: approve caller is not owner nor approved for all"
        );

        _approve(to, tokenId);
    }

    /**
     * @dev See {IERC721-getApproved}.
     */
    function getApproved(uint256 tokenId) public view virtual override returns (address) {
        require(_exists(tokenId), "ERC721: approved query for nonexistent token");

        return _tokenApprovals[tokenId];
    }

    /**
     * @dev See {IERC721-setApprovalForAll}.
     */
    function setApprovalForAll(address operator, bool approved) public virtual override {
        require(operator != _msgSender(), "ERC721: approve to caller");

        _operatorApprovals[_msgSender()][operator] = approved;
        emit ApprovalForAll(_msgSender(), operator, approved);
    }

    /**
     * @dev See {IERC721-isApprovedForAll}.
     */
    function isApprovedForAll(address owner, address operator) public view virtual override returns (bool) {
        return _operatorApprovals[owner][operator];
    }

    /**
     * @dev See {IERC721-transferFrom}.
     */
    function transferFrom(address from, address to, uint256 tokenId) public virtual override {
        //solhint-disable-next-line max-line-length
        require(_isApprovedOrOwner(_msgSender(), tokenId), "ERC721: transfer caller is not owner nor approved");

        _transfer(from, to, tokenId);
    }

    /**
     * @dev See {IERC721-safeTransferFrom}.
     */
    function safeTransferFrom(address from, address to, uint256 tokenId) public virtual override {
        safeTransferFrom(from, to, tokenId, "");
    }

    /**
