pragma solidity 0.6.12;
pragma experimental ABIEncoderV2;

import "@openzeppelin/contracts-upgradeable/token/ERC20/SafeERC20Upgradeable.sol";
import "@openzeppelin/contracts/proxy/TransparentUpgradeableProxy.sol";

import "../interfaces/IVerifier.sol";
import "../interfaces/ILocker.sol";
import "../interfaces/IDealLockups.sol";
import "../interfaces/IVestingCreator.sol";
import "../interfaces/IDeal.sol";
import "../interfaces/IDealCreator.sol";

import "../tier-calculator/interfaces/ITierCalculator.sol";

import "../utils/AdminableUpgradeable.sol";

/**
 * @title Deal creator contract
 * @dev Allows owner to create another deals
 */
contract DealCreator is AdminableUpgradeable {

    using SafeERC20Upgradeable for IERC20Upgradeable;

    string internal constant DEAL_HASH = "__Deal_init(address,address,address,(address,uint256,address,uint256,uint256,uint256,uint256,uint256,bool),uint256,address)";

    IDealLockups public dealLockups;
    IVerifier public verifier;
    ILocker public locker;
    IVestingCreator public vestingCreator;
    ITierCalculator public tierCalculator;

    address public dealImpl;
    address public proxyAdmin;

    mapping(address => bool) public deals;

    event SetTierCalculator(address tierCalculator);
    event SetDealImpl(address dealImpl);
    event SetProxyAdmin(address proxyAdmin);

    event DealCreated(
        address dealAddress,
        address paymentToken,
        uint256 tokenPrice,
        address rewardToken,
        uint256 startTimestamp,
        uint256 finishTimestamp,
        uint256 startClaimTimestamp,
        uint256 minimumRaise,
        uint256 maxDistributedTokenAmount,
        bool allowRefund
    );

    modifier onlyDeals {
        require(deals[msg.sender], "Only deal can call");
        _;
    }

    function __DealCreator_init(
        IDealLockups _dealLockups,
        IVerifier _verifier,
        ILocker _locker,
        IVestingCreator _vestingCreator,
        ITierCalculator _tierCalculator,
        address _dealImpl,
        address _proxyAdmin
    ) public initializer {
        __Adminable_init();

        dealLockups = _dealLockups;
        verifier = _verifier;
        locker = _locker;
        vestingCreator = _vestingCreator;
        tierCalculator = _tierCalculator;
        dealImpl = _dealImpl;
        proxyAdmin = _proxyAdmin;
    }

    // with onlyOwnerOrAdmin permission and default decimalsRewardToken=18
    function createDeal(IDealCreator.DealInit memory _dealInit) external returns (address) {
        return createDeal(
            _dealInit,
            18
        );
    }

    function createDeal(IDealCreator.DealInit memory _dealInit, uint256 _decimalsRewardToken) public onlyOwnerOrAdmin returns (address) {
        require(dealImpl != address(0), "createDeal: dealImpl has not been set");

        bytes memory data = abi.encodeWithSignature(
            DEAL_HASH,
            address(verifier),
            address(locker),
            address(vestingCreator),
            _dealInit,
            _decimalsRewardToken,
            address(this)
        );
        TransparentUpgradeableProxy proxy = new TransparentUpgradeableProxy(dealImpl, proxyAdmin, data);

        if (address(_dealInit.rewardToken) != address(0)) {
            IERC20Upgradeable(_dealInit.rewardToken).safeTransferFrom(msg.sender, address(proxy), _dealInit.maxDistributedTokenAmount);
            require(IERC20Upgradeable(_dealInit.rewardToken).balanceOf(address(proxy)) == _dealInit.maxDistributedTokenAmount, "Unsupported token");
        }

        if (isAdmin[msg.sender]) {
            IDeal(address(proxy)).setAdminPermission(msg.sender, true);
        }
        IDeal(address(proxy)).transferOwnership(owner());

        deals[address(proxy)] = true;

        emit DealCreated(
            address(proxy),
            address(_dealInit.paymentToken),
            _dealInit.tokenPrice,
            address(_dealInit.rewardToken),
            _dealInit.startTimestamp,
            _dealInit.finishTimestamp,
            _dealInit.startClaimTimestamp,
            _dealInit.minimumRaise,
            _dealInit.maxDistributedTokenAmount,
            _dealInit.allowRefund
        );

        return address(proxy);
    }

    function userParticipate(address _user, uint256 _tierIndex) external onlyDeals {
        dealLockups.userParticipate(_user, msg.sender, _tierIndex);
    }

    function getTierIndex(address _user, address _deal) external view returns (bool, uint256, bool) {
        return tierCalculator.getTierIndex(_user, _deal);
    }

    function resetStart(address _user) external onlyDeals {
        tierCalculator.resetStart(_user);
    }

    function setTierCalculator(ITierCalculator _tierCalculator) external onlyOwner {
        tierCalculator = _tierCalculator;

        emit SetTierCalculator(address(_tierCalculator));
    }

    function setDealImpl(address _dealImpl) external onlyOwner {
        dealImpl = _dealImpl;

        emit SetDealImpl(_dealImpl);
    }

    function setProxyAdmin(address _proxyAdmin) external onlyOwner {
        proxyAdmin = _proxyAdmin;

        emit SetProxyAdmin(_proxyAdmin);
    }

    function setDealLockups(address _dealLockups) external onlyOwner {
        dealLockups = IDealLockups(_dealLockups);
    }

    // ============ Version Control ============
    function version() external pure returns (uint256) {
        return 102; // 1.0.2
    }
}
"
