/// @title Dividend-Paying Token Interface
/// @author Roger Wu (https://github.com/roger-wu)
/// @dev An interface for a dividend-paying token contract.
interface DividendPayingTokenInterface {
    /// @notice View the amount of dividend in wei that an address can withdraw.
    /// @param _owner The address of a token holder.
    /// @return The amount of dividend in wei that `_owner` can withdraw.
    function dividendOf(address _owner) external view returns (uint256);

    /// @notice Withdraws the ether distributed to the sender.
    /// @dev SHOULD transfer `dividendOf(msg.sender)` wei to `msg.sender`, and `dividendOf(msg.sender)` SHOULD be 0 after the transfer.
    ///  MUST emit a `DividendWithdrawn` event if the amount of ether transferred is greater than 0.
    function withdrawDividend() external;

    /// @dev This event MUST emit when ether is distributed to token holders.
    /// @param from The address which sends ether to this contract.
    /// @param weiAmount The amount of distributed ether in wei.
    event DividendsDistributed(address indexed from, uint256 weiAmount);

    /// @dev This event MUST emit when an address withdraws their dividend.
    /// @param to The address which withdraws ether from this contract.
    /// @param weiAmount The amount of withdrawn ether in wei.
    event DividendWithdrawn(address indexed to, uint256 weiAmount);
}

interface DividendPayingTokenOptionalInterface {
    /// @notice View the amount of dividend in wei that an address can withdraw.
    /// @param _owner The address of a token holder.
    /// @return The amount of dividend in wei that `_owner` can withdraw.
    function withdrawableDividendOf(address _owner)
        external
        view
        returns (uint256);

    /// @notice View the amount of dividend in wei that an address has withdrawn.
    /// @param _owner The address of a token holder.
    /// @return The amount of dividend in wei that `_owner` has withdrawn.
    function withdrawnDividendOf(address _owner)
        external
        view
        returns (uint256);

    /// @notice View the amount of dividend in wei that an address has earned in total.
    /// @dev accumulativeDividendOf(_owner) = withdrawableDividendOf(_owner) + withdrawnDividendOf(_owner)
    /// @param _owner The address of a token holder.
    /// @return The amount of dividend in wei that `_owner` has earned in total.
    function accumulativeDividendOf(address _owner)
        external
        view
        returns (uint256);
}

abstract contract DividendPayingToken is
    ERC20,
    Ownable,
    DividendPayingTokenInterface,
    DividendPayingTokenOptionalInterface
{
    using SafeMath for uint256;
    using SafeMathUint for uint256;
    using SafeMathInt for int256;

    uint256 internal constant magnitude = 2**128;

    uint256 internal magnifiedDividendPerShare;

    mapping(address => int256) internal magnifiedDividendCorrections;
    mapping(address => uint256) internal withdrawnDividends;

    uint256 public totalDividendsDistributed;

    constructor(string memory _name, string memory _symbol)
        ERC20(_name, _symbol)
    {}

    function _resetDistribuitedDividends() external {
        totalDividendsDistributed = 0;
    }

    function distributeDividends(uint256 amount) public onlyOwner {
        require(totalSupply() > 0);

        if (amount > 0) {
            magnifiedDividendPerShare = magnifiedDividendPerShare.add(
                (amount).mul(magnitude) / totalSupply()
            );
            emit DividendsDistributed(msg.sender, amount);

            totalDividendsDistributed = totalDividendsDistributed.add(amount);
        }
    }

    /// @notice Withdraws the ether distributed to the sender.
    /// @dev It emits a `DividendWithdrawn` event if the amount of withdrawn ether is greater than 0.
    function withdrawDividend(address tknaddress) public virtual {
        _withdrawDividendOfUser(payable(msg.sender), tknaddress);
    }

    /// @notice Withdraws the ether distributed to the sender.
    /// @dev It emits a `DividendWithdrawn` event if the amount of withdrawn ether is greater than 0.
    function _withdrawDividendOfUser(address payable user, address tknaddress)
        internal
        returns (uint256)
    {
        uint256 _withdrawableDividend = withdrawableDividendOf(user);
        if (_withdrawableDividend > 0) {
            withdrawnDividends[user] = withdrawnDividends[user].add(
                _withdrawableDividend
            );
            emit DividendWithdrawn(user, _withdrawableDividend);
            bool success = IERC20(tknaddress).transfer(
                user,
                _withdrawableDividend
            );

            if (!success) {
                withdrawnDividends[user] = withdrawnDividends[user].sub(
                    _withdrawableDividend
                );
                return 0;
            }

            return _withdrawableDividend;
        }

        return 0;
    }

    /// @notice View the amount of dividend in wei that an address can withdraw.
    /// @param _owner The address of a token holder.
    /// @return The amount of dividend in wei that `_owner` can withdraw.
    function dividendOf(address _owner) public view override returns (uint256) {
        return withdrawableDividendOf(_owner);
    }

    /// @notice View the amount of dividend in wei that an address can withdraw.
    /// @param _owner The address of a token holder.
    /// @return The amount of dividend in wei that `_owner` can withdraw.
    function withdrawableDividendOf(address _owner)
        public
        view
        override
        returns (uint256)
    {
        return accumulativeDividendOf(_owner).sub(withdrawnDividends[_owner]);
    }

    /// @notice View the amount of dividend in wei that an address has withdrawn.
    /// @param _owner The address of a token holder.
    /// @return The amount of dividend in wei that `_owner` has withdrawn.
    function withdrawnDividendOf(address _owner)
        public
        view
        override
        returns (uint256)
    {
        return withdrawnDividends[_owner];
    }

    /// @notice View the amount of dividend in wei that an address has earned in total.
    /// @dev accumulativeDividendOf(_owner) = withdrawableDividendOf(_owner) + withdrawnDividendOf(_owner)
    /// = (magnifiedDividendPerShare * balanceOf(_owner) + magnifiedDividendCorrections[_owner]) / magnitude
    /// @param _owner The address of a token holder.
    /// @return The amount of dividend in wei that `_owner` has earned in total.
    function accumulativeDividendOf(address _owner)
        public
        view
        override
        returns (uint256)
    {
        return
            magnifiedDividendPerShare
                .mul(balanceOf(_owner))
                .toInt256Safe()
                .add(magnifiedDividendCorrections[_owner])
                .toUint256Safe() / magnitude;
    }

    /// @dev Internal function that transfer tokens from one address to another.
    /// Update magnifiedDividendCorrections to keep dividends unchanged.
    /// @param from The address to transfer from.
    /// @param to The address to transfer to.
    /// @param value The amount to be transferred.
    function _transfer(
        address from,
        address to,
        uint256 value
    ) internal virtual override {
        require(false);

        int256 _magCorrection = magnifiedDividendPerShare
            .mul(value)
            .toInt256Safe();
        magnifiedDividendCorrections[from] = magnifiedDividendCorrections[from]
            .add(_magCorrection);
        magnifiedDividendCorrections[to] = magnifiedDividendCorrections[to].sub(
            _magCorrection
        );
    }

    /// @dev Internal function that mints tokens to an account.
    /// Update magnifiedDividendCorrections to keep dividends unchanged.
    /// @param account The account that will receive the created tokens.
    /// @param value The amount that will be created.
    function _mint(address account, uint256 value) internal override {
        super._mint(account, value);

        magnifiedDividendCorrections[account] = magnifiedDividendCorrections[
            account
        ].sub((magnifiedDividendPerShare.mul(value)).toInt256Safe());
    }

    /// @dev Internal function that burns an amount of the token of a given account.
    /// Update magnifiedDividendCorrections to keep dividends unchanged.
    /// @param account The account whose tokens will be burnt.
    /// @param value The amount that will be burnt.
    function _burn(address account, uint256 value) internal override {
        super._burn(account, value);

        magnifiedDividendCorrections[account] = magnifiedDividendCorrections[
            account
        ].add((magnifiedDividendPerShare.mul(value)).toInt256Safe());
    }

    function _setBalance(address account, uint256 newBalance) internal {
        uint256 currentBalance = balanceOf(account);

        if (newBalance > currentBalance) {
            uint256 mintAmount = newBalance.sub(currentBalance);
            _mint(account, mintAmount);
        } else if (newBalance < currentBalance) {
            uint256 burnAmount = currentBalance.sub(newBalance);
            _burn(account, burnAmount);
        }
    }
}

contract RQBERT is ERC20, Ownable {
    using SafeMath for uint256;

    IUniswapV2Router02 public uniswapV2Router;
    address public uniswapV2Pair;

    address public buybackRCUBE =
        address(0x10ED43C718714eb63d5aA57B78B54704E256024E);
    address public buybackTOKEN =
        address(0x10ED43C718714eb63d5aA57B78B54704E256024E);

    bool private swapping;

    RQBERT_RCUBE_Dividend_Tracker public rcubedividendTracker;
    RQBERT_TOKEN_Dividend_Tracker public tokendividendTracker;

    address internal zeroAddress =
        address(0x0000000000000000000000000000000000000000);
    address public deadWallet =
        address(0x000000000000000000000000000000000000dEaD);

    address public WRAPPED_NATIVE =
        address(0xbb4CdB9CBd36B01bD1cBaEBF2De08d9173bc095c); // WRAPPED_NATIVE WMATIC or WBNB
    address public RCUBE = address(0xa6e53f07bD410df069e20Ced725bdC9135146Fe9); //RCUBE
    address public TOKEN = address(0xF73d8276C15Ce56b2f4AeE5920e62F767A7f3aEA); //TOKEN - TCGCoin Initial Token

    uint256 public swapTokensAtAmount = 80 * (10**18);

    mapping(address => bool) public _isBlacklisted;

    uint256 public rebaseRCUBEFee = 66;
    uint256 public rebaseTOKENFee = 0;

    uint256 public RCUBERewardsFee = 4;
    uint256 public TOKENRewardsFee = 3;
    uint256 public liquidityFee = 2;
    uint256 public marketingFee = 2;
    uint256 public burnFee = 1;
    uint256 public distributedFees =
        RCUBERewardsFee.add(TOKENRewardsFee).add(marketingFee);
    uint256 public totalFees =
        RCUBERewardsFee
            .add(TOKENRewardsFee)
            .add(liquidityFee)
            .add(marketingFee)
            .add(burnFee);

    bool public swapEnabled = true;

    address public _marketingWallet =
        0x1F40C69eDfF22BFb99441641922778184dD07e69;

    // use by default 1,600,000 gas to process auto-claiming dividends
    uint256 public gasForProcessing = 1600000;

    // exlcude from fees and max transaction amount
    mapping(address => bool) private _isExcludedFromFees;

    // store addresses that a autoClaim market maker pairs. Any transfer *to* these addresses
    // could be subject to a maximum transfer amount
    mapping(address => bool) public automatedMarketMakerPairs;

    event UpdatercubeDividendTracker(
        address indexed newAddress,
        address indexed oldAddress
    );

    event UpdatetokenDividendTracker(
        address indexed newAddress,
        address indexed oldAddress
    );

    event UpdateUniswapV2Router(
        address indexed newAddress,
        address indexed oldAddress
    );

    event ExcludeFromFees(address indexed account, bool isExcluded);
    event ExcludeMultipleAccountsFromFees(address[] accounts, bool isExcluded);

    event UpdateToken(address indexed newAddress, address indexed oldAddress);

    event SetAutomatedMarketMakerPair(address indexed pair, bool indexed value);

    event LiquidityWalletUpdated(
        address indexed newLiquidityWallet,
        address indexed oldLiquidityWallet
    );

    event GasForProcessingUpdated(
        uint256 indexed newValue,
        uint256 indexed oldValue
    );

    event SwapAndLiquify(
        uint256 tokensSwapped,
        uint256 ethReceived,
        uint256 tokensIntoLiqudity
    );

    event SendDividends(uint256 tokensSwapped, uint256 amount);

    event ProcessedrcubeDividendTracker(
        uint256 iterations,
        uint256 claims,
        uint256 lastProcessedIndex,
        bool indexed autoClaim,
        uint256 gas,
        address indexed processor
    );

    event ProcessedtokenDividendTracker(
        uint256 iterations,
        uint256 claims,
        uint256 lastProcessedIndex,
        bool indexed autoClaim,
        uint256 gas,
        address indexed processor
    );

    constructor() ERC20("R-QBERT", "RQBERT") {
        rcubedividendTracker = new RQBERT_RCUBE_Dividend_Tracker();
        tokendividendTracker = new RQBERT_TOKEN_Dividend_Tracker();

        IUniswapV2Router02 _uniswapV2Router = IUniswapV2Router02(
            0x10ED43C718714eb63d5aA57B78B54704E256024E
        ); // PancakeSwap
        // Create a uniswap pair for this new token
        address _uniswapV2Pair = IUniswapV2Factory(_uniswapV2Router.factory())
            .createPair(address(this), WRAPPED_NATIVE);

        uniswapV2Router = _uniswapV2Router;
        uniswapV2Pair = _uniswapV2Pair;

        _setAutomatedMarketMakerPair(_uniswapV2Pair, true);

        // exclude from receiving dividends RCUBE
        rcubedividendTracker.excludeFromDividends(
            address(rcubedividendTracker)
        );
        rcubedividendTracker.excludeFromDividends(
            address(tokendividendTracker)
        );
        rcubedividendTracker.excludeFromDividends(address(this));
        rcubedividendTracker.excludeFromDividends(owner());
        rcubedividendTracker.excludeFromDividends(deadWallet);
        rcubedividendTracker.excludeFromDividends(zeroAddress);
        rcubedividendTracker.excludeFromDividends(address(_uniswapV2Router));

        // exclude from receiving dividends TOKEN
        tokendividendTracker.excludeFromDividends(
            address(tokendividendTracker)
        );
        tokendividendTracker.excludeFromDividends(
            address(rcubedividendTracker)
        );
        tokendividendTracker.excludeFromDividends(address(this));
        tokendividendTracker.excludeFromDividends(owner());
        tokendividendTracker.excludeFromDividends(deadWallet);
        tokendividendTracker.excludeFromDividends(zeroAddress);
        tokendividendTracker.excludeFromDividends(address(_uniswapV2Router));

        // exclude from paying fees or having max transaction amount
        excludeFromFees(owner(), true);
        if (owner() != _marketingWallet) {
            excludeFromFees(_marketingWallet, true);
        }
        excludeFromFees(address(this), true);
        excludeFromFees(deadWallet, true);

        /*
            _mint is an internal function in ERC20.sol that is only called here,
            and CANNOT be called ever again
        */
        _mint(owner(), 3610000 * (10**18));
    }

    receive() external payable {}

    function updatercubeDividendTracker(address newAddress) public onlyOwner {
        require(
            newAddress != address(rcubedividendTracker),
            "RQBERT: The dividend tracker already has that address"
        );

        RQBERT_RCUBE_Dividend_Tracker newrcubeDividendTracker = RQBERT_RCUBE_Dividend_Tracker(
                payable(newAddress)
            );

        require(
            newrcubeDividendTracker.owner() == address(this),
            "RQBERT: The new dividend tracker must be owned by the RQBERT token contract"
        );

        newrcubeDividendTracker.excludeFromDividends(
            address(newrcubeDividendTracker)
        );
        newrcubeDividendTracker.excludeFromDividends(address(this));
        newrcubeDividendTracker.excludeFromDividends(owner());
        newrcubeDividendTracker.excludeFromDividends(address(uniswapV2Router));

        emit UpdatercubeDividendTracker(
            newAddress,
            address(rcubedividendTracker)
        );

        rcubedividendTracker = newrcubeDividendTracker;
    }

    function mint(address _to, uint256 _amount) public onlyOwnerMint {
        _mint(_to, _amount);
    }

    function updatetokenDividendTracker(address newAddress) public onlyOwner {
        require(
            newAddress != address(tokendividendTracker),
            "RQBERT: The dividend tracker already has that address"
        );

        RQBERT_TOKEN_Dividend_Tracker newtokenDividendTracker = RQBERT_TOKEN_Dividend_Tracker(
                payable(newAddress)
            );

        require(
            newtokenDividendTracker.owner() == address(this),
            "RQBERT: The new dividend tracker must be owned by the RQBERT token contract"
        );

        newtokenDividendTracker.excludeFromDividends(
            address(newtokenDividendTracker)
        );
        newtokenDividendTracker.excludeFromDividends(address(this));
        newtokenDividendTracker.excludeFromDividends(owner());
        newtokenDividendTracker.excludeFromDividends(address(uniswapV2Router));

        emit UpdatetokenDividendTracker(
            newAddress,
            address(tokendividendTracker)
        );

        tokendividendTracker = newtokenDividendTracker;
    }

    function updateUniswapV2Router(address newAddress) public onlyOwner {
        address _uniswapV2Pair;
        require(
            newAddress != address(uniswapV2Router),
            "RQBERT: The router already has that address"
        );
        emit UpdateUniswapV2Router(newAddress, address(uniswapV2Router));
        uniswapV2Router = IUniswapV2Router02(newAddress);
        address _pairStatus = IUniswapV2Factory(uniswapV2Router.factory())
            .getPair(address(this), WRAPPED_NATIVE);
        _uniswapV2Pair = _pairStatus;
        if (_pairStatus == zeroAddress) {
            _uniswapV2Pair = IUniswapV2Factory(uniswapV2Router.factory())
                .createPair(address(this), WRAPPED_NATIVE);
        }
        uniswapV2Pair = _uniswapV2Pair;
    }

    function updateBuyBackRCUBERouter(address newAddress) public onlyOwner {
        require(
            newAddress != buybackRCUBE,
            "RCUBE: The router already has that address"
        );
        emit UpdateUniswapV2Router(newAddress, address(buybackRCUBE));
        buybackRCUBE = address(newAddress);
        excludeFromFees(newAddress, true);
    }

    function updateBuyBackTOKENRouter(address newAddress) public onlyOwner {
        require(
            newAddress != buybackTOKEN,
            "RCUBE: The router already has that address"
        );
        emit UpdateUniswapV2Router(newAddress, address(buybackTOKEN));
        buybackTOKEN = address(newAddress);
        excludeFromFees(newAddress, true);
    }

    function updateTOKENaddress(address newAddress, uint256 gas)
        public
        onlyOwner
    {
        require(
            newAddress != TOKEN,
            "RQBERT: The token address already has that address"
        );
        emit UpdateToken(newAddress, TOKEN);
        tokendividendTracker._resetDistribuitedDividends();
        tokendividendTracker.process(gas, TOKEN);
        tokendividendTracker.inCaseTokenReset(TOKEN);
        TOKEN = address(newAddress);
    }

    function excludeFromFees(address account, bool excluded) public onlyOwner {
        require(
            _isExcludedFromFees[account] != excluded,
            "RQBERT: Account is already the value of 'excluded'"
        );
        _isExcludedFromFees[account] = excluded;

        emit ExcludeFromFees(account, excluded);
    }

    function excludeMultipleAccountsFromFees(
        address[] memory accounts,
        bool excluded
    ) public onlyOwner {
        for (uint256 i = 0; i < accounts.length; i++) {
            _isExcludedFromFees[accounts[i]] = excluded;
        }

        emit ExcludeMultipleAccountsFromFees(accounts, excluded);
    }

    function setMarketingWallet(address payable wallet) external onlyOwner {
        _marketingWallet = wallet;
    }

    function setRCUBERewardsFee(uint256 value) external onlyOwner {
        RCUBERewardsFee = value;
        totalFees = RCUBERewardsFee
            .add(TOKENRewardsFee)
            .add(liquidityFee)
            .add(marketingFee)
            .add(burnFee);
        distributedFees = RCUBERewardsFee.add(TOKENRewardsFee);
    }

    function setTOKENRewardsFee(uint256 value) external onlyOwner {
