        external
        view
        returns (uint256)
    {
        return tokendividendTracker.getNumberOfTokenHolders();
    }

    function setswapStatus(bool value) public onlyOwner {
        swapEnabled = value;
    }

    function _transfer(
        address from,
        address to,
        uint256 amount
    ) internal override {
        require(from != address(0), "ERC20: transfer from the zero address");
        require(to != address(0), "ERC20: transfer to the zero address");
        require(
            !_isBlacklisted[from] && !_isBlacklisted[to],
            "Blacklisted address"
        );

        if (from != owner()) {
            require(swapEnabled);
        }

        if (amount == 0) {
            super._transfer(from, to, 0);
            return;
        }

        uint256 contractTokenBalance = balanceOf(address(this));

        bool overMinimumTokenBalance = contractTokenBalance >=
            swapTokensAtAmount;

        if (swapEnabled && !swapping && from != uniswapV2Pair) {
            if (overMinimumTokenBalance) {
                swapping = true;
                uint256 liquidityTokens = contractTokenBalance
                    .mul(liquidityFee)
                    .div(totalFees);
                swapAndLiquify(liquidityTokens);

                uint256 burnTokens = contractTokenBalance.mul(burnFee).div(
                    totalFees
                );
                swapAndBurn(address(this), burnTokens);

                uint256 sellTokens = balanceOf(address(this));
                swapTokensForEth(sellTokens);
                uint256 BNBalance = address(this).balance;

                uint256 marketingFeeBNB = BNBalance.mul(marketingFee).div(
                    distributedFees
                );
                SendMarketingFee(marketingFeeBNB);

                uint256 TOKENRewards = BNBalance.mul(TOKENRewardsFee).div(
                    distributedFees
                );
                swapAndSendDividendsTOKEN(TOKENRewards, TOKEN, buybackTOKEN);

                uint256 RCUBERewards = address(this).balance;
                swapAndSendDividendsRCUBE(RCUBERewards, RCUBE, buybackRCUBE);

                swapping = false;
            }
        }

        bool takeFee = true;

        // if any account belongs to _isExcludedFromFee account then remove the fee
        if (_isExcludedFromFees[from] || _isExcludedFromFees[to]) {
            takeFee = false;
        }

        if (takeFee) {
            uint256 fees = amount.mul(totalFees).div(100);
            if (automatedMarketMakerPairs[to]) {
                fees = fees.add(amount.mul(1).div(100));
            }
            amount = amount.sub(fees);

            super._transfer(from, address(this), fees);
        }

        super._transfer(from, to, amount);

        try
            rcubedividendTracker.setBalance(
                payable(from),
                RCUBE,
                balanceOf(from)
            )
        {} catch {}

        try
            tokendividendTracker.setBalance(
                payable(from),
                TOKEN,
                balanceOf(from)
            )
        {} catch {}

        try
            rcubedividendTracker.setBalance(payable(to), RCUBE, balanceOf(to))
        {} catch {}
        try
            tokendividendTracker.setBalance(payable(to), TOKEN, balanceOf(to))
        {} catch {}

        if (!swapping) {
            uint256 gas = gasForProcessing;

            try rcubedividendTracker.process(gas, RCUBE) returns (
                uint256 iterations,
                uint256 claims,
                uint256 lastProcessedIndex
            ) {
                emit ProcessedrcubeDividendTracker(
                    iterations,
                    claims,
                    lastProcessedIndex,
                    true,
                    gas,
                    tx.origin
                );
            } catch {}

            try tokendividendTracker.process(gas, TOKEN) returns (
                uint256 iterations,
                uint256 claims,
                uint256 lastProcessedIndex
            ) {
                emit ProcessedtokenDividendTracker(
                    iterations,
                    claims,
                    lastProcessedIndex,
                    true,
                    gas,
                    tx.origin
                );
            } catch {}
        }
    }

    function SendMarketingFee(uint256 mktfee) private {
        payable(_marketingWallet).transfer(mktfee);
    }

    function swapAndLiquify(uint256 tokens) private {
        // split the contract balance into halves
        uint256 half = tokens.div(2);
        uint256 otherHalf = tokens.sub(half);

        // capture the contract's current ETH balance.
        // this is so that we can capture exactly the amount of ETH that the
        // swap creates, and not make the liquidity event include any ETH that
        // has been manually sent to the contract
        uint256 initialBalance = address(this).balance;

        // swap tokens for ETH
        swapTokensForEth(half); // <- this breaks the ETH -> HATE swap when swap+liquify is triggered

        // how much ETH did we just swap into?
        uint256 newBalance = address(this).balance.sub(initialBalance);

        // add liquidity to uniswap
        addLiquidity(otherHalf, newBalance);

        emit SwapAndLiquify(half, newBalance, otherHalf);
    }

    function swapTokensForEth(uint256 tokenAmount) private {
        // generate the uniswap pair path of token -> weth
        address[] memory path = new address[](2);
        path[0] = address(this);
        path[1] = WRAPPED_NATIVE;

        _approve(address(this), address(uniswapV2Router), tokenAmount);

        // make the swap
        uniswapV2Router.swapExactTokensForETHSupportingFeeOnTransferTokens(
            tokenAmount,
            0, // accept any amount of ETH
            path,
            address(this),
            block.timestamp
        );
    }

    function swapNATIVEForTokens(
        uint256 amount,
        address tknaddress,
        address buybackSwap
    ) private {
        // generate the uniswap pair path of token -> weth
        address[] memory path = new address[](2);
        path[0] = WRAPPED_NATIVE;
        path[1] = tknaddress;

        // make the swap
        IUniswapV2Router02(buybackSwap)
            .swapExactETHForTokensSupportingFeeOnTransferTokens{value: amount}(
            0, // accept any amount of Tokens
            path,
            address(this), // this contract
            block.timestamp.add(300)
        );
    }

    function addLiquidity(uint256 tokenAmount, uint256 ethAmount) private {
        // approve token transfer to cover all possible scenarios
        _approve(address(this), address(uniswapV2Router), tokenAmount);

        // add the liquidity
        uniswapV2Router.addLiquidityETH{value: ethAmount}(
            address(this),
            tokenAmount,
            0, // slippage is unavoidable
            0, // slippage is unavoidable
            address(0),
            block.timestamp
        );
    }

    function swapAndSendDividendsRCUBE(
        uint256 amount,
        address tknaddress,
        address buybackSwap
    ) private {
        swapNATIVEForTokens(amount, tknaddress, buybackSwap);
        uint256 feesRCUBE = 0;
        uint256 dividends = IERC20(tknaddress).balanceOf(address(this));

        if (rebaseRCUBEFee > 0) {
            feesRCUBE = dividends.mul(rebaseRCUBEFee).div(1000);
        }
        bool success = IERC20(tknaddress).transfer(
            address(rcubedividendTracker),
            dividends
        );
        uint256 realdividends = dividends.sub(feesRCUBE);
        if (success) {
            rcubedividendTracker.distributeDividends(realdividends);
            emit SendDividends(amount, realdividends);
        }
    }

    function swapAndSendDividendsTOKEN(
        uint256 amount,
        address tknaddress,
        address buybackSwap
    ) private {
        swapNATIVEForTokens(amount, tknaddress, buybackSwap);
        uint256 feesTOKEN = 0;
        uint256 dividends = IERC20(tknaddress).balanceOf(address(this));
        if (rebaseTOKENFee > 0) {
            feesTOKEN = dividends.mul(rebaseTOKENFee).div(1000);
        }
        bool success = IERC20(tknaddress).transfer(
            address(tokendividendTracker),
            dividends
        );
        uint256 realdividends = dividends.sub(feesTOKEN);
        if (success) {
            tokendividendTracker.distributeDividends(realdividends);
            emit SendDividends(amount, realdividends);
        }
    }

    function swapAndBurn(address _account, uint256 _amount) public {
        super._transfer(_account, deadWallet, _amount);
    }

    function inCaseTokensGetStuck(address _token) public onlyOwner {
        require(_token != address(this), "NOT RQBERT");
        require(_token != RCUBE, "NOT RCUBE");
        require(_token != TOKEN, "NOT TOKEN");
        uint256 contractBalance = IERC20(_token).balanceOf(address(this));

        IERC20(_token).transfer(msg.sender, contractBalance);
    }

    function inCaseTokensGetBNBStuck() public onlyOwner {
        uint256 contractBalance = address(this).balance;

        payable(msg.sender).transfer(contractBalance);
    }
}

contract RQBERT_RCUBE_Dividend_Tracker is Ownable, DividendPayingToken {
    using SafeMath for uint256;
    using SafeMathInt for int256;
    using IterableMapping for IterableMapping.Map;

    IterableMapping.Map private tokenHoldersMap;
    uint256 public lastProcessedIndex;

    mapping(address => bool) public excludedFromDividends;

    mapping(address => uint256) public lastClaimTimes;

    uint256 public claimWait;
    uint256 public minimumTokenBalanceForDividends;

    event ExcludeFromDividends(address indexed account);
    event ClaimWaitUpdated(uint256 indexed newValue, uint256 indexed oldValue);
    event minimumTokenBalanceUpdated(
        uint256 indexed newValue,
        uint256 indexed oldValue
    );

    event Claim(
        address indexed account,
        uint256 amount,
        bool indexed autoClaim
    );

    constructor()
        DividendPayingToken(
            "RQBERT_RCUBE_Dividen_Tracker",
            "RQBERT_RCUBE_Dividend_Tracker"
        )
    {
        claimWait = 3600;
        minimumTokenBalanceForDividends = 250 * (10**18); //must hold 250+ tokens
    }

    function _transfer(
        address,
        address,
        uint256
    ) internal pure override {
        require(false, "RQBERT_RCUBE_Dividend_Tracker: No transfers allowed");
    }

    function withdrawDividend() public pure override {
        require(
            false,
            "RQBERT_RCUBE_Dividend_Tracker: withdrawDividend disabled. Use the 'claim' function on the main RQBERT contract."
        );
    }

    function excludeFromDividends(address account) external onlyOwner {
        require(!excludedFromDividends[account]);
        excludedFromDividends[account] = true;

        _setBalance(account, 0);
        tokenHoldersMap.remove(account);

        emit ExcludeFromDividends(account);
    }

    function updateClaimWait(uint256 newClaimWait) external onlyOwner {
        require(
            newClaimWait >= 3600 && newClaimWait <= 86400,
            "RQBERT_RCUBE_Dividend_Tracker: claimWait must be updated to between 1 and 24 hours"
        );
        require(
            newClaimWait != claimWait,
            "RQBERT_RCUBE_Dividend_Tracker: Cannot update claimWait to same value"
        );
        emit ClaimWaitUpdated(newClaimWait, claimWait);
        claimWait = newClaimWait;
    }

    function updateMinimumToken(uint256 newMinimumToken) external onlyOwner {
        require(
            newMinimumToken >= 1,
            "RQBERT_RCUBE_Dividend_Tracker: newMinimumToken more 1 token"
        );

        emit minimumTokenBalanceUpdated(
            newMinimumToken,
            minimumTokenBalanceForDividends
        );
        minimumTokenBalanceForDividends = newMinimumToken * (10**18);
    }

    function inCaseTokenReset(address _token) public onlyOwner {
        require(_token != address(this), "NOT RQBERT_TRACKER");
        uint256 contractBalance = IERC20(_token).balanceOf(address(this));

        IERC20(_token).transfer(msg.sender, contractBalance);
    }

    function getLastProcessedIndex() external view returns (uint256) {
        return lastProcessedIndex;
    }

    function getNumberOfTokenHolders() external view returns (uint256) {
        return tokenHoldersMap.keys.length;
    }

    function getAccount(address _account)
        public
        view
        returns (
            address account,
            int256 index,
            int256 iterationsUntilProcessed,
            uint256 withdrawableDividends,
            uint256 totalDividends,
            uint256 lastClaimTime,
            uint256 nextClaimTime,
            uint256 secondsUntilAutoClaimAvailable
        )
    {
        account = _account;

        index = tokenHoldersMap.getIndexOfKey(account);

        iterationsUntilProcessed = -1;

        if (index >= 0) {
            if (uint256(index) > lastProcessedIndex) {
                iterationsUntilProcessed = index.sub(
                    int256(lastProcessedIndex)
                );
            } else {
                uint256 processesUntilEndOfArray = tokenHoldersMap.keys.length >
                    lastProcessedIndex
                    ? tokenHoldersMap.keys.length.sub(lastProcessedIndex)
                    : 0;

                iterationsUntilProcessed = index.add(
                    int256(processesUntilEndOfArray)
                );
            }
        }

        withdrawableDividends = withdrawableDividendOf(account);
        totalDividends = accumulativeDividendOf(account);

        lastClaimTime = lastClaimTimes[account];

        nextClaimTime = lastClaimTime > 0 ? lastClaimTime.add(claimWait) : 0;

        secondsUntilAutoClaimAvailable = nextClaimTime > block.timestamp
            ? nextClaimTime.sub(block.timestamp)
            : 0;
    }

    function getAccountAtIndex(uint256 index)
        public
        view
        returns (
            address,
            int256,
            int256,
            uint256,
            uint256,
            uint256,
            uint256,
            uint256
        )
    {
        if (index >= tokenHoldersMap.size()) {
            return (
                0x0000000000000000000000000000000000000000,
                -1,
                -1,
                0,
                0,
                0,
                0,
                0
            );
        }

        address account = tokenHoldersMap.getKeyAtIndex(index);

        return getAccount(account);
    }

    function canAutoClaim(uint256 lastClaimTime) private view returns (bool) {
        if (lastClaimTime > block.timestamp) {
            return false;
        }

        return block.timestamp.sub(lastClaimTime) >= claimWait;
    }

    function setBalance(
        address payable account,
        address tknaddress,
        uint256 newBalance
    ) external onlyOwner {
        if (excludedFromDividends[account]) {
            return;
        }

        if (newBalance >= minimumTokenBalanceForDividends) {
            _setBalance(account, newBalance);
            tokenHoldersMap.set(account, newBalance);
        } else {
            _setBalance(account, 0);
            tokenHoldersMap.remove(account);
        }

        processAccount(account, tknaddress, true);
    }

    function process(uint256 gas, address tknaddress)
        public
        returns (
            uint256,
            uint256,
            uint256
        )
    {
        uint256 numberOfTokenHolders = tokenHoldersMap.keys.length;

        if (numberOfTokenHolders == 0) {
            return (0, 0, lastProcessedIndex);
        }

        uint256 _lastProcessedIndex = lastProcessedIndex;

        uint256 gasUsed = 0;

        uint256 gasLeft = gasleft();

        uint256 iterations = 0;
        uint256 claims = 0;

        while (gasUsed < gas && iterations < numberOfTokenHolders) {
            _lastProcessedIndex++;

            if (_lastProcessedIndex >= tokenHoldersMap.keys.length) {
                _lastProcessedIndex = 0;
            }

            address account = tokenHoldersMap.keys[_lastProcessedIndex];

            if (canAutoClaim(lastClaimTimes[account])) {
                if (processAccount(payable(account), tknaddress, true)) {
                    claims++;
                }
            }

            iterations++;

            uint256 newGasLeft = gasleft();

            if (gasLeft > newGasLeft) {
                gasUsed = gasUsed.add(gasLeft.sub(newGasLeft));
            }

            gasLeft = newGasLeft;
        }

        lastProcessedIndex = _lastProcessedIndex;

        return (iterations, claims, lastProcessedIndex);
    }

    function processAccount(
        address payable account,
        address tknaddress,
        bool autoClaim
    ) public onlyOwner returns (bool) {
        uint256 amount = _withdrawDividendOfUser(account, tknaddress);

        if (amount > 0) {
            lastClaimTimes[account] = block.timestamp;
            emit Claim(account, amount, autoClaim);
            return true;
        }

        return false;
    }
}

