pragma solidity ^0.8.0;

contract OpenOceanDistributor is Ownable {
    using SafeERC20 for IERC20;

    address public newToken;
    bool public distributionPaused;
    mapping(address => bool) private _hasRoles;
    mapping(uint256 => bool) private _nonce;

    event Ditribution(address user, uint256 amount, uint256 nonce);

    modifier checkdistributionPaused() {
        require(!distributionPaused, "distribution is paused");
        _;
    }

    constructor(address newTokenAddress, address signer) {
        newToken = newTokenAddress;
        grantRole(signer);
    }

    function distribute(
        uint256 nonce,
        uint256 amount,
        bytes32 _hash,
        bytes32 r,
        bytes32 s,
        uint8 v
    ) external checkdistributionPaused returns (uint256) {
        require(_nonce[nonce] == false, "Already distributed");
        bytes32 hash = keccak256(abi.encode(msg.sender, amount, nonce));
        require(hash == _hash, "Invalid hash");
        address signer = ECDSA.recover(hash, v, r, s);
        require(hasRole(signer), "Input signature error");
        _nonce[nonce] = true;

        require(IERC20(newToken).balanceOf(address(this)) >= amount, "Insufficient new token balance in distributor");
        require(IERC20(newToken).transfer(msg.sender, amount), "New token transfer failed");
        emit Ditribution(msg.sender, amount, nonce);
        return amount;
    }

    function hasRole(address account) public view returns (bool) {
        return _hasRoles[account];
    }

    function grantRole(address account) public onlyOwner {
        _grantRole(account, true);
    }

    function deleteRole(address account) public onlyOwner {
        _grantRole(account, false);
    }

    function setdistributionPaused(bool pause) public onlyOwner {
        distributionPaused = pause;
    }

    function rescueFunds(IERC20 token, uint256 amount) external onlyOwner {
        token.safeTransfer(msg.sender, amount);
    }

    function _grantRole(address account, bool grant) internal virtual {
        _hasRoles[account] = grant;
    }
}
