      "content": "// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/security/ReentrancyGuard.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";

contract StakingReward is ReentrancyGuard, Ownable {
    using SafeERC20 for IERC20;

    IERC20 public immutable stakingToken;
    IERC20 public immutable rewardsToken;

    string public name;
    uint256 public duration; // Duration of rewards to be paid out (in seconds)
    uint256 public finishAt; // Timestamp of when the rewards finish
    uint256 public updatedAt; // Minimum of last updated time and reward finish time
    uint256 public rewardRate; // Reward to be paid out per second
    uint256 public rewardPerTokenStored; // Sum of (reward rate * duration * 1e18 / total staked)
    uint256 public minAmount; // Minimum amount of staking
    uint256 public totalStaked; // Total Staked
    uint256 public unstakeCooldown; // Timestamp cooldown period to claim your unstake
    uint256 public lockPeriod; // Timestamp to lock your token
    uint256 public totalRewards; // Total of Rewards
    bool public IS_EMERGENCY;

    struct Withdraw {
        bool status;
        uint256 amount;
        uint256 lastUnstakeTimestamp;
    }
    mapping(address => Withdraw) private userWithdraw; // User Withdraw Info

    struct UserInStake {
        uint256 lastStake;
        uint256 userRewardPerTokenPaid;
        uint256 stakeBalance;
        uint256 userRewards;
    }
    mapping(address => UserInStake) public userInStake; // User Staking Info

    event Staked(address indexed from, uint256 amount, uint256 stakedTimestamp);
    event Unstaked(address indexed from, uint256 amount, uint256 unstakedTimestamp);
    event RewardPaid(address indexed to, uint256 reward, uint256 rewardPaidTimestamp);
    event RewardsDurationUpdated(uint256 newDuration);
    event ClaimWithdraw(address indexed to, uint256 amount, uint256 withdrawTimestamp);
    event StakeInfoUpdated(uint256 indexed timestamp, uint256 minAmount, uint256 unstakeCooldown, uint256 lockPeriod);
    event EmergencyToggle(uint256 indexed timestamp, bool status);
    event EmergencyWithdrawReward(uint256 indexed timestamp, uint256 amount);

    constructor(
        address poolOwner,
        string memory _name,
        address _stakingToken,
        address _rewardsToken,
        uint256 _minAmount,
        uint256 _unstakeCooldown,
        uint256 _lockPeriod
    ) {
        name = _name;
        stakingToken = IERC20(_stakingToken);
        rewardsToken = IERC20(_rewardsToken);
        minAmount = _minAmount;
        unstakeCooldown = _unstakeCooldown;
        lockPeriod = _lockPeriod;

        transferOwnership(poolOwner);
    }

    modifier updateReward(address _account) {
        rewardPerTokenStored = rewardPerToken();
        updatedAt = _lastTimeRewardApplicable();
        if (_account != address(0)) {
            userInStake[_account].userRewards = earned(_account);
            userInStake[_account].userRewardPerTokenPaid = rewardPerTokenStored;
        }
        _;
    }

    function stake(uint256 _amount)
        external
        nonReentrant
        updateReward(msg.sender)
    {
        require(_amount >= minAmount, "Please increase your staking value!");
        userInStake[msg.sender].lastStake = block.timestamp;
        userInStake[msg.sender].stakeBalance += _amount;
        totalStaked += _amount;
        stakingToken.safeTransferFrom(msg.sender, address(this), _amount);
        emit Staked(msg.sender, _amount, block.timestamp);
    }

    function requestToUnstake(uint256 _amount)
        public
        nonReentrant
        updateReward(msg.sender)
    {
        require(_amount > 0, "Amount = 0");
        require(userInStake[msg.sender].stakeBalance >= _amount, "Insufficient token amount");
        require(block.timestamp >= userInStake[msg.sender].lastStake + lockPeriod, "Token lock period is not over!");
        userInStake[msg.sender].stakeBalance -= _amount;
        totalStaked -= _amount;
        
        uint256 prevAmountWithdraw = userWithdraw[msg.sender].amount;
        uint256 totalWithdrawAmount = prevAmountWithdraw + _amount;

        userWithdraw[msg.sender] = Withdraw(true, totalWithdrawAmount, block.timestamp);

        emit Unstaked(msg.sender, _amount, block.timestamp);
    }

    function claimUnstake() external nonReentrant {
        require(userWithdraw[msg.sender].status, "Not eligible to claim withdraw!");
        require(
            block.timestamp >= userWithdraw[msg.sender].lastUnstakeTimestamp + unstakeCooldown,
            "Maturity date is not over!"
        );
        uint256 withdrawAmount = userWithdraw[msg.sender].amount;
        stakingToken.transfer(msg.sender, userWithdraw[msg.sender].amount);

        userWithdraw[msg.sender] = Withdraw(false, 0, 0);

        emit ClaimWithdraw(msg.sender, withdrawAmount, block.timestamp);
    }

    function exit() external {
        requestToUnstake(userInStake[msg.sender].stakeBalance);
        getReward();
    }

    function getReward() public nonReentrant updateReward(msg.sender) {
        uint256 reward = userInStake[msg.sender].userRewards;
        if (reward > 0) {
            userInStake[msg.sender].userRewards = 0;
            rewardsToken.transfer(msg.sender, reward);
            totalRewards -= reward;
            emit RewardPaid(msg.sender, reward, block.timestamp);
        } 
    }

    function emergencyWithdraw() external {
        require(IS_EMERGENCY, "Only emergency situation");
        uint256 balance = userInStake[msg.sender].stakeBalance;
        require(balance > 0, "Balance = 0");
        totalStaked -= balance;
        userInStake[msg.sender].stakeBalance -= balance;
        stakingToken.transfer(msg.sender, balance);
    }

    function getStakingBalance(address _account) external view returns(uint256){
        return userInStake[_account].stakeBalance;
    }

    function getStakePositionByUser(address _account) external view returns(UserInStake memory){
        return userInStake[_account];
    }

    function checkWithdrawInfo(address _address)
        external
        view
        returns (
            bool,
            uint256,
            uint256
        )
    {
        return (
            userWithdraw[_address].status,
            userWithdraw[_address].amount,
            userWithdraw[_address].lastUnstakeTimestamp + unstakeCooldown
        );
    }

    function rewardPerToken() public view returns (uint256) {
        if (totalStaked == 0) {
            return rewardPerTokenStored;
        }

        return
            rewardPerTokenStored +
            (rewardRate * (_lastTimeRewardApplicable() - updatedAt) * 1e18) /
            totalStaked;
    }

    function earned(address _account) public view returns (uint256) {
        return
            ((userInStake[_account].stakeBalance *
                (rewardPerToken() - userInStake[_account].userRewardPerTokenPaid)) / 1e18) +
            userInStake[_account].userRewards;
    }

    /* ========== RESTRICTED FUNCTIONS ONLY OWNER ========== */

    function setRewardsDuration(uint256 _duration) external onlyOwner {
        require(finishAt < block.timestamp, "Reward duration not finished yet");
        duration = _duration;
        emit RewardsDurationUpdated(_duration);
    }

    function notifyRewardAmount(uint256 _amount) external onlyOwner updateReward(address(0)) {
        // Init rewards
        rewardsToken.transferFrom(msg.sender, address(this), _amount);
        totalRewards += _amount;

        uint256 durationLeft;

        if (block.timestamp > finishAt) {
            rewardRate = _amount / duration;
            durationLeft = duration;
        } else {
            durationLeft = finishAt - block.timestamp;
            uint256 remainingRewards = rewardRate * durationLeft;
            
            rewardRate = (remainingRewards + _amount) / durationLeft;
        }

        require(rewardRate > 0, "reward rate = 0");
        require(
            rewardRate * durationLeft <= totalRewards,
            "Provided reward too high"
        );

        if(finishAt == 0 || finishAt < block.timestamp){
            finishAt = block.timestamp + duration;
        }
        updatedAt = block.timestamp;
    }

    function setStakeInfo(uint256 _minAmount,uint256 _unstakeCooldown, uint256 _lockPeriod) external onlyOwner {
        minAmount = _minAmount;
        unstakeCooldown = _unstakeCooldown;
        lockPeriod = _lockPeriod;
        emit StakeInfoUpdated(block.timestamp, _minAmount, _unstakeCooldown, _lockPeriod);
    }

    function emergencyToggle(bool _status) external onlyOwner {
        IS_EMERGENCY = _status;
        emit EmergencyToggle(block.timestamp, _status);
    }

    function emergencyWithdrawReward() external onlyOwner {
        require(IS_EMERGENCY, "Only emergency situation");
        rewardsToken.transfer(msg.sender, totalRewards);
        totalRewards -= totalRewards;
        emit EmergencyWithdrawReward(block.timestamp, totalRewards);
    }

    function _lastTimeRewardApplicable() private view returns (uint256) {
        return _min(block.timestamp, finishAt);
    }

    function _min(uint256 x, uint256 y) private pure returns (uint256) {
        return x <= y ? x : y;
    }
}
"
