// SPDX-License-Identifier: MIT
pragma solidity 0.8.16;

import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "@openzeppelin/contracts/utils/cryptography/ECDSA.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/security/Pausable.sol";
import "@openzeppelin/contracts/token/ERC1155/IERC1155.sol";
import "@openzeppelin/contracts/token/ERC721/IERC721.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/utils/structs/EnumerableSet.sol";
import "@openzeppelin/contracts/security/ReentrancyGuard.sol";

contract AssetsBridge is ReentrancyGuard, Pausable, Ownable {

    event NFT721Received(address operator, address from, uint256 erc721Id, bytes data);
    event NFT1155Received(address operator, address from, uint256 erc721Id, uint256 amount, bytes data);
    event NFT1155BatchReceived(address operator, address from, uint256[] tokenIds, uint256[] amounts, bytes data);

    event eUpdateSigner(
        address signer,
        uint256 blockNum
    );

    event eUpdateVault(
        address vault,
        uint256 blockNum
    );

    event eDeposit20(
        address erc20,
        uint256 amount,
        address owner, 
        uint256 timestamp
    );

    event eDeposit721(
        address   erc1155,
        uint256[] ids,
        address owner, 
        uint256 timestamp
    );

    event eDeposit1155(
        address   erc1155,
        uint256[] ids,
        uint256[] amounts,
        address   owner, 
        uint256   timestamp
    );

    event eWithdraw20( 
       address erc20, 
       uint256 amount, 
       address owner, 
       bytes32 signCode,
       uint256 timestamp
    );

    event eWithdraw721( 
       address erc721, 
       uint256[] ids, 
       address owner, 
       bytes32 signCode,
       uint256 timestamp
    );

    event eWithdraw1155( 
       address erc1155,
       uint256[] ids, 
       uint256[] amounts, 
       address owner, 
       bytes32 signCode,
       uint256 timestamp
    );

   struct Withdraw20Info {
        address erc20;  
        uint256 amount;   
        bytes32 signCode; 
        address to; 
    }

   struct Withdraw721Info {
        address erc721;  
        uint256 [] ids;   
        bytes32 signCode; 
        address to; 
    }

   struct Withdraw1155Info {
        address erc1155;  
        uint256 [] ids;   
        uint256 [] amounts;   
        bytes32 signCode; 
        address to; 
    }

    //type hash
    bytes32 public constant TYPE_HASH_20 = keccak256(
        "Withdraw20Info(address erc20,uint256 amount,bytes32 signCode,address to)"
    );
    bytes32 public constant TYPE_HASH_721 = keccak256(
        "Withdraw721Info(address erc721,uint256[] ids,bytes32 signCode,address to)"
    );
    bytes32 public constant TYPE_HASH_1155 = keccak256(
        "Withdraw1155Info(address erc1155,uint256[] ids,uint256[] amounts,bytes32 signCode,address to)"
    );

    struct EIP712Domain {
        string  name;
        string  version;
        uint256 chainId;
        address verifyingContract;
    }

    using ECDSA for bytes32;
    using EnumerableSet for EnumerableSet.Bytes32Set;

    using SafeERC20 for IERC20;
    using Address for address;

    bytes32 public immutable DOMAIN_SEPARATOR;
    bytes32 public constant EIP712DOMAIN_TYPEHASH = keccak256(
        "EIP712Domain(string name,string version,uint256 chainId,address verifyingContract)"
    );

    address public _SIGNER;
    EnumerableSet.Bytes32Set private _signCodes;

    address public _VAULT;
    uint256 public _MAX = 0xffffffffffffffffffffffffffffffffffffffffffffffffffffffffffffffff;

    constructor(address VAULT, address SIGNER ) {

        require(SIGNER != address(0x0), "SIGNER is zero address!");
        require(VAULT != address(0x0), "VAULT is zero address!");

        _VAULT = VAULT;
        _SIGNER = SIGNER;

        DOMAIN_SEPARATOR = keccak256(
            abi.encode(
                EIP712DOMAIN_TYPEHASH,
                keccak256("AssetsBridge"),
                keccak256("1"),
                block.chainid,
                address(this)
            )
        );
    }

    function setVault(address VAULT) public onlyOwner{

        require(VAULT != address(0x0), "VAULT is zero address!");
        _VAULT = VAULT;

        emit eUpdateVault(VAULT,block.number);
    }

    function updateSigner(address SIGNER) public onlyOwner{

        require(SIGNER != address(0x0), "SIGNER is zero address!");
        _SIGNER = SIGNER;

        emit eUpdateSigner(SIGNER,block.number);
    }

    function withdraw1155( Withdraw1155Info calldata transferInfo, bytes memory dataSignature) external whenNotPaused nonReentrant {

        require( !msg.sender.isContract(), "haloworld: call to non-contract" );
        require( !isExistSignCode(transferInfo.signCode),"invalid signCode!");
        require( verify1155(transferInfo, dataSignature), "this sign is not valid");
        require( transferInfo.ids.length == transferInfo.amounts.length, "invalid data!");

        _signCodes.add(transferInfo.signCode);

        IERC1155(transferInfo.erc1155).safeBatchTransferFrom(address(this), msg.sender, transferInfo.ids, transferInfo.amounts, "");

        emit eWithdraw1155(transferInfo.erc1155, transferInfo.ids, transferInfo.amounts, msg.sender, transferInfo.signCode, block.timestamp);

    }

    function withdraw721( Withdraw721Info calldata transferInfo, bytes memory dataSignature ) external whenNotPaused nonReentrant {

        require( !msg.sender.isContract(), "haloworld: call to non-contract" );
        require( !isExistSignCode(transferInfo.signCode),"invalid signCode!");
        require( verify721(transferInfo, dataSignature), "this sign is not valid");

        _signCodes.add(transferInfo.signCode);

        IERC721 erc721 = IERC721(transferInfo.erc721);
        for(uint256 i=0; i<transferInfo.ids.length; i++){
            erc721.safeTransferFrom(address(this), msg.sender, transferInfo.ids[i]);
        }

        emit eWithdraw721(transferInfo.erc721, transferInfo.ids, msg.sender, transferInfo.signCode, block.timestamp);

    }

    function withdraw20( Withdraw20Info calldata transferInfo, bytes memory dataSignature ) external whenNotPaused nonReentrant {

        require( !msg.sender.isContract(), "haloworld: call to non-contract" );
        require( !isExistSignCode(transferInfo.signCode),"invalid signCode!");
        require( verify20(transferInfo, dataSignature), "this sign is not valid");

        _signCodes.add(transferInfo.signCode);

        IERC20(address(transferInfo.erc20)).safeTransfer(msg.sender, transferInfo.amount);

        emit eWithdraw20(transferInfo.erc20, transferInfo.amount, msg.sender, transferInfo.signCode, block.timestamp);

    }

    function deposit20( address erc20, uint256 amount ) external whenNotPaused nonReentrant {

        require(  !msg.sender.isContract(), "haloworld: call to non-contract" );

        IERC20 iErc20 = IERC20(erc20);
        uint256 balanceBefore = iErc20.balanceOf(_VAULT);
        iErc20.safeTransferFrom(msg.sender, _VAULT, amount);
        uint256 balanceAfter = iErc20.balanceOf(_VAULT);

        emit eDeposit20(
                erc20,
                balanceAfter-balanceBefore,
                msg.sender,
                block.timestamp
            ); 
    } 

    function deposit721( address erc721, uint256[] calldata ids ) external whenNotPaused nonReentrant {

        require(  !msg.sender.isContract(), "haloworld: call to non-contract" );

        for(uint256 i=0; i<ids.length; i++){
            IERC721(erc721).safeTransferFrom(msg.sender, address(this), ids[i]);
        }

        emit eDeposit721(
                erc721,
                ids,
                msg.sender,
                block.timestamp
            ); 
    } 

    function deposit1155( address erc1155, uint256[] calldata ids, uint256[] calldata amounts ) external whenNotPaused nonReentrant {

        require(  !msg.sender.isContract(), "haloworld: call to non-contract" );

        IERC1155(erc1155).safeBatchTransferFrom(msg.sender, address(this), ids,amounts, "");

        emit eDeposit1155(
                erc1155,
                ids,
                amounts,
                msg.sender,
                block.timestamp
            ); 
    } 


    //check the state of a signCode
    function isExistSignCode(bytes32 signCode) view public returns(bool) {
        return _signCodes.contains(signCode);
    }

    //generate the transferInfo hash
    function hashtransferInfo20(Withdraw20Info calldata transferInfo) public pure returns (bytes32) {

        return keccak256(
            abi.encode(
                TYPE_HASH_20,
                transferInfo.erc20,
                transferInfo.amount,
                transferInfo.signCode,
                transferInfo.to)
        );
    }

    function hashtransferInfo721(Withdraw721Info calldata transferInfo) public pure returns (bytes32) {

        return keccak256(
            abi.encode(
                TYPE_HASH_721,
                transferInfo.erc721,
                keccak256(abi.encodePacked(transferInfo.ids)),
                transferInfo.signCode,
                transferInfo.to)
        );
    }

    function hashtransferInfo1155(Withdraw1155Info calldata transferInfo) public pure returns (bytes32) {

        return keccak256(
            abi.encode(
                TYPE_HASH_1155,
                transferInfo.erc1155,
                keccak256(abi.encodePacked(transferInfo.ids)),
                keccak256(abi.encodePacked(transferInfo.amounts)),
                transferInfo.signCode,
                transferInfo.to)
        );
    }


    function verifySignature(bytes32 hash, bytes memory  signature) public view returns (bool) {
        //hash must be a soliditySha3 with accounts.sign
        return hash.recover(signature) == _SIGNER;
    }

    function verify20( Withdraw20Info calldata transferInfo, bytes memory dataSignature ) public view returns (bool) {

        require(transferInfo.signCode != "","haloWorld: invalid sign code!");
        require(transferInfo.to == msg.sender,"haloWorld: invalid call data!");

        bytes32 digest = keccak256(abi.encodePacked(
            "\x19\x01",
            DOMAIN_SEPARATOR,
            hashtransferInfo20(transferInfo)
        ));
        require(verifySignature(digest,dataSignature)," invalid dataSignatures! ");
        return true;
    }

    function verify721( Withdraw721Info calldata transferInfo, bytes memory dataSignature ) public view returns (bool) {

        require(transferInfo.signCode != "","haloWorld: invalid sign code!");
        require(transferInfo.to == msg.sender,"haloWorld: invalid call data!");

        bytes32 digest = keccak256(abi.encodePacked(
            "\x19\x01",
            DOMAIN_SEPARATOR,
            hashtransferInfo721(transferInfo)
        ));
        require(verifySignature(digest,dataSignature),"haloWorld: invalid dataSignatures! ");
        return true;
    }

    function verify1155( Withdraw1155Info calldata transferInfo, bytes memory dataSignature ) public view returns (bool) {

        require(transferInfo.signCode != "","haloWorld: invalid sign code!");
        require(transferInfo.to == msg.sender,"haloWorld: invalid call data!");

        bytes32 digest = keccak256(abi.encodePacked(
            "\x19\x01",
            DOMAIN_SEPARATOR,
            hashtransferInfo1155(transferInfo)
        ));
        require(verifySignature(digest,dataSignature),"haloWorld: invalid dataSignatures! ");
        return true;
    }


    function onERC721Received(address operator, address from, uint256 erc721Id, bytes memory data) public returns (bytes4) {
        //only receive the _nft staff
        if(address(this) != operator) {
            //invalid from nft
            return 0;
        }
        //success
        emit NFT721Received(operator, from, erc721Id, data);
        return bytes4(keccak256("onERC721Received(address,address,uint256,bytes)"));
    }

    function onERC1155Received(address operator, address from, uint256 erc721Id, uint256 amount, bytes memory data) public returns (bytes4) {
        //only receive the _nft staff
        if(address(this) != operator) {
            //invalid from nft
            return 0;
        }
        //success
        emit NFT1155Received(operator, from, erc721Id, amount, data);
        return bytes4(keccak256("onERC1155Received(address,address,uint256,uint256,bytes)"));
    }

    function onERC1155BatchReceived(address operator, address from, uint256[] memory tokenIds, uint256[] memory amounts, bytes memory data) public returns (bytes4) {
        //only receive the _nft staff
        if(address(this) != operator) {
            //invalid from nft
            return 0;
        }
        //success
        emit NFT1155BatchReceived(operator, from, tokenIds, amounts, data);
        return bytes4(keccak256("onERC1155BatchReceived(address,address,uint256[],uint256[],bytes)"));
    }

    function withdrawETH(address target) public  onlyOwner {
        payable(target).transfer(address(this).balance);
    }

    function urgencyWithdrawErc20(address erc20, address target) public  onlyOwner {
        IERC20(erc20).safeTransfer(target, IERC20(erc20).balanceOf(address(this)));
    }

    function urgencyWithdrawErc721(address erc721, address target, uint256[] calldata ids) external onlyOwner {
        for (uint256 i = 0; i < ids.length; ++i) {
            (IERC721)(erc721).safeTransferFrom(address(this), target, ids[i],"");
        }
    }

    function urgencyWithdrawErc1155(address erc1155, address target, uint256[] calldata ids,  uint256[] calldata amounts) public onlyOwner {
        IERC1155(erc1155).safeBatchTransferFrom(address(this), target, ids,amounts,"");
    }


    function pause() public onlyOwner{
        if(!paused()){
            _pause();
        }
        else{
            _unpause();
        }
    }

}
