      "content": "// SPDX-License-Identifier: MIT
pragma solidity ^0.8.4;

import "./Market.sol";

error NftAddressDoesNotSupportERC721();

contract LNFTMarketplace is Market {
    using SafeMath for uint256;

    event ItemListed(
        address indexed owner,
        address indexed nft,
        uint256 tokenId,
        address paymentToken,
        uint256 pricePerItem,
        uint256 startingTime
    );

    event ItemSold(
        address indexed seller,
        address indexed buyer,
        address indexed nft,
        uint256 tokenId,
        address paymentToken,
        uint256 pricePerItem
    );

    event ItemPriceUpdated(
        address indexed owner,
        address indexed nft,
        uint256 tokenId,
        address paymentToken,
        uint256 newPrice
    );

    event ItemCanceled(
        address indexed owner,
        address indexed nft,
        uint256 tokenId
    );

    struct Listing {
        address paymentToken;
        uint256 pricePerItem;
        uint256 startingTime;
    }

    // Nft Address -> TokenID -> Owner -> Listing item
    mapping(address => mapping(uint256 => mapping(address => Listing)))
        public listings;

    constructor() {}

    function _ownerIsValid(
        address _nftAddress,
        uint256 _tokenId,
        address _owner
    ) internal view {
        if (IERC165(_nftAddress).supportsInterface(INTERFACE_ID_ERC721)) {
            IERC721 nft = IERC721(_nftAddress);
            require(
                nft.ownerOf(_tokenId) == _owner,
                "ownerOf this tokenId is not the real owner"
            );
        } else {
            revert NftAddressDoesNotSupportERC721();
        }
    }

    function _paymentTokenIsValid(address _paymentToken) internal view {
        require(
            _paymentToken == address(0) || _isTokenPayment(_paymentToken),
            "must be Accepted Tokens"
        );
    }

    function listItem(
        address _nftAddress,
        uint256 _tokenId,
        address _paymentToken,
        uint256 _pricePerItem,
        uint256 _startingTime
    ) external payable {
        Listing memory listing = listings[_nftAddress][_tokenId][_msgSender()];
        require(listing.startingTime == 0, "listing.startingTime is Zero");
        if (IERC165(_nftAddress).supportsInterface(INTERFACE_ID_ERC721)) {
            IERC721 nft = IERC721(_nftAddress);
            require(
                nft.ownerOf(_tokenId) == _msgSender(),
                "ownerOf this tokenId is not the msgSender"
            );
            require(
                nft.isApprovedForAll(_msgSender(), address(this)),
                "address(this) have to Approve"
            );
        } else {
            revert NftAddressDoesNotSupportERC721();
        }

        _paymentTokenIsValid(_paymentToken);


        listings[_nftAddress][_tokenId][_msgSender()] = Listing(
            _paymentToken,
            _pricePerItem,
            _startingTime
        );
        emit ItemListed(
            _msgSender(),
            _nftAddress,
            _tokenId,
            _paymentToken,
            _pricePerItem,
            _startingTime
        );
    }

    function cancelListing(address _nftAddress, uint256 _tokenId)
        external
        nonReentrant
    {
        Listing storage listedItem = listings[_nftAddress][_tokenId][
            _msgSender()
        ];
        require(listedItem.startingTime != 0, "startingTime is Zero");
        _ownerIsValid(_nftAddress, _tokenId, _msgSender());

        delete (listings[_nftAddress][_tokenId][_msgSender()]);
        emit ItemCanceled(_msgSender(), _nftAddress, _tokenId);
    }

    function buyItem(
        address _nftAddress,
        uint256 _tokenId,
        address _paymentToken,
        uint256 _pricePerItem,
        address _owner
    ) external payable nonReentrant {
        Listing storage listedItem = listings[_nftAddress][_tokenId][_owner];
        _ownerIsValid(_nftAddress, _tokenId, _owner);

        require(
            (_getNow() >= listedItem.startingTime) &&
                listedItem.startingTime != 0,
            "startingTime is Zero or you come soon"
        );

        require(
            _paymentToken == listedItem.paymentToken,
            "_paymentToken != listedItem.paymentToken "
        );

        uint256 feeAmount = _calculateFee(
            _paymentToken,
            listedItem.pricePerItem
        );

        if (feeAmount != 0) {
            require(
                _pricePerItem >= listedItem.pricePerItem,
                "your expected price is lower than seller price"
            );
            if (_isTokenPayment(_paymentToken)) {
                require(
                    ERC20(_paymentToken).transferFrom(
                        _msgSender(),
                        owner(),
                        feeAmount
                    )
                );
            } else {
                require(
                    msg.value >= listedItem.pricePerItem.add(feeAmount),
                    "msg.value is lower than expected amount"
                );
                (bool success, ) = owner().call{value: feeAmount}("");
                require(success, "Ether Transfer Failed");
            }
        }

        uint256 royaltyFee = _payMinter(
            _nftAddress,
            _paymentToken,
            _tokenId,
            listedItem.pricePerItem
        );

        feeAmount = feeAmount.add(royaltyFee);

        if (
            _paymentToken == LUST ||
            _paymentToken == LWAP ||
            _isTokenPayment(_paymentToken)
        ) {
            require(
                ERC20(_paymentToken).transferFrom(
                    _msgSender(),
                    _owner,
                    listedItem.pricePerItem.sub(feeAmount)
                )
            );
        } else {
            (bool ownerTransferSuccess, ) = _owner.call{
                value: listedItem.pricePerItem.sub(feeAmount)
            }("");
            require(ownerTransferSuccess, "ownerTransfer failed");
        }

        if (IERC165(_nftAddress).supportsInterface(INTERFACE_ID_ERC721)) {
            IERC721(_nftAddress).safeTransferFrom(
                _owner,
                _msgSender(),
                _tokenId
            );
        } else {
            revert NftAddressDoesNotSupportERC721();
        }

        emit ItemSold(
            _owner,
            _msgSender(),
            _nftAddress,
            _tokenId,
            _paymentToken,
            listedItem.pricePerItem
        );
        delete (listings[_nftAddress][_tokenId][_owner]);
    }

    function _calculateFee(address _token, uint256 _price)
        private
        view
        returns (uint256)
    {
        if (_token == LUST || _token == LWAP) {
            return 0;
        } else {
            return _price.mul(platformFee).div(100);
        }
    }

    function _payMinter(
        address _nft,
        address _payToken,
        uint256 _tokenId,
        uint256 _price
    ) private returns (uint256) {
        address payable minter = payable(getTokenMinter(_nft, _tokenId));
        uint256 royal = _getTokenRoyalty(_nft, _tokenId);
        if (minter != address(0) && royal != 0) {
            uint256 royaltyFee = _price.mul(royal).div(100);
            if (_payToken == LUST || _payToken == LWAP) {
                require(
                    ERC20(_payToken).transferFrom(
                        _msgSender(),
                        minter,
                        royaltyFee
                    ),
                    "_payMinter Token Transfer Failed"
                );
            } else {
                (bool success, ) = minter.call{value: royaltyFee}("");
                require(success, "_payMinter Ether Transfer Failed");
            }
            return royaltyFee;
        } else {
            return 0;
        }
    }
}
"
