interface IReceiver {
    function burn(uint256 amount) external returns (bool);

    function addReward(uint256 amount) external returns (bool);
}

interface IBurnable {
    function burn(uint256 amount) external returns (bool);
}

contract PaymentGate is Ownable {
    using Address for address;
    using SafeERC20 for IERC20;

    event Payment(address from, address token, uint amount);

    mapping(address => bool) private _tokenAllowed;

    mapping(address => address) private _referrals;

    mapping(address => address[]) private _referredUsers;

    mapping(address => uint) private _earnedAmountBNB;

    mapping(address => mapping(address => uint)) private _earnedAmountToken;

    address private _receiver;

    address private _burnable;

    address public coinContract;

    uint256[2] private _refbonus = [1000, 500];

    bool public locked;

    constructor(address cOwner) Ownable(cOwner) {}

    function setCoinContract(address account) public onlyOwner {
        require(
            (account != address(0) && account.isContract()),
            "Invalid coin contract address"
        );
        coinContract = account;
    }

    function changeLock() external onlyOwner {
        locked = !locked;
    }

    function getReceiverContract() public view returns (address) {
        return _receiver;
    }

    function setReceiverContract(address account) external onlyOwner {
        require(account != address(0), "Invalid receiver address");
        _receiver = account;
    }

    function getBurnableToken() public view returns (address) {
        return _burnable;
    }

    function setBurnableToken(address account) external onlyOwner {
        require(account != address(0), "Invalid receiver address");
        _burnable = account;
    }

    function getTokenAllowance(address _token) public view returns (bool) {
        return _tokenAllowed[_token];
    }

    function toggleTokenAllowance(address _token) external onlyOwner {
        _tokenAllowed[_token] = !_tokenAllowed[_token];
    }

    function getReferralBonuses() public view returns (uint256[2] memory) {
        uint256[2] memory bonuses;
        bonuses[0] = _refbonus[0];
        bonuses[1] = _refbonus[1];
        return bonuses;
    }

    function setReferralBonuses(
        uint256[2] calldata _newbonus
    ) external onlyOwner {
        require(
            _newbonus[0] + _newbonus[1] <= 10000,
            "Sum of bonuses cannot exceed 100%"
        );
        _refbonus = _newbonus;
    }

    function getReferrals(
        address _account
    ) public view returns (address[2] memory) {
        address[2] memory refs;
        refs[0] = _referrals[_account];
        refs[1] = _referrals[_referrals[_account]];
        return refs;
    }

    function getReferralsNumbers(
        address _account
    ) public view returns (uint[2] memory) {
        uint[2] memory refsNum;
        refsNum[0] = _referredUsers[_account].length;
        refsNum[1] = 0;

        for (uint i = 0; i < _referredUsers[_account].length; i++) {
            address temp = _referredUsers[_account][i];
            refsNum[1] += _referredUsers[temp].length;
        }
        return refsNum;
    }

    function getearnedAmountBNB(address _account) public view returns (uint) {
        return _earnedAmountBNB[_account];
    }

    function getearnedAmountToken(
        address _account,
        address[] memory _token
    ) public view returns (uint[] memory) {
        uint[] memory _earnedTokenAmount = new uint[](_token.length);
        for (uint i = 0; i < _token.length; i++) {
            address temp = _token[i];
            _earnedTokenAmount[i] = _earnedAmountToken[_account][temp];
        }
        return _earnedTokenAmount;
    }

    function processReferrals(
        uint256 amount,
        address _ref
    ) internal returns (uint256) {
        uint256 firstRefAmount = (amount * _refbonus[0]) / 10000;
        (bool sent, ) = payable(_ref).call{value: firstRefAmount, gas: 100000}(
            ""
        );
        require(sent, "Failed to send BNB payment to first referral");
        _earnedAmountBNB[_ref] += firstRefAmount;
        uint256 secondRefAmount;
        if (_referrals[_ref] != address(0)) {
            secondRefAmount = (amount * _refbonus[1]) / 10000;
            (sent, ) = payable(_referrals[_ref]).call{
                value: secondRefAmount,
                gas: 100000
            }("");
            require(sent, "Failed to send BNB payment to second referral");
            _earnedAmountBNB[_referrals[_ref]] += secondRefAmount;
        }
        return amount - firstRefAmount - secondRefAmount;
    }

    function processReferrals(
        uint256 amount,
        address _ref,
        address _token
    ) internal returns (uint256) {
        uint256 firstRefAmount = (amount * _refbonus[0]) / 10000;
        IERC20(_token).safeTransfer(_ref, firstRefAmount);
        _earnedAmountToken[_ref][_token] += firstRefAmount;
        uint256 secondRefAmount;
        if (_referrals[_ref] != address(0)) {
            secondRefAmount = (amount * _refbonus[1]) / 10000;
            IERC20(_token).safeTransfer(_referrals[_ref], secondRefAmount);
            _earnedAmountToken[_referrals[_ref]][_token] += secondRefAmount;
        }
        return amount - firstRefAmount - secondRefAmount;
    }

    function send(address _ref, address _token, uint256 amount) public payable {
        require(!locked, "Payments are temporarily locked by admin");
        require(_receiver != address(0), "Receiving contract is not set");
        if (_ref != address(0) && _referrals[_msgSender()] == address(0)) {
            _referrals[_msgSender()] = _ref;
            _referredUsers[_ref].push(_msgSender());
        }
        if (_token == address(0)) {
            require(msg.value > 0, "Insufficient BNB");
            amount = msg.value;
            if (_referrals[_msgSender()] != address(0)) {
                amount = processReferrals(amount, _referrals[_msgSender()]);
            }
            (bool sent, ) = payable(owner()).call{value: amount, gas: 100000}(
                ""
            );
            require(sent, "Failed to send BNB payment to owner");
            emit Payment(msg.sender, address(0), amount);
        } else {
            require(
                getTokenAllowance(_token),
                "This token is not allowed to use for payments."
            );
            require(amount > 0, "Insufficient token amount");
            IERC20(_token).safeTransferFrom(
                _msgSender(),
                address(this),
                amount
            );
            if (_referrals[_msgSender()] != address(0)) {
                amount = processReferrals(
                    amount,
                    _referrals[_msgSender()],
                    _token
                );
            }
            if (_token == _burnable) {
                IBurnable(_burnable).burn(amount);
            } else if (_token == coinContract) {
                if (_receiver.isContract()) {
                    IERC20(_token).safeApprove(_receiver, amount);
                    IReceiver(_receiver).addReward(amount);
                } else {
                    IERC20(_token).safeTransfer(_receiver, amount);
                }
            } else {
                IERC20(_token).safeTransfer(owner(), amount);
            }
            emit Payment(msg.sender, _token, amount);
        }
    }
}
