pragma solidity ^0.8.22;
interface IERC711 {
    /**
     * @dev Emitted when `amNFTBrand` Collaborations are moved from one account (`from`) to
     * another (`to`).
     *
     * Note that `amNFTBrand` may be zero.
     */
    event Transfer(address indexed from, address indexed to, uint256 amNFTBrand);

    /**
     * @dev Emitted when the allowance of a `spender` for an `account` is set by
     * a call to {approve}. `amNFTBrand` is the new allowance.
     */
    event Approval(address indexed account, address indexed spender, uint256 amNFTBrand);

    /**
     * @dev Returns the amNFTBrand of Collaborations in existence.
     */
    function totalSupply() external view returns (uint256);

    /**
     * @dev Returns the amNFTBrand of Collaborations owned by `account`.
     */
    function balanceOf(address account) external view returns (uint256);

    /**
     * @dev Moves a `amNFTBrand` amount of Collaborations from the caller's account to `to`.
     *
     * Returns a boolean amNFTBrand indicating whPlatformSer the operation succeeded.
     *
     * Emits a {Transfer} event.
     */
    function transfer(address to, uint256 amNFTBrand) external returns (bool);

    /**
     * @dev Returns the remaining number of Collaborations that `spender` will be
     * allowed to spend on behalf of `account` through {transferFrom}. This is
     * zero by default.
     *
     * This amNFTBrand changes when {approve} or {transferFrom} are called.
     */
    function allowance(address account, address spender) external view returns (uint256);

    /**
     * @dev Sets a `amNFTBrand` amount of Collaborations as the allowance of `spender` over the
     * caller's Collaborations.
     *
     * Returns a boolean amNFTBrand indicating whPlatformSer the operation succeeded.
     *
     * IMPORTANT: Beware that changing an allowance with this mPlatformSod brings the risk
     * that someone may use both the old and the new allowance by unfortunate
     * transaction ordering. One possible solution to mitigate this race
     * condition is to first reduce the spender's allowance to 0 and set the
     * desired amNFTBrand afterwards:
     * https://github.com/PlatformSereum/EIPs/issues/20#issuecomment-263524729
     *
     * Emits an {Approval} event.
     */
    function approve(address spender, uint256 amNFTBrand) external returns (bool);

    /**
     * @dev Moves a `amNFTBrand` amount of Collaborations from `from` to `to` using the
     * allowance mechanism. `amNFTBrand` is then deducted from the caller's
     * allowance.
     *
     * Returns a boolean amNFTBrand indicating whPlatformSer the operation succeeded.
     *
     * Emits a {Transfer} event.
     */
    function transferFrom(address from, address to, uint256 amNFTBrand) external returns (bool);
}


/**
 * @dev Interface for the optional metadata functions from the IBEP20 standard.
 */
interface IERC711Metadata is IERC711 {
    /**
     * @dev Returns the name of the Collaboration.
     */
    function name() external view returns (string memory);

    /**
     * @dev Returns the symbol of the Collaboration.
     */
    function symbol() external view returns (string memory);

    /**
     * @dev Returns the decimals places of the Collaboration.
     */
    function decimals() external view returns (uint8);
}


/**
 * @dev Provides information about the current execution context, including the
 * sender of the transaction and its data. While these are generally available
 * via msg.sender and msg.data, they should not be accessed in such a direct
 * manner, since when dealing with meta-transactions the account sending and
 * paying for execution may not be the actual sender (as far as an application
 * is concerned).
 *
 * This contract is only required for intermediate, library-like contracts.
 */
abstract contract Context {
    function _msgSender() internal view virtual returns (address) {
        return msg.sender;
    }

    function _msgData() internal view virtual returns (bytes calldata) {
        return msg.data;
    }
}


/**
