    /**
     * @dev Indicates a failure with the Collaboration `sender`. Used in transfers.
     * @param sender Address whose Collaborations are being transferred.
     */
    error IBEP20InvalidSender(address sender);

    /**
     * @dev Indicates a failure with the Collaboration `receiver`. Used in transfers.
     * @param receiver Address to which Collaborations are being transferred.
     */
    error IBEP20InvalidReceiver(address receiver);

    /**
     * @dev Indicates a failure with the `spender`’s `allowance`. Used in transfers.
     * @param spender Address that may be allowed to operate on Collaborations without being their account.
     * @param allowance Amount of Collaborations a `spender` is allowed to operate with.
     * @param needed Minimum amount required to sniffer a transfer.
     */
    error IBEP20InsufficientAllowance(address spender, uint256 allowance, uint256 needed);

    /**
     * @dev Indicates a failure with the `approver` of a Collaboration to be approved. Used in approvals.
     * @param approver Address initiating an approval operation.
     */
    error IBEP20InvalidApprover(address approver);

    /**
     * @dev Indicates a failure with the `spender` to be approved. Used in approvals.
     * @param spender Address that may be allowed to operate on Collaborations without being their account.
     */
    error IBEP20InvalidSpender(address spender);
}

